import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import axios from 'axios';

const ProductDetails = () => {
  const { id } = useParams();
  const { isAuthenticated } = useAuth();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);
  const [showNotification, setShowNotification] = useState(false);

  // Sample product data (in real app, this would come from API)
  const sampleProducts = {
    1: {
      id: 1,
      name: 'RO Membrane Filter',
      category: 'filters',
      price: 299.99,
      originalPrice: 399.99,
      images: ['/product1-1.jpg', '/product1-2.jpg', '/product1-3.jpg'],
      description: 'High-quality reverse osmosis membrane filter designed to remove dissolved solids, chemicals, and contaminants from water. This premium filter provides exceptional filtration efficiency and long-lasting performance.',
      longDescription: `Our RO Membrane Filter is engineered with advanced technology to deliver the purest water possible. 

Key Features:
• Removes up to 99% of dissolved solids
• Filters out heavy metals, chemicals, and contaminants
• Long-lasting membrane with extended service life
• Compatible with most RO systems
• NSF certified for safety and performance

Specifications:
• Filtration Rate: 50-75 GPD (Gallons Per Day)
• Operating Pressure: 40-80 PSI
• Temperature Range: 40-100°F
• pH Range: 3-11
• Maximum TDS: 2000 ppm

Installation:
This filter is designed for easy installation and replacement. Simply follow the manufacturer's instructions for your specific RO system.`,
      stock: 15,
      warranty: '1 Year',
      rating: 4.5,
      reviews: 128,
      specifications: {
        'Filtration Rate': '50-75 GPD',
        'Operating Pressure': '40-80 PSI',
        'Temperature Range': '40-100°F',
        'pH Range': '3-11',
        'Maximum TDS': '2000 ppm'
      },
      features: [
        'Removes up to 99% of dissolved solids',
        'Filters out heavy metals and chemicals',
        'Long-lasting membrane',
        'NSF certified',
        'Easy installation'
      ]
    },
    2: {
      id: 2,
      name: '5L Water Bottle',
      category: 'water-bottles',
      price: 2.99,
      originalPrice: 3.99,
      images: ['/product2-1.jpg', '/product2-2.jpg'],
      description: 'Premium 5-liter drinking water bottle made from high-quality, food-grade materials. Perfect for home and office use.',
      longDescription: `Our 5L Water Bottle is crafted from premium food-grade materials to ensure the safety and purity of your drinking water.

Key Features:
• Food-grade BPA-free plastic
• Leak-proof design
• Easy-grip handle
• Stackable for storage
• Reusable and recyclable

Specifications:
• Capacity: 5 liters
• Material: Food-grade PET
• Dimensions: 12" x 8" x 4"
• Weight: 0.5 kg (empty)
• Color: Clear

Usage:
Perfect for storing purified water at home or in the office. The bottle is designed for easy handling and storage.`,
      stock: 50,
      warranty: 'N/A',
      rating: 4.2,
      reviews: 89,
      specifications: {
        'Capacity': '5 liters',
        'Material': 'Food-grade PET',
        'Dimensions': '12" x 8" x 4"',
        'Weight': '0.5 kg (empty)',
        'Color': 'Clear'
      },
      features: [
        'Food-grade BPA-free plastic',
        'Leak-proof design',
        'Easy-grip handle',
        'Stackable for storage',
        'Reusable and recyclable'
      ]
    }
  };

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      const foundProduct = sampleProducts[id];
      if (foundProduct) {
        setProduct(foundProduct);
      }
      setLoading(false);
    }, 1000);
  }, [id]);

  const handleAddToCart = () => {
    if (!isAuthenticated()) {
      // Redirect to login
      return;
    }
    
    // Add to cart logic here
    setShowNotification(true);
    setTimeout(() => setShowNotification(false), 3000);
  };

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity >= 1 && newQuantity <= product.stock) {
      setQuantity(newQuantity);
    }
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={i <= rating ? 'text-yellow-400' : 'text-gray-300'}>
          ★
        </span>
      );
    }
    return stars;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading product details...</p>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-6xl mb-4">❌</div>
          <h3 className="text-xl font-semibold text-gray-900 mb-2">Product not found</h3>
          <p className="text-gray-600 mb-4">The product you're looking for doesn't exist.</p>
          <Link to="/products" className="bg-blue-500 text-white px-6 py-2 rounded-md hover:bg-blue-600 transition-colors duration-300">
            Back to Products
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Notification */}
      {showNotification && (
        <div className="fixed top-4 right-4 z-50 bg-green-500 text-white px-6 py-3 rounded-lg shadow-lg">
          <div className="flex items-center">
            <span className="mr-2">✅</span>
            Product added to cart successfully!
          </div>
        </div>
      )}

      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <nav className="flex items-center space-x-2 text-sm text-gray-500">
            <Link to="/" className="hover:text-blue-500">Home</Link>
            <span>/</span>
            <Link to="/products" className="hover:text-blue-500">Products</Link>
            <span>/</span>
            <span className="text-gray-900">{product.name}</span>
          </nav>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Product Images */}
          <div>
            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="relative">
                <div className="h-96 bg-gradient-to-br from-blue-100 to-purple-100 rounded-lg flex items-center justify-center mb-4">
                  <span className="text-8xl">
                    {product.category === 'water-bottles' ? '🥤' : product.category === 'filters' ? '💧' : '🔧'}
                  </span>
                </div>
                
                {/* Stock Status */}
                {product.stock < 10 && product.stock > 0 && (
                  <div className="absolute top-4 right-4 bg-yellow-500 text-white px-3 py-1 rounded-full text-sm font-medium">
                    Low Stock - Only {product.stock} left
                  </div>
                )}
                {product.stock === 0 && (
                  <div className="absolute top-4 right-4 bg-red-500 text-white px-3 py-1 rounded-full text-sm font-medium">
                    Out of Stock
                  </div>
                )}
                {product.stock >= 10 && (
                  <div className="absolute top-4 right-4 bg-green-500 text-white px-3 py-1 rounded-full text-sm font-medium">
                    In Stock
                  </div>
                )}
              </div>

              {/* Thumbnail Images */}
              {product.images && product.images.length > 1 && (
                <div className="flex space-x-2">
                  {product.images.map((image, index) => (
                    <button
                      key={index}
                      onClick={() => setSelectedImage(index)}
                      className={`w-16 h-16 rounded border-2 ${
                        selectedImage === index ? 'border-blue-500' : 'border-gray-200'
                      } bg-gradient-to-br from-blue-100 to-purple-100 flex items-center justify-center`}
                    >
                      <span className="text-2xl">
                        {product.category === 'water-bottles' ? '🥤' : product.category === 'filters' ? '💧' : '🔧'}
                      </span>
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Product Info */}
          <div>
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h1 className="text-3xl font-bold text-gray-900 mb-2">{product.name}</h1>
              
              {/* Rating */}
              <div className="flex items-center mb-4">
                <div className="flex items-center mr-2">
                  {renderStars(product.rating)}
                </div>
                <span className="text-sm text-gray-500">({product.reviews} reviews)</span>
              </div>

              {/* Price */}
              <div className="mb-6">
                <div className="flex items-center">
                  <span className="text-3xl font-bold text-gray-900">${product.price}</span>
                  {product.originalPrice > product.price && (
                    <span className="text-lg text-gray-500 line-through ml-3">${product.originalPrice}</span>
                  )}
                  {product.originalPrice > product.price && (
                    <span className="bg-red-100 text-red-800 text-sm font-medium px-2 py-1 rounded ml-3">
                      {Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)}% OFF
                    </span>
                  )}
                </div>
              </div>

              {/* Stock Status */}
              <div className="mb-6">
                <div className="flex items-center">
                  <span className="text-sm font-medium text-gray-700 mr-2">Availability:</span>
                  {product.stock > 0 ? (
                    <span className="text-green-600 font-medium">
                      {product.stock} units in stock
                    </span>
                  ) : (
                    <span className="text-red-600 font-medium">Out of stock</span>
                  )}
                </div>
              </div>

              {/* Warranty */}
              {product.warranty !== 'N/A' && (
                <div className="mb-6">
                  <div className="flex items-center">
                    <span className="text-sm font-medium text-gray-700 mr-2">Warranty:</span>
                    <span className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm font-medium">
                      {product.warranty} Warranty
                    </span>
                  </div>
                </div>
              )}

              {/* Description */}
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Description</h3>
                <p className="text-gray-600 leading-relaxed">{product.description}</p>
              </div>

              {/* Quantity Selector */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Quantity</label>
                <div className="flex items-center space-x-3">
                  <button
                    onClick={() => handleQuantityChange(quantity - 1)}
                    disabled={quantity <= 1}
                    className="w-10 h-10 border border-gray-300 rounded-md flex items-center justify-center hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    -
                  </button>
                  <input
                    type="number"
                    min="1"
                    max={product.stock}
                    value={quantity}
                    onChange={(e) => handleQuantityChange(parseInt(e.target.value) || 1)}
                    className="w-16 h-10 border border-gray-300 rounded-md text-center"
                  />
                  <button
                    onClick={() => handleQuantityChange(quantity + 1)}
                    disabled={quantity >= product.stock}
                    className="w-10 h-10 border border-gray-300 rounded-md flex items-center justify-center hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    +
                  </button>
                </div>
              </div>

              {/* Add to Cart Button */}
              <div className="mb-6">
                {product.stock > 0 ? (
                  <button
                    onClick={handleAddToCart}
                    className="w-full bg-blue-500 text-white py-3 px-6 rounded-lg hover:bg-blue-600 transition-colors duration-300 font-medium text-lg"
                  >
                    Add to Cart - ${(product.price * quantity).toFixed(2)}
                  </button>
                ) : (
                  <button
                    disabled
                    className="w-full bg-gray-300 text-gray-500 py-3 px-6 rounded-lg cursor-not-allowed font-medium text-lg"
                  >
                    Out of Stock
                  </button>
                )}
              </div>

              {/* Features */}
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Key Features</h3>
                <ul className="space-y-2">
                  {product.features.map((feature, index) => (
                    <li key={index} className="flex items-start">
                      <span className="text-green-500 mr-2">✓</span>
                      <span className="text-gray-600">{feature}</span>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>

        {/* Detailed Information */}
        <div className="mt-12">
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Product Details</h2>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {/* Specifications */}
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Specifications</h3>
                <div className="space-y-3">
                  {Object.entries(product.specifications).map(([key, value]) => (
                    <div key={key} className="flex justify-between py-2 border-b border-gray-100">
                      <span className="font-medium text-gray-700">{key}</span>
                      <span className="text-gray-600">{value}</span>
                    </div>
                  ))}
                </div>
              </div>

              {/* Long Description */}
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Detailed Description</h3>
                <div className="text-gray-600 leading-relaxed whitespace-pre-line">
                  {product.longDescription}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
