import React, { useState, useEffect } from 'react';

const EmergencyNotification = ({ emergencyRequests, onClose }) => {
  const [isVisible, setIsVisible] = useState(false);
  const [pendingCount, setPendingCount] = useState(0);

  useEffect(() => {
    const newPendingCount = emergencyRequests.filter(req => req.status === 'Pending').length;
    
    if (newPendingCount > pendingCount && newPendingCount > 0) {
      setIsVisible(true);
      // Auto-hide after 10 seconds
      setTimeout(() => setIsVisible(false), 10000);
    }
    
    setPendingCount(newPendingCount);
  }, [emergencyRequests, pendingCount]);

  if (!isVisible) return null;

  const pendingRequests = emergencyRequests.filter(req => req.status === 'Pending');

  return (
    <div className="fixed top-20 right-4 z-50 max-w-md">
      <div className="bg-red-50 border border-red-200 rounded-lg shadow-lg p-4">
        <div className="flex items-start">
          <div className="flex-shrink-0">
            <div className="w-8 h-8 bg-red-100 rounded-full flex items-center justify-center">
              <svg className="w-5 h-5 text-red-600" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
            </div>
          </div>
          <div className="ml-3 flex-1">
            <h3 className="text-sm font-medium text-red-800">
              Emergency Water Request Alert!
            </h3>
            <div className="mt-2 text-sm text-red-700">
              <p>You have {pendingRequests.length} pending emergency request(s) from fire brigades.</p>
              {pendingRequests.slice(0, 2).map((request, index) => (
                <div key={request._id || request.id} className="mt-1 text-xs">
                  â€¢ {request.brigadeName} - {request.requestType} ({request.priority} Priority)
                </div>
              ))}
              {pendingRequests.length > 2 && (
                <p className="mt-1 text-xs">...and {pendingRequests.length - 2} more</p>
              )}
            </div>
          </div>
          <div className="ml-4 flex-shrink-0 flex">
            <button
              onClick={() => setIsVisible(false)}
              className="bg-red-50 rounded-md inline-flex text-red-400 hover:text-red-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
            >
              <span className="sr-only">Close</span>
              <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmergencyNotification;
