import React, { useEffect, useState } from 'react';
import Nav from '../Nav/Nav'; // Fix import case
import axios from 'axios';
import User from '../User/User'; // Import User component

const URL = 'http://localhost:5000/Users';
const fetchHandler = async () => {
  return await axios.get(URL).then((res) => res.data);
};

function UserDetails() { // Capitalize component name
  const [UserDetails, setUserDetails] = useState([]); // Use array for mapping

  useEffect(() => {
    fetchHandler().then((data) => setUserDetails(data.UserDetails)); // Use 'users' key from backend
  }, []);

  return (
    <div>
      <Nav />
      <h1>User Details display page</h1>
      <div>
      <h1>User display</h1>
      <br />
      <h1>id: {_id}</h1>
      <h1>Name: {name}</h1>
      <h1>Age: {age}</h1>
      <h1>Address: {address}</h1>
      <button>Update </button>
      <button>Delete </button>
    </div>
      <div>
        {UserDetails && UserDetails.map((user, index) => (
          <div key={user._id || index}>
            <User user={user} />
            <br />  
          </div>
          
        ))}
      </div>
    </div>
  );
}

export default UserDetails;
