import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import UniversalNavbar from '../Nav/UniversalNavbar';
import { useAuth } from '../../Context/AuthContext';
import { 
  generateEmployeeReportPDF, 
  generateEmergencyReportPDF, 
  generateBranchReportPDF, 
  generateUserReportPDF,
  generateAdminDashboardReport 
} from '../../utils/adminPdfGenerator';
import { emergencyRequestAPI, branchAPI, userAPI, employeeAPI } from '../../utils/apiService';
import EmergencyNotification from './EmergencyNotification';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  
  // Check if user has admin role
  useEffect(() => {
    if (user && user.role !== 'Admin') {
      console.error('User does not have admin privileges:', user.role);
      alert('Access denied. Admin privileges required.');
      // Redirect to login or home page
      window.location.href = '/login';
    }
  }, [user]);

  // Debug emergency requests state changes
  useEffect(() => {
    console.log('ðŸ”„ Emergency requests state changed:', emergencyRequests);
    console.log('ðŸ“Š Emergency requests count:', emergencyRequests.length);
    if (emergencyRequests.length > 0) {
      console.log('ðŸ“‹ First emergency request:', emergencyRequests[0]);
    }
  }, [emergencyRequests]);

  const [employees, setEmployees] = useState([]);
  const [emergencyRequests, setEmergencyRequests] = useState([]);
  const [branches, setBranches] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch data from API
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        console.log('ðŸ”„ Fetching dashboard data...');
        
        // Fetch all data in parallel
        const [emergencyResponse, branchesResponse, usersResponse, employeesResponse] = await Promise.allSettled([
          emergencyRequestAPI.getAllRequests(),
          branchAPI.getAllBranches(),
          userAPI.getAllUsers(),
          employeeAPI.getAllEmployees()
        ]);

        // Set emergency requests
        if (emergencyResponse.status === 'fulfilled') {
          const responseData = emergencyResponse.value;
          console.log('ðŸ“‹ Emergency response:', responseData);
          if (responseData && responseData.data) {
            console.log('âœ… Setting emergency requests:', responseData.data.length);
            setEmergencyRequests(responseData.data);
          } else {
            console.error('âŒ Invalid response structure:', responseData);
            setEmergencyRequests([]);
          }
        } else {
          console.error('âŒ Error fetching emergency requests:', emergencyResponse.reason);
          setEmergencyRequests([]);
        }

        // Set branches
        if (branchesResponse.status === 'fulfilled') {
          setBranches(branchesResponse.value.data || []);
        } else {
          console.error('Error fetching branches:', branchesResponse.reason);
          setBranches([]);
        }

        // Set users
        if (usersResponse.status === 'fulfilled') {
          setUsers(usersResponse.value.data || []);
        } else {
          console.error('Error fetching users:', usersResponse.reason);
          setUsers([]);
        }

        // Set employees
        if (employeesResponse.status === 'fulfilled') {
          setEmployees(employeesResponse.value.data || []);
        } else {
          console.error('Error fetching employees:', employeesResponse.reason);
          setEmployees([]);
        }

      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();

    // Set up periodic refresh for emergency requests (every 10 seconds for faster updates)
    const emergencyRefreshInterval = setInterval(async () => {
      try {
        console.log('ðŸ”„ Refreshing emergency requests...');
        const response = await emergencyRequestAPI.getAllRequests();
        if (response && response.data) {
          console.log('âœ… Refreshed emergency requests:', response.data.length);
          setEmergencyRequests(response.data);
        }
      } catch (error) {
        console.error('Error refreshing emergency requests:', error);
      }
    }, 10000); // Changed from 30 seconds to 10 seconds

    return () => {
      clearInterval(emergencyRefreshInterval);
    };
  }, []);

  // Manual refresh function
  const refreshEmergencyRequests = async () => {
    try {
      console.log('ðŸ”„ Manually refreshing emergency requests...');
      const response = await emergencyRequestAPI.getAllRequests();
      if (response && response.data) {
        console.log('âœ… Manually refreshed emergency requests:', response.data.length);
        setEmergencyRequests(response.data);
        alert('Emergency requests refreshed successfully!');
      }
    } catch (error) {
      console.error('Error manually refreshing emergency requests:', error);
      alert('Failed to refresh emergency requests. Please try again.');
    }
  };

  const handleEmergencyResponse = async (requestId, response) => {
    try {
      console.log('Updating request:', requestId, 'with status:', response);
      
      // Update the request status via API
      const result = await emergencyRequestAPI.updateRequestStatus(requestId, { 
        status: response,
        adminNotes: `Request ${response.toLowerCase()} by admin`
      });
      
      console.log('Update result:', result);
      
      // Update local state
      setEmergencyRequests(prev => 
        prev.map(req => 
          req._id === requestId || req.id === requestId
            ? { ...req, status: response, responseDate: new Date().toISOString() }
            : req
        )
      );
    } catch (error) {
      console.error('Error updating emergency request status:', error);
      console.error('Error details:', error.response?.data || error.message);
      alert('Failed to update request status. Please try again.');
    }
  };

  const handleFindNearestBranch = async (requestId) => {
    try {
      console.log('Finding nearest branch for request:', requestId);
      
      const request = emergencyRequests.find(req => req._id === requestId || req.id === requestId);
      if (!request || !request.coordinates) {
        alert('Request coordinates not available');
        return;
      }

      console.log('Request coordinates:', request.coordinates);

      const response = await emergencyRequestAPI.findNearestBranch({
        lat: request.coordinates.lat,
        lng: request.coordinates.lng
      });

      console.log('Nearest branch response:', response);

      if (response.data && response.data.length > 0) {
        const nearestBranch = response.data[0];
        alert(`Nearest branch: ${nearestBranch.name} (${nearestBranch.location})`);
        
        // Update the request with assigned branch
        await emergencyRequestAPI.updateRequestStatus(requestId, { 
          assignedBranch: nearestBranch._id,
          status: 'In Progress',
          adminNotes: `Assigned to nearest branch: ${nearestBranch.name}`
        });

        // Update local state
        setEmergencyRequests(prev => 
          prev.map(req => 
            req._id === requestId || req.id === requestId
              ? { ...req, assignedBranch: nearestBranch, status: 'In Progress' }
              : req
          )
        );
      } else {
        alert('No nearby branches found');
      }
    } catch (error) {
      console.error('Error finding nearest branch:', error);
      console.error('Error details:', error.response?.data || error.message);
      alert('Failed to find nearest branch. Please try again.');
    }
  };

  const generateEmployeeReport = async () => {
    try {
      await generateEmployeeReportPDF(employees);
    } catch (error) {
      console.error('Error generating employee report:', error);
    }
  };

  const generateEmergencyReport = async () => {
    try {
      await generateEmergencyReportPDF(emergencyRequests);
    } catch (error) {
      console.error('Error generating emergency report:', error);
    }
  };

  const generateBranchReport = async () => {
    try {
      await generateBranchReportPDF(branches);
    } catch (error) {
      console.error('Error generating branch report:', error);
    }
  };

  const generateUserReport = async () => {
    try {
      await generateUserReportPDF(users);
    } catch (error) {
      console.error('Error generating user report:', error);
    }
  };

  const generateComprehensiveReport = async () => {
    try {
      await generateAdminDashboardReport({
        employees,
        emergencyRequests,
        branches,
        users
      });
    } catch (error) {
      console.error('Error generating comprehensive report:', error);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <UniversalNavbar />
        <div className="pt-16">
          <div className="flex items-center justify-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <UniversalNavbar />
      <EmergencyNotification emergencyRequests={emergencyRequests} />
      <div className="pt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900">Admin Dashboard</h1>
            <p className="text-gray-600 mt-2">Welcome back, {user?.name}</p>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-blue-100 rounded-lg">
                  <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
                  </svg>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Total Employees</p>
                  <p className="text-2xl font-semibold text-gray-900">{employees.length}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-red-100 rounded-lg">
                  <svg className="w-6 h-6 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                  </svg>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Emergency Requests</p>
                  <p className="text-2xl font-semibold text-gray-900">
                    {emergencyRequests.filter(req => req.status === 'Pending').length}
                  </p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-green-100 rounded-lg">
                  <svg className="w-6 h-6 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                  </svg>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Active Branches</p>
                  <p className="text-2xl font-semibold text-gray-900">{branches.length}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-purple-100 rounded-lg">
                  <svg className="w-6 h-6 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                  </svg>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Total Users</p>
                  <p className="text-2xl font-semibold text-gray-900">{users.length}</p>
                </div>
              </div>
            </div>
          </div>

          {/* Navigation Tabs */}
          <div className="bg-white rounded-lg shadow-sm mb-8">
            <div className="border-b border-gray-200">
              <nav className="flex space-x-8 px-6">
                {[
                  { id: 'overview', name: 'Overview', icon: 'ðŸ“Š' },
                  { id: 'employees', name: 'Employees', icon: 'ðŸ‘¥' },
                  { id: 'emergency', name: 'Emergency Requests', icon: 'ðŸš¨' },
                  { id: 'branches', name: 'Branches', icon: 'ðŸ¢' },
                  { id: 'users', name: 'Users', icon: 'ðŸ‘¤' }
                ].map((tab) => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`py-4 px-1 border-b-2 font-medium text-sm flex items-center space-x-2 ${
                      activeTab === tab.id
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    <span>{tab.icon}</span>
                    <span>{tab.name}</span>
                  </button>
                ))}
              </nav>
            </div>

            {/* Tab Content */}
            <div className="p-6">
              {activeTab === 'overview' && (
                <div className="space-y-6">
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    {/* Recent Emergency Requests */}
                    <div className="bg-gray-50 rounded-lg p-4">
                      <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Emergency Requests</h3>
                      <div className="space-y-3">
                        {emergencyRequests.slice(0, 3).map((request) => (
                          <div key={request.id} className="bg-white rounded-lg p-3 border-l-4 border-red-500">
                            <div className="flex justify-between items-start">
                              <div>
                                <p className="font-medium text-gray-900">{request.brigadeName}</p>
                                <p className="text-sm text-gray-600">{request.requestType}</p>
                                <p className="text-xs text-gray-500">{request.requestDate}</p>
                              </div>
                              <span className={`px-2 py-1 text-xs rounded-full ${
                                request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                                request.status === 'Approved' ? 'bg-green-100 text-green-800' :
                                'bg-red-100 text-red-800'
                              }`}>
                                {request.status}
                              </span>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>

                    {/* Recent Employees */}
                    <div className="bg-gray-50 rounded-lg p-4">
                      <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Employees</h3>
                      <div className="space-y-3">
                        {employees.slice(0, 3).map((employee) => (
                          <div key={employee.id} className="bg-white rounded-lg p-3 border-l-4 border-blue-500">
                            <div className="flex justify-between items-start">
                              <div>
                                <p className="font-medium text-gray-900">{employee.name}</p>
                                <p className="text-sm text-gray-600">{employee.role}</p>
                                <p className="text-xs text-gray-500">{employee.branch}</p>
                              </div>
                              <span className="px-2 py-1 text-xs rounded-full bg-green-100 text-green-800">
                                {employee.status}
                              </span>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>

                  {/* Quick Actions */}
                  <div className="bg-gray-50 rounded-lg p-4">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h3>
                    <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                      <Link
                        to="/admin/add-employee"
                        className="bg-blue-500 text-white p-4 rounded-lg hover:bg-blue-600 transition-colors duration-200 text-center"
                      >
                        <div className="text-2xl mb-2">ðŸ‘¨â€ðŸ’¼</div>
                        <p className="font-medium">Add Employee</p>
                      </Link>
                      <Link
                        to="/admin/emergency-requests"
                        className="bg-red-500 text-white p-4 rounded-lg hover:bg-red-600 transition-colors duration-200 text-center"
                      >
                        <div className="text-2xl mb-2">ðŸš¨</div>
                        <p className="font-medium">Emergency Requests</p>
                      </Link>
                      <Link
                        to="/admin/branches"
                        className="bg-green-500 text-white p-4 rounded-lg hover:bg-green-600 transition-colors duration-200 text-center"
                      >
                        <div className="text-2xl mb-2">ðŸ¢</div>
                        <p className="font-medium">Manage Branches</p>
                      </Link>
                      <button
                        onClick={generateComprehensiveReport}
                        className="bg-purple-500 text-white p-4 rounded-lg hover:bg-purple-600 transition-colors duration-200 text-center"
                      >
                        <div className="text-2xl mb-2">ðŸ“Š</div>
                        <p className="font-medium">Generate Report</p>
                      </button>
                    </div>
                  </div>
                </div>
              )}

              {activeTab === 'employees' && (
                <div>
                  <div className="flex justify-between items-center mb-6">
                    <h3 className="text-lg font-semibold text-gray-900">Employee Management</h3>
                    <div className="flex space-x-3">
                      <button
                        onClick={generateEmployeeReport}
                        className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors duration-200"
                      >
                        ðŸ“„ Generate Report
                      </button>
                      <Link
                        to="/admin/add-employee"
                        className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-colors duration-200"
                      >
                        + Add Employee
                      </Link>
                    </div>
                  </div>

                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Employee</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Role</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Branch</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Join Date</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {employees.map((employee) => (
                          <tr key={employee.id}>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <div>
                                <div className="text-sm font-medium text-gray-900">{employee.name}</div>
                                <div className="text-sm text-gray-500">{employee.email}</div>
                              </div>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{employee.role}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{employee.branch}</td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <span className="px-2 py-1 text-xs rounded-full bg-green-100 text-green-800">
                                {employee.status}
                              </span>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{employee.joinDate}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                              <button className="text-blue-600 hover:text-blue-900 mr-3">Edit</button>
                              <button className="text-red-600 hover:text-red-900">Remove</button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}

              {activeTab === 'emergency' && (
                <div>
                  <div className="flex justify-between items-center mb-6">
                    <h3 className="text-lg font-semibold text-gray-900">Emergency Request Management</h3>
                    <div className="flex space-x-3">
                      <button
                        onClick={refreshEmergencyRequests}
                        className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-colors duration-200"
                      >
                        ðŸ”„ Refresh Requests
                      </button>
                      <button
                        onClick={generateEmergencyReport}
                        className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors duration-200"
                      >
                        ðŸ“„ Generate Report
                      </button>
                    </div>
                  </div>

                  <div className="space-y-4">
                    {emergencyRequests.length === 0 ? (
                      <div className="text-center py-8">
                        <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                        </svg>
                        <h3 className="mt-2 text-sm font-medium text-gray-900">No emergency requests</h3>
                        <p className="mt-1 text-sm text-gray-500">No emergency requests have been created yet.</p>
                        <div className="mt-6">
                          <button
                            onClick={refreshEmergencyRequests}
                            className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
                          >
                            ðŸ”„ Refresh
                          </button>
                        </div>
                      </div>
                    ) : (
                      emergencyRequests.map((request) => (
                      <div key={request.id} className="bg-white border rounded-lg p-6">
                        <div className="flex justify-between items-start mb-4">
                          <div>
                            <h4 className="text-lg font-semibold text-gray-900">{request.brigadeName}</h4>
                            <p className="text-sm text-gray-600">{request.brigadeLocation}</p>
                            <p className="text-sm text-gray-500">{request.requestDate}</p>
                          </div>
                          <div className="flex space-x-2">
                            <span className={`px-3 py-1 text-sm rounded-full ${
                              request.priority === 'High' ? 'bg-red-100 text-red-800' :
                              request.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                              'bg-green-100 text-green-800'
                            }`}>
                              {request.priority} Priority
                            </span>
                            <span className={`px-3 py-1 text-sm rounded-full ${
                              request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                              request.status === 'Approved' ? 'bg-green-100 text-green-800' :
                              'bg-red-100 text-red-800'
                            }`}>
                              {request.status}
                            </span>
                          </div>
                        </div>

                        <div className="mb-4">
                          <p className="text-gray-700 mb-2"><strong>Request Type:</strong> {request.requestType}</p>
                          <p className="text-gray-700 mb-2"><strong>Description:</strong> {request.description}</p>
                          <p className="text-gray-700"><strong>Water Level:</strong> {request.waterLevel}</p>
                        </div>

                        {request.status === 'Pending' && (
                          <div className="flex space-x-3">
                            <button
                              onClick={() => handleEmergencyResponse(request._id || request.id, 'Approved')}
                              className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors duration-200"
                            >
                              âœ… Approve Request
                            </button>
                            <button
                              onClick={() => handleEmergencyResponse(request._id || request.id, 'Rejected')}
                              className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition-colors duration-200"
                            >
                              âŒ Reject Request
                            </button>
                            <button 
                              onClick={() => handleFindNearestBranch(request._id || request.id)}
                              className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-colors duration-200"
                            >
                              ðŸ“ Find Nearest Branch
                            </button>
                          </div>
                        )}

                        {request.status === 'Approved' && (
                          <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                            <p className="text-green-800">
                              âœ… Request approved. Fire brigade has been notified and nearest branch has been alerted.
                            </p>
                          </div>
                                                 )}
                       </div>
                     ))
                   )}
                 </div>
                </div>
              )}

              {activeTab === 'branches' && (
                <div>
                  <div className="flex justify-between items-center mb-6">
                    <h3 className="text-lg font-semibold text-gray-900">Branch Management</h3>
                    <div className="flex space-x-3">
                      <button
                        onClick={generateBranchReport}
                        className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors duration-200"
                      >
                        ðŸ“„ Generate Report
                      </button>
                      <Link
                        to="/admin/add-branch"
                        className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-colors duration-200"
                      >
                        + Add Branch
                      </Link>
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {branches.map((branch) => (
                      <div key={branch.id} className="bg-white border rounded-lg p-6">
                        <div className="flex justify-between items-start mb-4">
                          <div>
                            <h4 className="text-lg font-semibold text-gray-900">{branch.name}</h4>
                            <p className="text-sm text-gray-600">{branch.location}</p>
                          </div>
                          <span className="px-2 py-1 text-xs rounded-full bg-green-100 text-green-800">
                            {branch.status}
                          </span>
                        </div>

                        <div className="space-y-2 mb-4">
                          <p className="text-sm text-gray-600"><strong>Manager:</strong> {branch.manager}</p>
                          <p className="text-sm text-gray-600"><strong>Capacity:</strong> {branch.capacity}</p>
                          <p className="text-sm text-gray-600"><strong>Current Stock:</strong> {branch.currentStock}</p>
                        </div>

                        <div className="flex space-x-2">
                          <button className="text-blue-600 hover:text-blue-900 text-sm">Edit</button>
                          <button className="text-red-600 hover:text-red-900 text-sm">Remove</button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {activeTab === 'users' && (
                <div>
                  <div className="flex justify-between items-center mb-6">
                    <h3 className="text-lg font-semibold text-gray-900">User Management</h3>
                    <button
                      onClick={generateUserReport}
                      className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors duration-200"
                    >
                      ðŸ“„ Generate Report
                    </button>
                  </div>

                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">User</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Role</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Join Date</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {users.map((user) => (
                          <tr key={user.id}>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <div>
                                <div className="text-sm font-medium text-gray-900">{user.name}</div>
                                <div className="text-sm text-gray-500">{user.email}</div>
                              </div>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{user.role}</td>
                            <td className="px-6 py-4 whitespace-nowrap">
                              <span className="px-2 py-1 text-xs rounded-full bg-green-100 text-green-800">
                                {user.status}
                              </span>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{user.joinDate}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                              <button className="text-red-600 hover:text-red-900">Remove</button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
