import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  PieChart, Pie, Cell, LineChart, Line
} from 'recharts';
import axios from 'axios';

const FactoryDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  
  // Check if user has factory manager role
  useEffect(() => {
    if (user && user.role !== 'Factory Manager') {
      console.error('User does not have factory manager privileges:', user.role);
      alert('Access denied. Factory Manager privileges required.');
      window.location.href = '/login';
    }
  }, [user]);
  
  const [inventoryStats, setInventoryStats] = useState(null);
  const [inventoryData, setInventoryData] = useState([]);
  const [pendingOrders, setPendingOrders] = useState([]);
  const [orderStats, setOrderStats] = useState(null);
  const [recentActivities, setRecentActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [recyclingRequests, setRecyclingRequests] = useState([]);
  const [recyclingBins, setRecyclingBins] = useState([]);
  const [recyclingStats, setRecyclingStats] = useState(null);
  const [monthlyData, setMonthlyData] = useState([]);
  const [factoryRequests, setFactoryRequests] = useState([]);

  // Fetch data from API
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        console.log('ðŸ”„ Fetching factory dashboard data...');
        
        // Fetch all data in parallel
        const [statsResponse, inventoryResponse, ordersResponse, orderStatsResponse, activitiesResponse, recyclingResponse, binsResponse, recyclingStatsResponse, monthlyDataResponse, factoryRequestsResponse] = await Promise.allSettled([
          axios.get('http://localhost:5000/Inventory/stats/overview'),
          axios.get('http://localhost:5000/Inventory'),
          axios.get('http://localhost:5000/Orders/pending'),
          axios.get('http://localhost:5000/Orders/stats/overview'),
          axios.get('http://localhost:5000/Orders/activities/recent'),
          axios.get('http://localhost:5000/RecyclingRequests/pending'),
          axios.get('http://localhost:5000/RecyclingBins/factory-notification'),
          axios.get('http://localhost:5000/RecyclingRequests/statistics'),
          axios.get('http://localhost:5000/Orders/monthly-data'),
          axios.get('http://localhost:5000/FactoryRequests')
        ]);

        // Set inventory stats
