import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import { useCart } from '../../Context/CartContext';
import UniversalNavbar from '../Nav/UniversalNavbar';
import axios from 'axios';

const HomePage = () => {
  const { user, isAuthenticated } = useAuth();
  const { totalItems } = useCart();
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  
  // Customer dashboard state
  const [activeTab, setActiveTab] = useState('dashboard');
  const [branches, setBranches] = useState([]);
  const [recyclingRequests, setRecyclingRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    branchId: '',
    wasteWeight: '',
    notes: ''
  });
  const [branchesLoading, setBranchesLoading] = useState(false);

  const handleSystemSwitch = () => {
    navigate('/emergency');
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Implement search functionality
    console.log('Searching for:', searchQuery);
  };

  const handleLogin = () => {
    navigate('/login');
  };

  const handleSignup = () => {
    navigate('/register/customer');
  };

  // Customer dashboard functions
  useEffect(() => {
    if (isAuthenticated() && user?.role === 'Customer') {
      fetchBranches();
      if (user && user._id) {
        fetchRecyclingRequests();
      }
    }
  }, [user, isAuthenticated]);

  const fetchBranches = async () => {
    try {
      setBranchesLoading(true);
      const response = await axios.get('http://localhost:5000/public/branches');
      if (response.data.success) {
        setBranches(response.data.branches || []);
      } else {
        setBranches([]);
      }
    } catch (error) {
      console.error('Error fetching branches:', error);
      setBranches([]);
    } finally {
      setBranchesLoading(false);
    }
  };

  const fetchRecyclingRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get(`http://localhost:5000/RecyclingRequests/customer/${user._id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      setRecyclingRequests(response.data.requests || []);
    } catch (error) {
      console.error('Error fetching recycling requests:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRecyclingSubmit = async (e) => {
    e.preventDefault();
    
    if (!user) {
      alert('You must be logged in to submit recycling requests. Please log in again.');
      return;
    }
    
    if (!formData.branchId || !formData.wasteWeight) {
      alert('Please fill in all required fields');
      return;
    }

    const selectedBranch = branches.find(b => b._id === formData.branchId);
    if (!selectedBranch) {
      alert('Please select a valid branch');
      return;
    }

    setLoading(true);
    try {
      const requestData = {
        customerId: user._id,
        customerName: user.name,
        customerEmail: user.email,
        branchId: formData.branchId,
        branchName: selectedBranch.name,
        branchLocation: selectedBranch.location || selectedBranch.address || 'N/A',
        wasteWeight: parseFloat(formData.wasteWeight),
        notes: formData.notes
      };

      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found. Please log in again.');
      }

      const response = await axios.post('http://localhost:5000/RecyclingRequests', requestData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      setFormData({
        branchId: '',
        wasteWeight: '',
        notes: ''
      });

      fetchRecyclingRequests();
      alert('Recycling request submitted successfully! You will earn points once approved.');
    } catch (error) {
      console.error('Error submitting recycling request:', error);
      let errorMessage = 'Error submitting recycling request. Please try again.';
      
      if (error.response?.status === 401) {
        errorMessage = 'Authentication failed. Please log in again.';
      } else if (error.response?.status === 400) {
        errorMessage = error.response.data.message || 'Invalid request data.';
      }
      
      alert(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending': return 'bg-yellow-100 text-yellow-800';
      case 'Approved': return 'bg-green-100 text-green-800';
      case 'Rejected': return 'bg-red-100 text-red-800';
      case 'Completed': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600">
      {/* Universal Navbar */}
      <UniversalNavbar />

      {/* Main Content with top padding for fixed navbar */}
      <div className="pt-16">
        {/* Authenticated User Notice */}
        {isAuthenticated() && (
          <div className="bg-blue-50 border-b border-blue-200 py-2">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
              <p className="text-sm text-blue-700">
                Welcome back, {user?.name}! You're viewing the full homepage with all features. 
                <Link to="/" className="ml-2 underline hover:text-blue-900">View public version</Link>
              </p>
            </div>
          </div>
        )}
        
        {/* Hero Section */}
        <section className="relative py-20 px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto text-center">
            <h1 className="text-4xl md:text-6xl font-bold text-white mb-6">
              Premium Water Solutions
            </h1>
            <p className="text-xl md:text-2xl text-blue-100 mb-8 max-w-3xl mx-auto">
              Discover the purest drinking water and advanced filtration systems for your home and business
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link
                to="/products"
                className="bg-white text-blue-500 px-8 py-4 rounded-full font-semibold hover:shadow-xl hover:-translate-y-1 transition-all duration-300 text-lg"
              >
                Shop Now
              </Link>
              <Link
                to="/services"
                className="border-2 border-white text-white px-8 py-4 rounded-full font-semibold hover:bg-white hover:text-blue-500 transition-all duration-300 text-lg"
              >
                Our Services
              </Link>
            </div>
          </div>
        </section>

        {/* System Switch Banner - Only show for non-customers or non-authenticated users */}
        {(!isAuthenticated() || user?.role !== 'Customer') && (
          <div className="bg-gradient-to-r from-red-500 to-orange-500 text-white py-4">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                  </svg>
                  <span className="font-medium">Emergency Water Supply System Available</span>
                </div>
                <button
                  onClick={handleSystemSwitch}
                  className="bg-white/20 hover:bg-white/30 px-4 py-2 rounded-lg transition-colors duration-300 font-medium"
                >
                  Switch to Emergency System
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Product Slider */}
        <section className="py-16 px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto">
            <h2 className="text-3xl font-bold text-white text-center mb-12">Featured Products</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 text-center hover:bg-white/20 transition-all duration-300">
                <div className="text-6xl mb-4"></div>
                <h3 className="text-xl font-semibold text-white mb-4">RO Filters</h3>
                <p className="text-blue-100 mb-6">
                  Advanced reverse osmosis systems for the purest water
                </p>
                <Link
                  to="/products/filters"
                  className="inline-block bg-white text-blue-500 px-6 py-3 rounded-full font-medium hover:shadow-lg hover:-translate-y-0.5 transition-all duration-300"
                >
                  View Products
                </Link>
              </div>

              <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 text-center hover:bg-white/20 transition-all duration-300">
                <div className="text-6xl mb-4"></div>
                <h3 className="text-xl font-semibold text-white mb-4">Spare Parts</h3>
                <p className="text-blue-100 mb-6">
                  High-quality replacement parts for your filtration systems
                </p>
                <Link
                  to="/products/spare-parts"
                  className="inline-block bg-white text-blue-500 px-6 py-3 rounded-full font-medium hover:shadow-lg hover:-translate-y-0.5 transition-all duration-300"
                >
                  View Products
                </Link>
              </div>

              <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 text-center hover:bg-white/20 transition-all duration-300">
                <div className="text-6xl mb-4"></div>
                <h3 className="text-xl font-semibold text-white mb-4">Water Bottles</h3>
                <p className="text-blue-100 mb-6">
                  Premium drinking water in various sizes (5L, 10L, 20L)
                </p>
                <Link
                  to="/products/water-bottles"
                  className="inline-block bg-white text-blue-500 px-6 py-3 rounded-full font-medium hover:shadow-lg hover:-translate-y-0.5 transition-all duration-300"
                >
                  View Products
                </Link>
              </div>
            </div>
          </div>
        </section>

        {/* Services Section */}
        <section className="py-16 px-4 sm:px-6 lg:px-8 bg-white/10 backdrop-blur-md">
          <div className="max-w-7xl mx-auto">
            <h2 className="text-3xl font-bold text-white text-center mb-12">Our Services</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 text-center hover:bg-white/20 transition-all duration-300">
                <div className="text-6xl mb-4"></div>
                <h3 className="text-xl font-semibold text-white mb-4">Water Supply</h3>
                <p className="text-blue-100">
                  Reliable delivery of premium drinking water to your doorstep
                </p>
              </div>

              <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 text-center hover:bg-white/20 transition-all duration-300">
                <div className="text-6xl mb-4"></div>
                <h3 className="text-xl font-semibold text-white mb-4">Installation</h3>
                <p className="text-blue-100">
                  Professional installation and maintenance of water filtration systems
                </p>
              </div>

              <div className="bg-white/10 backdrop-blur-md rounded-2xl p-8 text-center hover:bg-white/20 transition-all duration-300">
                <div className="text-6xl mb-4"></div>
                <h3 className="text-xl font-semibold text-white mb-4">Recycling</h3>
                <p className="text-blue-100">
                  Environmentally responsible water bottle recycling program
                </p>
              </div>
            </div>
          </div>
        </section>

        {/* Customer Dashboard Section - Only show for authenticated customers */}
        {isAuthenticated() && user?.role === 'Customer' && (
          <section className="py-16 px-4 sm:px-6 lg:px-8 bg-white/5 backdrop-blur-md">
            <div className="max-w-7xl mx-auto">
              <h2 className="text-3xl font-bold text-white text-center mb-12">Customer Dashboard</h2>
              
              {/* Customer Navigation */}
              <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 mb-8">
                <div className="flex flex-wrap justify-center gap-4">
                  <button
                    onClick={() => setActiveTab('dashboard')}
                    className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                      activeTab === 'dashboard'
                        ? 'bg-white text-blue-600 shadow-lg'
                        : 'text-white hover:bg-white/20'
                    }`}
                  >
                    Dashboard
                  </button>
                  <button
                    onClick={() => setActiveTab('recycling')}
                    className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                      activeTab === 'recycling'
                        ? 'bg-white text-blue-600 shadow-lg'
                        : 'text-white hover:bg-white/20'
                    }`}
                  >
                     Recycling
                  </button>
                  <button
                    onClick={() => setActiveTab('orders')}
                    className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                      activeTab === 'orders'
                        ? 'bg-white text-blue-600 shadow-lg'
                        : 'text-white hover:bg-white/20'
                    }`}
                  >
                    My Orders
                  </button>
                  <button
                    onClick={() => setActiveTab('profile')}
                    className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                      activeTab === 'profile'
                        ? 'bg-white text-blue-600 shadow-lg'
                        : 'text-white hover:bg-white/20'
                    }`}
                  >
                    Profile
                  </button>
                </div>
              </div>

              {/* Dashboard Content */}
              {activeTab === 'dashboard' && (
                <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 text-center">
                    <div className="text-4xl mb-4"></div>
                    <h3 className="text-xl font-semibold text-white mb-2">Total Orders</h3>
                    <p className="text-3xl font-bold text-blue-200">12</p>
                  </div>
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 text-center">
                    <div className="text-4xl mb-4"></div>
                    <h3 className="text-xl font-semibold text-white mb-2">Completed</h3>
                    <p className="text-3xl font-bold text-green-200">8</p>
                  </div>
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 text-center">
                    <div className="text-4xl mb-4"></div>
                    <h3 className="text-xl font-semibold text-white mb-2">Pending</h3>
                    <p className="text-3xl font-bold text-yellow-200">4</p>
                  </div>
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 text-center">
                    <div className="text-4xl mb-4"></div>
                    <h3 className="text-xl font-semibold text-white mb-2">Points</h3>
                    <p className="text-3xl font-bold text-green-200">{user?.recyclingPoints || 0}</p>
                  </div>
                </div>
              )}

              {/* Recycling Content */}
              {activeTab === 'recycling' && (
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                    <h3 className="text-xl font-semibold text-white mb-4">Submit Recycling Request</h3>
                    <form onSubmit={handleRecyclingSubmit} className="space-y-4">
                      <div>
                        <label className="block text-sm font-medium text-white mb-2">Select Branch *</label>
                        <select
                          name="branchId"
                          value={formData.branchId}
                          onChange={handleInputChange}
                          required
                          disabled={branchesLoading}
                          className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/50"
                        >
                          <option value="">Choose a branch...</option>
                          {branches.map((branch) => (
                            <option key={branch._id} value={branch._id} className="text-gray-900">
                              {branch.name} - {branch.location || branch.address || 'N/A'}
                            </option>
                          ))}
                        </select>
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-white mb-2">Waste Weight (kg) *</label>
                        <input
                          type="number"
                          name="wasteWeight"
                          value={formData.wasteWeight}
                          onChange={handleInputChange}
                          min="0.1"
                          max="100"
                          step="0.1"
                          required
                          placeholder="Enter weight in kilograms"
                          className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/50"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-white mb-2">Notes (Optional)</label>
                        <textarea
                          name="notes"
                          value={formData.notes}
                          onChange={handleInputChange}
                          rows="3"
                          placeholder="Any additional information..."
                          className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/50"
                        />
                      </div>
                      <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-green-600 text-white py-3 px-4 rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
                      >
                        {loading ? (
                          <>
                            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                            Submitting...
                          </>
                        ) : (
                          <>
                            <span className="mr-2"></span>
                            Submit Recycling Request
                          </>
                        )}
                      </button>
                    </form>
                  </div>
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                    <h3 className="text-xl font-semibold text-white mb-4">Your Recycling History</h3>
                    {recyclingRequests.length > 0 ? (
                      <div className="space-y-3">
                        {recyclingRequests.slice(0, 5).map((request) => (
                          <div key={request._id} className="bg-white/10 rounded-lg p-3">
                            <div className="flex justify-between items-start mb-2">
                              <div>
                                <p className="font-medium text-white">{request.branchName}</p>
                                <p className="text-sm text-white/70">{request.branchLocation}</p>
                              </div>
                              <span className={`px-2 py-1 text-xs rounded-full ${getStatusColor(request.status)}`}>
                                {request.status}
                              </span>
                            </div>
                            <div className="text-sm text-white/70">
                              <p>Weight: {request.wasteWeight} kg</p>
                              <p>Points: {request.pointsEarned}</p>
                              <p>Date: {new Date(request.requestDate).toLocaleDateString()}</p>
                            </div>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <div className="text-center py-8">
                        <div className="text-4xl mb-2"></div>
                        <p className="text-white/70">No recycling requests yet</p>
                        <p className="text-sm text-white/50">Start recycling to earn points!</p>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Orders Content */}
              {activeTab === 'orders' && (
                <div className="space-y-4">
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="text-lg font-medium text-white">Order #ORD-001</h3>
                        <p className="text-white/70">5L Water Bottles x 10</p>
                        <p className="text-sm text-white/50">Ordered on: 2024-01-15</p>
                      </div>
                      <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-sm font-medium">
                        Delivered
                      </span>
                    </div>
                  </div>
                  <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="text-lg font-medium text-white">Order #ORD-002</h3>
                        <p className="text-white/70">10L Water Bottles x 5</p>
                        <p className="text-sm text-white/50">Ordered on: 2024-01-20</p>
                      </div>
                      <span className="bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full text-sm font-medium">
                        In Transit
                      </span>
                    </div>
                  </div>
                </div>
              )}

              {/* Profile Content */}
              {activeTab === 'profile' && (
                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                  <h3 className="text-xl font-semibold text-white mb-6">Profile Information</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <label className="block text-sm font-medium text-white/70">Name</label>
                      <p className="text-white">{user?.name || 'N/A'}</p>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70">Email</label>
                      <p className="text-white">{user?.email || 'N/A'}</p>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70">Phone</label>
                      <p className="text-white">{user?.phone || 'N/A'}</p>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70">Address</label>
                      <p className="text-white">{user?.address || 'N/A'}</p>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70">Role</label>
                      <p className="text-white">{user?.role || 'N/A'}</p>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white/70">Recycling Points</label>
                      <p className="text-green-200 font-medium">{user?.recyclingPoints || 0} </p>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </section>
        )}

        {/* Footer */}
        <footer className="bg-gray-900 text-white py-12 px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
              <div>
                <h4 className="text-lg font-semibold text-blue-400 mb-4">AquaLink</h4>
                <p className="text-gray-400">
                  Your trusted partner for premium water solutions and sustainable practices.
                </p>
              </div>

              <div>
                <h4 className="text-lg font-semibold text-blue-400 mb-4">Quick Links</h4>
                <ul className="space-y-2 text-gray-400">
                  <li><Link to="/products" className="hover:text-white transition-colors">Products</Link></li>
                  <li><Link to="/services" className="hover:text-white transition-colors">Services</Link></li>
                  <li><Link to="/branches" className="hover:text-white transition-colors">Branches</Link></li>
                  <li><Link to="/about" className="hover:text-white transition-colors">About Us</Link></li>
                </ul>
              </div>

              <div>
                <h4 className="text-lg font-semibold text-blue-400 mb-4">Contact</h4>
                <ul className="space-y-2 text-gray-400">
                  <li> info@aqualink.com</li>
                  <li> +94 11 234 5678</li>
                  <li> Colombo, Sri Lanka</li>
                </ul>
              </div>

              <div>
                <h4 className="text-lg font-semibold text-blue-400 mb-4">Follow Us</h4>
                <div className="flex space-x-4">
                  <a href="#" className="text-gray-400 hover:text-white transition-colors">
                    <span className="sr-only">Facebook</span>
                    <svg className="h-6 w-6" fill="currentColor" viewBox="0 0 24 24">
                      <path fillRule="evenodd" d="M22 12c0-5.523-4.477-10-10-10S2 6.477 2 12c0 4.991 3.657 9.128 8.438 9.878v-6.987h-2.54V12h2.54V9.797c0-2.506 1.492-3.89 3.777-3.89 1.094 0 2.238.195 2.238.195v2.46h-1.26c-1.243 0-1.63.771-1.63 1.562V12h2.773l-.443 2.89h-2.33v6.988C18.343 21.128 22 16.991 22 12z" clipRule="evenodd" />
                    </svg>
                  </a>
                  <a href="#" className="text-gray-400 hover:text-white transition-colors">
                    <span className="sr-only">Instagram</span>
                    <svg className="h-6 w-6" fill="currentColor" viewBox="0 0 24 24">
                      <path fillRule="evenodd" d="M12.315 2c2.43 0 2.784.013 3.808.06 1.064.049 1.791.218 2.427.465a4.902 4.902 0 011.772 1.153 4.902 4.902 0 011.153 1.772c.247.636.416 1.363.465 2.427.048 1.067.06 1.407.06 4.123v.08c0 2.643-.012 2.987-.06 4.043-.049 1.064-.218 1.791-.465 2.427a4.902 4.902 0 01-1.153 1.772 4.902 4.902 0 01-1.772 1.153c-.636.247-1.363.416-2.427.465-1.067.048-1.407.06-4.123.06h-.08c-2.643 0-2.987-.012-4.043-.06-1.064-.049-1.791-.218-2.427-.465a4.902 4.902 0 01-1.772-1.153 4.902 4.902 0 01-1.153-1.772c-.247-.636-.416-1.363-.465-2.427-.047-1.024-.06-1.379-.06-3.808v-.63c0-2.43.013-2.784.06-3.808.049-1.064.218-1.791.465-2.427a4.902 4.902 0 011.153-1.772A4.902 4.902 0 015.45 2.525c.636-.247 1.363-.416 2.427-.465C8.901 2.013 9.256 2 11.685 2h.63zm-.081 1.802h-.468c-2.456 0-2.784.011-3.807.058-.975.045-1.504.207-1.857.344-.467.182-.8.398-1.15.748-.35.35-.566.683-.748 1.15-.137.353-.3.882-.344 1.857-.047 1.023-.058 1.351-.058 3.807v.468c0 2.456.011 2.784.058 3.807.045.975.207 1.504.344 1.857.182.466.399.8.748 1.15.35.35.683.566 1.15.748.353.137.882.3 1.857.344 1.054.048 1.37.058 4.041.058h.08c2.597 0 2.917-.01 3.96-.058.976-.045 1.505-.207 1.858-.344.466-.182.8-.398 1.15-.748.35-.35.566-.683.748-1.15.137-.353.3-.882.344-1.857.048-1.055.058-1.37.058-4.041v-.08c0-2.597-.01-2.917-.058-3.96-.045-.976-.207-1.505-.344-1.858a3.097 3.097 0 00-.748-1.15 3.098 3.098 0 00-1.15-.748c-.353-.137-.882-.3-1.857-.344-1.023-.047-1.351-.058-3.807v-.468zM12 6.865a5.135 5.135 0 110 10.27 5.135 5.135 0 010-10.27zm0 1.802a3.333 3.333 0 100 6.666 3.333 3.333 0 000-6.666zm5.338-3.205a1.2 1.2 0 110 2.4 1.2 1.2 0 010-2.4z" clipRule="evenodd" />
                    </svg>
                  </a>
                  <a href="#" className="text-gray-400 hover:text-white transition-colors">
                    <span className="sr-only">Twitter</span>
                    <svg className="h-6 w-6" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M8.29 20.251c7.547 0 11.675-6.253 11.675-11.675 0-.178 0-.355-.012-.53A8.348 8.348 0 0022 5.92a8.19 8.19 0 01-2.357.646 4.118 4.118 0 001.804-2.27 8.224 8.224 0 01-2.605.996 4.107 4.107 0 00-6.993 3.743 11.65 11.65 0 01-8.457-4.287 4.106 4.106 0 001.27 5.477A4.072 4.072 0 012.8 9.713v.052a4.105 4.105 0 003.292 4.022 4.095 4.095 0 01-1.853.07 4.108 4.108 0 003.834 2.85A8.233 8.233 0 012 18.407a11.616 11.616 0 006.29 1.84" />
                    </svg>
                  </a>
                </div>
              </div>
            </div>

            <div className="border-t border-gray-700 pt-8 text-center">
              <p className="text-gray-400">&copy; 2024 AquaLink. All rights reserved.</p>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default HomePage;
