import React, { useState, useEffect } from 'react';
import { Link, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import axios from 'axios';
import BranchOrdersPage from './BranchOrdersPage';
import BranchInventoryPage from './BranchInventoryPage';
import BranchRecycleBin from './BranchRecycleBin';
import BranchDrivers from './BranchDrivers';
import BranchReports from './BranchReports';
import BranchSettings from './BranchSettings';
import BranchEmergency from './BranchEmergency';

function BranchManager() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const branchId = user?.branchId || 'BRANCH001';
  const branchName = user?.branchName || 'Main Branch';
  const [statistics, setStatistics] = useState({
    orders: { total: 0, pending: 0, processing: 0, delivered: 0 },
    inventory: { totalItems: 0, lowStock: 0, outOfStock: 0 },
    recycling: { totalBins: 0, criticalBins: 0, overallFill: 0 }
  });
  const [lowStockAlerts, setLowStockAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState(null);

  useEffect(() => {
    if (user) {
      fetchDashboardData();
      
      // Set up periodic refresh for low stock alerts (every 30 seconds)
      const interval = setInterval(() => {
        fetchDashboardData();
      }, 30000);
      
      return () => clearInterval(interval);
    }
  }, [user, branchId]);

  // Auto-hide notification after 5 seconds
  useEffect(() => {
    if (notification) {
      const timer = setTimeout(() => {
        setNotification(null);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [notification]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch all data in parallel
      const [orderStats, inventoryStats, recyclingStats, inventoryData] = await Promise.allSettled([
        axios.get(`http://localhost:5000/BranchOrders/statistics/${branchId}`),
        axios.get(`http://localhost:5000/BranchInventory/statistics/${branchId}`),
        axios.get(`http://localhost:5000/RecyclingBins/statistics/${branchId}`),
        axios.get(`http://localhost:5000/BranchInventory/branch/${branchId}`)
      ]);
      
      // Set statistics
      setStatistics({
        orders: orderStats.status === 'fulfilled' ? orderStats.value.data.statistics : { totalOrders: 0, pendingOrders: 0, processingOrders: 0, deliveredOrders: 0, todayOrders: 0 },
        inventory: inventoryStats.status === 'fulfilled' ? inventoryStats.value.data.statistics : { totalItems: 0, lowStockItems: 0, outOfStockItems: 0 },
        recycling: recyclingStats.status === 'fulfilled' ? recyclingStats.value.data.statistics : { totalBins: 0, criticalBins: 0, overallFillPercentage: 0 }
      });
      
      // Set low stock alerts by filtering inventory data
      if (inventoryData.status === 'fulfilled') {
        console.log('Inventory API response:', inventoryData.value.data);
        const allInventory = inventoryData.value.data.inventory || [];
        
        // Filter for low stock and out of stock items using calculated status
        const lowStockItems = allInventory.filter(item => {
          const calculatedStatus = item.quantity <= 0 ? 'Out of Stock' : 
                                 item.quantity <= item.minStockLevel ? 'Low Stock' : 'In Stock';
          return calculatedStatus === 'Low Stock' || calculatedStatus === 'Out of Stock';
        }).map(item => ({
          ...item,
          status: item.quantity <= 0 ? 'Out of Stock' : 
                 item.quantity <= item.minStockLevel ? 'Low Stock' : 'In Stock'
        }));
        
        console.log('Filtered low stock items:', lowStockItems);
        
        console.log('Setting low stock items:', lowStockItems);
        setLowStockAlerts(lowStockItems);
      } else {
        console.error('Error fetching inventory data:', inventoryData.reason);
        setLowStockAlerts([]);
      }
      
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      // Set fallback data
      setStatistics({
        orders: { totalOrders: 0, pendingOrders: 0, processingOrders: 0, deliveredOrders: 0, todayOrders: 0 },
        inventory: { totalItems: 0, lowStockItems: 0, outOfStockItems: 0 },
        recycling: { totalBins: 0, criticalBins: 0, overallFillPercentage: 0 }
      });
      // Show mock data for testing when there's an error
      setLowStockAlerts([
        {
          _id: 'mock1',
          name: 'RO Membranes',
          quantity: 3,
          minStockLevel: 10,
          status: 'Low Stock',
          branchId: branchId
        },
        {
          _id: 'mock2', 
          name: 'Water Pumps',
          quantity: 0,
          minStockLevel: 5,
          status: 'Out of Stock',
          branchId: branchId
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
  };

  const getStatusColor = (percentage) => {
    if (percentage >= 80) return 'text-red-600';
    if (percentage >= 60) return 'text-orange-600';
    if (percentage >= 40) return 'text-yellow-600';
    return 'text-green-600';
  };

  const isActiveRoute = (path) => {
    if (path === '/branch-manager') {
      return location.pathname === '/branch-manager' || location.pathname === '/branch-manager/';
    }
    return location.pathname === path;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Notification */}
      {notification && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-lg ${
          notification.type === 'success' ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
        }`}>
          <div className="flex items-center">
            <span className="mr-2">
              {notification.type === 'success' ? '✅' : '❌'}
            </span>
            {notification.message}
            <button
              onClick={() => setNotification(null)}
              className="ml-4 text-white hover:text-gray-200"
            >
              ×
            </button>
          </div>
        </div>
      )}

      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Branch Manager</h1>
              <p className="text-gray-600">{branchName}</p>
            </div>
            <div className="flex items-center space-x-4">
              <div className="text-right">
                <p className="text-sm text-gray-500">Branch ID</p>
                <p className="font-semibold text-gray-900">{branchId}</p>
              </div>
              <div className="flex items-center space-x-3">
                <div className="text-right">
                  <p className="text-sm text-gray-500">Welcome,</p>
                  <p className="font-semibold text-gray-900">{user?.name}</p>
                </div>
                <div className="h-10 w-10 bg-blue-600 rounded-full flex items-center justify-center">
                  <span className="text-white font-semibold">BM</span>
                </div>
                <button
                  onClick={handleLogout}
