// Low Stock Table Component
function LowStockTable({ lowStockAlerts, branchId, branchName }) {
  const [requestingItems, setRequestingItems] = useState([]);
  const [showRequestModal, setShowRequestModal] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);

  const displayItems = lowStockAlerts || [];

  const handleRequestFromFactory = (item) => {
    setSelectedItems([item]);
    setShowRequestModal(true);
  };

  const handleBulkRequest = () => {
    setSelectedItems(displayItems);
    setShowRequestModal(true);
  };

  const handleSubmitRequest = async () => {
    try {
      setRequestingItems(selectedItems.map(item => item._id));
      
      const requestData = {
        branchId,
        branchName,
        items: selectedItems.map(item => ({
          name: item.name,
          currentQuantity: item.quantity,
          minStockLevel: item.minStockLevel,
          maxStockLevel: item.maxStockLevel,
          requestedQuantity: item.maxStockLevel - item.quantity,
          unit: item.unit
        })),
        status: 'pending',
        requestedAt: new Date().toISOString()
      };

      const response = await axios.post('http://localhost:5000/FactoryRequests', requestData);
      
      if (response.data.success) {
        alert('Request sent to factory successfully!');
        setShowRequestModal(false);
        setSelectedItems([]);
      } else {
        alert('Failed to send request to factory');
      }
    } catch (error) {
      console.error('Error sending request to factory:', error);
      alert('Error sending request to factory');
    } finally {
      setRequestingItems([]);
    }
  };

  if (displayItems.length === 0) {
    return (
      <div className="mb-6">
        <div className="bg-green-50 border-l-4 border-green-400 p-4 rounded-md">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-green-400" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-green-800">
                All inventory items are well stocked!
              </h3>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="mb-6">
      <div className="bg-white rounded-lg shadow">
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <svg className="h-6 w-6 text-red-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
              </div>
              <div className="ml-3">
                <h3 className="text-lg font-medium text-gray-900">
                  Low Stock Items ({displayItems.length})
                </h3>
                <p className="text-sm text-gray-500">Items that need restocking from factory</p>
              </div>
            </div>
            <div className="flex space-x-2">
              <button
                onClick={handleBulkRequest}
                className="bg-blue-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-700 transition-colors"
              >
                Request All from Factory
              </button>
              <button
                onClick={() => window.location.reload()}
                className="text-gray-400 hover:text-gray-600 transition-colors"
                title="Refresh"
              >
                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                </svg>
              </button>
            </div>
          </div>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Item
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Current Stock
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Min/Max Levels
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Action
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {displayItems.map((item) => {
                const stockPercentage = (item.quantity / item.maxStockLevel) * 100;
                const isRequesting = requestingItems.includes(item._id);
                
                return (
                  <tr key={item._id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-gray-900">{item.name}</div>
                        <div className="text-sm text-gray-500">Unit: {item.unit}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="text-sm font-medium text-gray-900">{item.quantity} {item.unit}</div>
                        <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
                          <div 
                            className={`h-2 rounded-full ${
                              stockPercentage <= 0 ? 'bg-red-500' :
                              stockPercentage <= 20 ? 'bg-orange-500' :
                              stockPercentage <= 50 ? 'bg-yellow-500' : 'bg-green-500'
                            }`}
                            style={{ width: `${Math.max(stockPercentage, 5)}%` }}
                          ></div>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      <div>Min: {item.minStockLevel}</div>
                      <div>Max: {item.maxStockLevel}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                        item.status === 'Out of Stock' 
                          ? 'bg-red-100 text-red-800' 
                          : 'bg-orange-100 text-orange-800'
                      }`}>
                        {item.status}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <button
                        onClick={() => handleRequestFromFactory(item)}
                        disabled={isRequesting}
                        className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                          isRequesting
                            ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                            : 'bg-blue-600 text-white hover:bg-blue-700'
                        }`}
                      >
                        {isRequesting ? 'Requesting...' : `Request ${item.maxStockLevel - item.quantity} ${item.unit}`}
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* Request Modal */}
      {showRequestModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-medium text-gray-900">Request from Factory</h3>
                <button
                  onClick={() => setShowRequestModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              
              <div className="mb-4">
                <p className="text-sm text-gray-600 mb-3">
                  The following items will be requested from the factory to fill to maximum capacity:
                </p>
                <div className="space-y-2">
                  {selectedItems.map((item) => (
                    <div key={item._id} className="flex justify-between items-center p-3 bg-gray-50 rounded-md">
                      <div>
                        <div className="font-medium text-gray-900">{item.name}</div>
                        <div className="text-sm text-gray-500">
                          Current: {item.quantity} {item.unit} → Requesting: {item.maxStockLevel - item.quantity} {item.unit}
                        </div>
                      </div>
                      <div className="text-sm font-medium text-blue-600">
                        Total: {item.maxStockLevel} {item.unit}
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div className="flex justify-end space-x-3">
                <button
                  onClick={() => setShowRequestModal(false)}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300 transition-colors"
                >
                  Cancel
                </button>
                <button
                  onClick={handleSubmitRequest}
                  className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 transition-colors"
                >
                  Send Request to Factory
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default BranchManager;
