import React, { useState, useEffect } from 'react';
import { useAuth } from '../../Context/AuthContext';
import axios from 'axios';

const CustomerDashboard = () => {
    const { user, logout } = useAuth();

    // Navigation guard - ensure only Customer users can access
    useEffect(() => {
        if (user && user.role !== 'Customer') {
            console.error('Access denied: User does not have Customer privileges');
            // Redirect based on user role
            if (user.role === 'Factory Manager') {
                window.location.href = '/factory-manager';
            } else if (user.role === 'Branch Manager') {
                window.location.href = '/branch-manager';
            } else if (user.role === 'Admin') {
                window.location.href = '/admin-dashboard';
            } else {
                window.location.href = '/login';
            }
        }
    }, [user]);

    // If user is not authenticated or doesn't have the right role, show loading
    if (!user || user.role !== 'Customer') {
        return (
            <div className="min-h-screen bg-gray-50 flex items-center justify-center">
                <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
            </div>
        );
    }

    const [activeTab, setActiveTab] = useState('dashboard');
    const [branches, setBranches] = useState([]);
    const [recyclingRequests, setRecyclingRequests] = useState([]);
    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({
        branchId: '',
        wasteWeight: '',
        notes: ''
    });
    const [branchesLoading, setBranchesLoading] = useState(false);

    useEffect(() => {
        console.log('ðŸ”„ CustomerDashboard useEffect triggered');
        console.log('ðŸ‘¤ Current user:', user);
        
        fetchBranches();
        if (user && user._id) {
            console.log('âœ… User authenticated, fetching recycling requests...');
            fetchRecyclingRequests();
        } else {
            console.log('âŒ User not authenticated or missing ID');
        }
    }, [user]);

    const fetchBranches = async () => {
        try {
            setBranchesLoading(true);
            console.log('ðŸ”„ Fetching branches from backend...');
            
            const response = await axios.get('http://localhost:5000/public/branches');
            console.log('ðŸ“¡ Backend response:', response.data);
            
            if (response.data.success) {
                const fetchedBranches = response.data.branches || [];
                console.log('âœ… Branches fetched successfully:', fetchedBranches);
                setBranches(fetchedBranches);
            } else {
                console.error('âŒ Error in response:', response.data.message);
                setBranches([]);
            }
        } catch (error) {
            console.error('âŒ Error fetching branches:', error);
            if (error.response) {
                console.error('Response data:', error.response.data);
                console.error('Response status:', error.response.status);
            }
            setBranches([]);
        } finally {
            setBranchesLoading(false);
        }
    };

    const fetchRecyclingRequests = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No authentication token found');
                return;
            }

            const response = await axios.get(`http://localhost:5000/RecyclingRequests/customer/${user._id}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            setRecyclingRequests(response.data.requests || []);
        } catch (error) {
            console.error('Error fetching recycling requests:', error);
            if (error.response?.status === 401) {
                console.error('Authentication failed - user may need to log in again');
            }
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleRecyclingSubmit = async (e) => {
        e.preventDefault();
        
        // Check if user is logged in
        if (!user) {
            alert('You must be logged in to submit recycling requests. Please log in again.');
            return;
        }
        
        if (!formData.branchId || !formData.wasteWeight) {
            alert('Please fill in all required fields');
            return;
        }

        const selectedBranch = branches.find(b => b._id === formData.branchId);
        if (!selectedBranch) {
            alert('Please select a valid branch');
            return;
        }

        setLoading(true);
        try {
            // Debug logging
            console.log('ðŸ”„ Submitting recycling request...');
            console.log('ðŸ‘¤ User:', user);
            console.log('ðŸ¢ Selected branch:', selectedBranch);
            console.log('ðŸ“¦ Form data:', formData);
            
            const requestData = {
                customerId: user._id,
                customerName: user.name,
                customerEmail: user.email,
                branchId: formData.branchId,
                branchName: selectedBranch.name,
                branchLocation: selectedBranch.location || selectedBranch.address || 'N/A',
                wasteWeight: parseFloat(formData.wasteWeight),
                notes: formData.notes
            };

            console.log('ðŸ“¤ Request data being sent:', requestData);

            // Get token from localStorage and add to headers
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('No authentication token found. Please log in again.');
            }

            console.log('ðŸ”‘ Token found:', token.substring(0, 20) + '...');

            const response = await axios.post('http://localhost:5000/RecyclingRequests', requestData, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            
            console.log('âœ… Response received:', response.data);
            
            // Reset form
            setFormData({
                branchId: '',
                wasteWeight: '',
                notes: ''
            });

            // Refresh recycling requests
            fetchRecyclingRequests();
            
            alert('Recycling request submitted successfully! You will earn points once approved.');
        } catch (error) {
            console.error('âŒ Error submitting recycling request:', error);
            console.error('ðŸ“Š Error response:', error.response?.data);
            console.error('ðŸ“Š Error status:', error.response?.status);
            
            let errorMessage = 'Error submitting recycling request. Please try again.';
            
            if (error.response?.status === 401) {
                errorMessage = 'Authentication failed. Please log in again.';
            } else if (error.response?.status === 400) {
                errorMessage = error.response.data.message || 'Invalid request data.';
            } else if (error.response?.status === 404) {
                errorMessage = 'Customer not found. Please check your account.';
            } else if (error.response?.status === 500) {
                errorMessage = `Server error: ${error.response.data?.error || 'Unknown error'}. Please try again later.`;
            }
            
            alert(errorMessage);
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        logout();
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'Pending': return 'bg-yellow-100 text-yellow-800';
            case 'Approved': return 'bg-green-100 text-green-800';
            case 'Rejected': return 'bg-red-100 text-red-800';
            case 'Completed': return 'bg-blue-100 text-blue-800';
            default: return 'bg-gray-100 text-gray-800';
        }
    };

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Header */}
            <header className="bg-white shadow-sm border-b">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center py-4">
                        <div className="flex items-center">
                            <div className="flex-shrink-0">
                                <h1 className="text-2xl font-bold text-blue-600">AquaLink</h1>
                            </div>
                        </div>
                        <div className="flex items-center space-x-4">
                            <span className="text-gray-700">Welcome, {user?.name}</span>
                            <div className="flex items-center space-x-2">
                                <span className="text-sm text-green-600 font-medium">
                                    ðŸŒ± {user?.recyclingPoints || 0} Points
                                </span>
                            </div>
                            <button
                                onClick={handleLogout}
                                className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </header>

            {/* Navigation */}
            <nav className="bg-white shadow-sm">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex space-x-8">
                        <button
                            onClick={() => setActiveTab('dashboard')}
                            className={`py-4 px-1 border-b-2 font-medium text-sm ${
                                activeTab === 'dashboard'
                                    ? 'border-blue-500 text-blue-600'
                                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                            }`}
                        >
                            Dashboard
                        </button>
                        <button
                            onClick={() => setActiveTab('recycling')}
                            className={`py-4 px-1 border-b-2 font-medium text-sm ${
                                activeTab === 'recycling'
                                    ? 'border-blue-500 text-blue-600'
                                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                            }`}
                        >
                            ðŸŒ± Recycling
                        </button>
                        <button
                            onClick={() => setActiveTab('orders')}
                            className={`py-4 px-1 border-b-2 font-medium text-sm ${
                                activeTab === 'orders'
                                    ? 'border-blue-500 text-blue-600'
                                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                            }`}
                        >
                            My Orders
                        </button>
                        <button
                            onClick={() => setActiveTab('profile')}
                            className={`py-4 px-1 border-b-2 font-medium text-sm ${
                                activeTab === 'profile'
                                    ? 'border-blue-500 text-blue-600'
                                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                            }`}
                        >
                            Profile
                        </button>
                    </div>
                </div>
            </nav>

            {/* Main Content */}
            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                {activeTab === 'dashboard' && (
                    <div className="px-4 py-6 sm:px-0">
                        <div className="border-4 border-dashed border-gray-200 rounded-lg p-6">
                            <h2 className="text-2xl font-bold text-gray-900 mb-6">Customer Dashboard</h2>
                            
                            {/* Quick Stats */}
                            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
                                <div className="bg-white overflow-hidden shadow rounded-lg">
                                    <div className="p-5">
                                        <div className="flex items-center">
                                            <div className="flex-shrink-0">
                                                <div className="w-8 h-8 bg-blue-500 rounded-md flex items-center justify-center">
                                                    <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
                                                    </svg>
                                                </div>
                                            </div>
                                            <div className="ml-5 w-0 flex-1">
                                                <dl>
                                                    <dt className="text-sm font-medium text-gray-500 truncate">Total Orders</dt>
                                                    <dd className="text-lg font-medium text-gray-900">12</dd>
                                                </dl>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="bg-white overflow-hidden shadow rounded-lg">
                                    <div className="p-5">
                                        <div className="flex items-center">
                                            <div className="flex-shrink-0">
                                                <div className="w-8 h-8 bg-green-500 rounded-md flex items-center justify-center">
                                                    <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                                                    </svg>
                                                </div>
                                            </div>
                                            <div className="ml-5 w-0 flex-1">
                                                <dl>
                                                    <dt className="text-sm font-medium text-gray-500 truncate">Completed Orders</dt>
                                                    <dd className="text-lg font-medium text-gray-900">8</dd>
                                                </dl>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="bg-white overflow-hidden shadow rounded-lg">
                                    <div className="p-5">
                                        <div className="flex items-center">
                                            <div className="flex-shrink-0">
                                                <div className="w-8 h-8 bg-yellow-500 rounded-md flex items-center justify-center">
                                                    <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                                                    </svg>
                                                </div>
                                            </div>
                                            <div className="ml-5 w-0 flex-1">
                                                <dl>
                                                    <dt className="text-sm font-medium text-gray-500 truncate">Pending Orders</dt>
                                                    <dd className="text-lg font-medium text-gray-900">4</dd>
                                                </dl>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="bg-white overflow-hidden shadow rounded-lg">
                                    <div className="p-5">
                                        <div className="flex items-center">
                                            <div className="flex-shrink-0">
                                                <div className="w-8 h-8 bg-green-600 rounded-md flex items-center justify-center">
                                                    <span className="text-white text-lg">ðŸŒ±</span>
                                                </div>
                                            </div>
                                            <div className="ml-5 w-0 flex-1">
                                                <dl>
                                                    <dt className="text-sm font-medium text-gray-500 truncate">Recycling Points</dt>
                                                    <dd className="text-lg font-medium text-gray-900">{user?.recyclingPoints || 0}</dd>
                                                </dl>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Quick Actions */}
                            <div className="bg-white shadow rounded-lg p-6">
                                <h3 className="text-lg font-medium text-gray-900 mb-4">Quick Actions</h3>
                                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                    <button 
                                        onClick={() => setActiveTab('recycling')}
                                        className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 flex items-center justify-center"
                                    >
                                        <span className="mr-2">ðŸŒ±</span>
                                        Submit Recycling
                                    </button>
                                    <button className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 flex items-center justify-center">
                                        <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
                                        </svg>
                                        Place New Order
                                    </button>
                                    <button className="bg-purple-600 text-white px-4 py-2 rounded-md hover:bg-purple-700 flex items-center justify-center">
                                        <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                                        </svg>
                                        View Order History
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {activeTab === 'recycling' && (
                    <div className="px-4 py-6 sm:px-0">
                        <div className="border-4 border-dashed border-gray-200 rounded-lg p-6">
                            <h2 className="text-2xl font-bold text-gray-900 mb-6">ðŸŒ± Recycling Center</h2>
                            
                            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                                {/* Recycling Form */}
                                <div className="bg-white shadow rounded-lg p-6">
                                    <h3 className="text-lg font-medium text-gray-900 mb-4">Submit Recycling Request</h3>
                                    <form onSubmit={handleRecyclingSubmit} className="space-y-4">
                                        <div>
                                            <div className="flex justify-between items-center mb-2">
                                                <label className="block text-sm font-medium text-gray-700">
                                                    Select Branch *
                                                </label>
                                                <button
                                                    type="button"
                                                    onClick={fetchBranches}
                                                    disabled={branchesLoading}
                                                    className="text-sm text-blue-600 hover:text-blue-800 disabled:opacity-50 flex items-center gap-1"
                                                >
                                                    {branchesLoading ? (
                                                        <>
                                                            <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-blue-600"></div>
                                                            Loading...
                                                        </>
                                                    ) : (
                                                        <>
                                                            ðŸ”„ Refresh
                                                        </>
                                                    )}
                                                </button>
                                            </div>
                                            <select
                                                name="branchId"
                                                value={formData.branchId}
                                                onChange={handleInputChange}
                                                required
                                                disabled={branchesLoading}
                                                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 disabled:opacity-50"
                                            >
                                                <option value="">Choose a branch...</option>
                                                {branchesLoading ? (
                                                    <option value="" disabled>Loading branches...</option>
                                                ) : branches.length > 0 ? (
                                                    branches.map((branch) => (
                                                        <option key={branch._id} value={branch._id}>
                                                            {branch.name} - {branch.location || branch.address || 'N/A'}
                                                        </option>
                                                    ))
                                                ) : (
                                                    <option value="" disabled>No branches available</option>
                                                )}
                                            </select>
                                            {branches.length === 0 && !branchesLoading && (
                                                <p className="text-sm text-red-500 mt-1">
                                                    No branches available. Please contact support.
                                                </p>
                                            )}
                                            {branchesLoading && (
                                                <p className="text-sm text-blue-500 mt-1">
                                                    Loading available branches...
                                                </p>
                                            )}
                                            {branches.length > 0 && !branchesLoading && (
                                                <p className="text-sm text-green-500 mt-1">
                                                    {branches.length} branch{branches.length !== 1 ? 'es' : ''} available
                                                </p>
                                            )}
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Waste Weight (kg) *
                                            </label>
                                            <input
                                                type="number"
                                                name="wasteWeight"
                                                value={formData.wasteWeight}
                                                onChange={handleInputChange}
                                                min="0.1"
                                                max="100"
                                                step="0.1"
                                                required
                                                placeholder="Enter weight in kilograms"
                                                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                                            />
                                            <p className="text-sm text-gray-500 mt-1">
                                                Minimum: 0.1kg, Maximum: 100kg
                                            </p>
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                                Notes (Optional)
                                            </label>
                                            <textarea
                                                name="notes"
                                                value={formData.notes}
                                                onChange={handleInputChange}
                                                rows="3"
                                                placeholder="Any additional information about your recycling..."
                                                className="w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                                            />
                                        </div>

                                        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                                            <h4 className="text-sm font-medium text-blue-800 mb-2">Points System</h4>
                                            <p className="text-sm text-blue-700">
                                                You will earn <strong>1 point per kilogram</strong> of plastic waste recycled.
                                                Points can be used for future purchases and discounts.
                                            </p>
                                        </div>

                                        <button
                                            type="submit"
                                            disabled={loading}
                                            className="w-full bg-green-600 text-white py-2 px-4 rounded-md hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
                                        >
                                            {loading ? (
                                                <>
                                                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                                                    Submitting...
                                                </>
                                            ) : (
                                                <>
                                                    <span className="mr-2">ðŸŒ±</span>
                                                    Submit Recycling Request
                                                </>
                                            )}
                                        </button>
                                    </form>
                                </div>

                                {/* Recycling History */}
                                <div className="bg-white shadow rounded-lg p-6">
                                    <h3 className="text-lg font-medium text-gray-900 mb-4">Your Recycling History</h3>
                                    
                                    {recyclingRequests.length > 0 ? (
                                        <div className="space-y-3">
                                            {recyclingRequests.slice(0, 5).map((request) => (
                                                <div key={request._id} className="border border-gray-200 rounded-lg p-3">
                                                    <div className="flex justify-between items-start mb-2">
                                                        <div>
                                                            <p className="font-medium text-gray-900">{request.branchName}</p>
                                                            <p className="text-sm text-gray-600">{request.branchLocation}</p>
                                                        </div>
                                                        <span className={`px-2 py-1 text-xs rounded-full ${getStatusColor(request.status)}`}>
                                                            {request.status}
                                                        </span>
                                                    </div>
                                                    <div className="text-sm text-gray-600">
                                                        <p>Weight: {request.wasteWeight} kg</p>
                                                        <p>Points: {request.pointsEarned}</p>
                                                        <p>Date: {new Date(request.requestDate).toLocaleDateString()}</p>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    ) : (
                                        <div className="text-center py-8">
                                            <div className="text-4xl mb-2">ðŸŒ±</div>
                                            <p className="text-gray-500">No recycling requests yet</p>
                                            <p className="text-sm text-gray-400">Start recycling to earn points!</p>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {activeTab === 'orders' && (
                    <div className="px-4 py-6 sm:px-0">
                        <div className="border-4 border-dashed border-gray-200 rounded-lg p-6">
                            <h2 className="text-2xl font-bold text-gray-900 mb-6">My Orders</h2>
                            
                            {/* Sample Orders */}
                            <div className="space-y-4">
                                <div className="bg-white shadow rounded-lg p-6">
                                    <div className="flex justify-between items-start">
                                        <div>
                                            <h3 className="text-lg font-medium text-gray-900">Order #ORD-001</h3>
                                            <p className="text-gray-600">5L Water Bottles x 10</p>
                                            <p className="text-sm text-gray-500">Ordered on: 2024-01-15</p>
                                        </div>
                                        <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-sm font-medium">
                                            Delivered
                                        </span>
                                    </div>
                                </div>

                                <div className="bg-white shadow rounded-lg p-6">
                                    <div className="flex justify-between items-start">
                                        <div>
                                            <h3 className="text-lg font-medium text-gray-900">Order #ORD-002</h3>
                                            <p className="text-gray-600">10L Water Bottles x 5</p>
                                            <p className="text-sm text-gray-500">Ordered on: 2024-01-20</p>
                                        </div>
                                        <span className="bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full text-sm font-medium">
                                            In Transit
                                        </span>
                                    </div>
                                </div>

                                <div className="bg-white shadow rounded-lg p-6">
                                    <div className="flex justify-between items-start">
                                        <div>
                                            <h3 className="text-lg font-medium text-gray-900">Order #ORD-003</h3>
                                            <p className="text-gray-600">Water Filter System x 1</p>
                                            <p className="text-sm text-gray-500">Ordered on: 2024-01-25</p>
                                        </div>
                                        <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded-full text-sm font-medium">
                                            Processing
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {activeTab === 'profile' && (
                    <div className="px-4 py-6 sm:px-0">
                        <div className="border-4 border-dashed border-gray-200 rounded-lg p-6">
                            <h2 className="text-2xl font-bold text-gray-900 mb-6">Profile Information</h2>
                            
                            <div className="bg-white shadow rounded-lg p-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Name</label>
                                        <p className="mt-1 text-sm text-gray-900">{user?.name || 'N/A'}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Email</label>
                                        <p className="mt-1 text-sm text-gray-900">{user?.email || 'N/A'}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Phone</label>
                                        <p className="mt-1 text-sm text-gray-900">{user?.phone || 'N/A'}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Address</label>
                                        <p className="mt-1 text-sm text-gray-900">{user?.address || 'N/A'}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Role</label>
                                        <p className="mt-1 text-sm text-gray-900">{user?.role || 'N/A'}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Recycling Points</label>
                                        <p className="mt-1 text-sm text-green-600 font-medium">{user?.recyclingPoints || 0} ðŸŒ±</p>
                                    </div>
                                </div>
                                
                                <div className="mt-6">
                                    <button className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700">
                                        Edit Profile
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </main>
        </div>
    );
};

export default CustomerDashboard;
