import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  PieChart, Pie, Cell, LineChart, Line
} from 'recharts';
import axios from 'axios';

const FactoryDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  
  // Check if user has factory manager role
  useEffect(() => {
    if (user && user.role !== 'Factory Manager') {
      console.error('User does not have factory manager privileges:', user.role);
      alert('Access denied. Factory Manager privileges required.');
      window.location.href = '/login';
    }
  }, [user]);
  
  const [inventoryStats, setInventoryStats] = useState(null);
  const [inventoryData, setInventoryData] = useState([]);
  const [pendingOrders, setPendingOrders] = useState([]);
  const [orderStats, setOrderStats] = useState(null);
  const [recentActivities, setRecentActivities] = useState([]);
  const [deliveryStats, setDeliveryStats] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch data from API
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        console.log('🔄 Fetching factory dashboard data...');
        
        // Fetch all data in parallel
        const [statsResponse, inventoryResponse, ordersResponse, orderStatsResponse, activitiesResponse, deliveryResponse] = await Promise.allSettled([
          axios.get('http://localhost:5000/Inventory/stats/overview'),
          axios.get('http://localhost:5000/Inventory'),
          axios.get('http://localhost:5000/Orders/pending'),
          axios.get('http://localhost:5000/Orders/stats/overview'),
          axios.get('http://localhost:5000/Orders/activities/recent'),
          axios.get('http://localhost:5000/Delivery/stats')
        ]);

        // Set inventory stats
        if (statsResponse.status === 'fulfilled') {
          setInventoryStats(statsResponse.value.data.stats);
        } else {
          console.error('Error fetching inventory stats:', statsResponse.reason);
          setInventoryStats({
            totalItems: 8,
            lowStockItems: 2,
            outOfStockItems: 1,
            inStockItems: 5,
            totalQuantity: 450
          });
        }

        // Set inventory data
        if (inventoryResponse.status === 'fulfilled') {
          setInventoryData(inventoryResponse.value.data.inventory || []);
        } else {
          console.error('Error fetching inventory data:', inventoryResponse.reason);
          setInventoryData([]);
        }

        // Set pending orders
        if (ordersResponse.status === 'fulfilled') {
          setPendingOrders(ordersResponse.value.data.orders || []);
        } else {
          console.error('Error fetching pending orders:', ordersResponse.reason);
          setPendingOrders([]);
        }

        // Set order stats
        if (orderStatsResponse.status === 'fulfilled') {
          setOrderStats(orderStatsResponse.value.data.stats);
        } else {
          console.error('Error fetching order stats:', orderStatsResponse.reason);
          setOrderStats({
            totalOrders: 0,
            pendingOrders: 0,
            processingOrders: 0,
            urgentOrders: 0,
            highPriorityOrders: 0
          });
        }

        // Set recent activities
        if (activitiesResponse.status === 'fulfilled') {
          setRecentActivities(activitiesResponse.value.data.activities || []);
        } else {
          console.error('Error fetching recent activities:', activitiesResponse.reason);
          setRecentActivities([]);
        }

        // Set delivery stats
        if (deliveryResponse.status === 'fulfilled') {
          setDeliveryStats(deliveryResponse.value.data.stats);
        } else {
          console.error('Error fetching delivery stats:', deliveryResponse.reason);
          setDeliveryStats({
            factoryDeliveriesToday: 0,
            branchDeliveriesToday: 0,
            pendingShipments: 0,
            pendingDeliveries: 0
          });
        }

      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'In Stock': return 'text-green-600 bg-green-100';
      case 'Low Stock': return 'text-yellow-600 bg-yellow-100';
      case 'Out of Stock': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getPriorityColor = (priority) => {
