import React, { useState, useEffect } from 'react';
import { useAuth } from '../../Context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { employeeAPI, customerPurchaseAPI } from '../../utils/apiService';

const DriverDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [deliveries, setDeliveries] = useState([]);
  const [assignedDeliveries, setAssignedDeliveries] = useState([]);
  const [emergencyNotifications, setEmergencyNotifications] = useState([]);
  const [deliveryLogs, setDeliveryLogs] = useState([]);
  const [profile, setProfile] = useState({
    name: user?.name || 'Driver Name',
    email: user?.email || 'driver@example.com',
    phone: user?.phone || '+94 71 123 4567',
    vehicleNumber: user?.vehicleNumber || 'WP-ABC-1234',
    availability: user?.driverStatus || 'Available',
    rating: 4.5,
    totalDeliveries: 156,
    salary: user?.salary || 'Rs. 45,000',
    branch: user?.branchName || user?.branch || 'Main Branch'
  });
  const [loading, setLoading] = useState(true);

  // Fetch assigned deliveries
  const fetchAssignedDeliveries = async () => {
    try {
      if (user?._id) {
        const response = await customerPurchaseAPI.getDriverDeliveries(user._id);
        setAssignedDeliveries(response.deliveries || []);
      }
    } catch (error) {
      console.error('Error fetching assigned deliveries:', error);
    }
  };

  useEffect(() => {
    if (user?._id) {
      fetchAssignedDeliveries();
    }
  }, [user]);

  // Sample data (in real app, this would come from API)
  const sampleDeliveries = [
    {
      id: 1,
      orderId: 'ORD-12345678',
      customerName: 'John Doe',
      address: '123 Main St, Colombo 03',
      items: ['5L Water Bottle Ã— 2', 'RO Filter Ã— 1'],
      status: 'In Progress',
      startTime: '2024-03-15 10:30',
      estimatedDelivery: '2024-03-15 12:00',
      route: 'Colombo 03 â†’ Colombo 05 â†’ Colombo 07'
    },
    {
      id: 2,
      orderId: 'ORD-12345679',
      customerName: 'Jane Smith',
      address: '456 Lake Rd, Kandy',
      items: ['10L Water Bottle Ã— 1', 'Sediment Filter Ã— 2'],
      status: 'Pending',
      startTime: null,
      estimatedDelivery: '2024-03-15 14:00',
      route: 'Kandy â†’ Peradeniya â†’ Katugastota'
    },
    {
      id: 3,
      orderId: 'ORD-12345680',
      customerName: 'Bob Wilson',
      address: '789 Beach Rd, Galle',
      items: ['20L Water Bottle Ã— 3'],
      status: 'Completed',
      startTime: '2024-03-15 08:00',
      completedTime: '2024-03-15 09:30',
      route: 'Galle â†’ Unawatuna â†’ Galle'
    }
  ];

  const sampleEmergencyNotifications = [
    {
      id: 1,
      location: 'Colombo Fire Station',
      waterLevel: '25%',
      priority: 'High',
      status: 'Approved',
      assignedTime: '2024-03-15 14:30',
      estimatedArrival: '15 minutes'
    },
    {
      id: 2,
      location: 'Kandy Fire Station',
      waterLevel: '20%',
      priority: 'High',
      status: 'Pending',
      assignedTime: '2024-03-15 13:15',
      estimatedArrival: '25 minutes'
    }
  ];

  const sampleDeliveryLogs = [
    {
      id: 1,
      date: '2024-03-15',
      deliveries: 8,
      completed: 7,
      cancelled: 1,
      totalDistance: '45 km',
      totalEarnings: 2500
    },
    {
      id: 2,
      date: '2024-03-14',
      deliveries: 6,
      completed: 6,
      cancelled: 0,
      totalDistance: '38 km',
      totalEarnings: 2200
    },
    {
      id: 3,
      date: '2024-03-13',
      deliveries: 9,
      completed: 8,
      cancelled: 1,
      totalDistance: '52 km',
      totalEarnings: 2800
    }
  ];

  useEffect(() => {
    // Simulate API calls
    setTimeout(() => {
      setDeliveries(sampleDeliveries);
      setEmergencyNotifications(sampleEmergencyNotifications);
      setDeliveryLogs(sampleDeliveryLogs);
      setLoading(false);
    }, 1000);
  }, []);

  const handleStartDelivery = (deliveryId) => {
    setDeliveries(prev => 
      prev.map(delivery => 
        delivery.id === deliveryId 
          ? { ...delivery, status: 'In Progress', startTime: new Date().toLocaleString() }
          : delivery
      )
    );
  };

  const handleCompleteDelivery = (deliveryId) => {
    setDeliveries(prev => 
      prev.map(delivery => 
        delivery.id === deliveryId 
          ? { ...delivery, status: 'Completed', completedTime: new Date().toLocaleString() }
          : delivery
      )
    );
  };

  const handleToggleAvailability = async () => {
    try {
      const newStatus = profile.availability === 'Available' ? 'Off Duty' : 'Available';

      // Update the backend using the driver's own status endpoint
      await employeeAPI.updateOwnDriverStatus(newStatus);

      // Update local state
      setProfile(prev => ({
        ...prev,
        availability: newStatus
      }));

      console.log(`âœ… Driver status updated to: ${newStatus}`);
    } catch (error) {
      console.error('âŒ Error updating driver status:', error);
      // You could add a notification here to show the error to the user
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const downloadDeliveryLogs = (period = 'daily') => {
    let filteredLogs = deliveryLogs;
    let periodText = 'Daily';

    if (period === 'monthly') {
      filteredLogs = deliveryLogs.slice(0, 30); // Last 30 days
      periodText = 'Monthly';
    } else if (period === 'yearly') {
      filteredLogs = deliveryLogs.slice(0, 365); // Last 365 days
      periodText = 'Yearly';
    }

    const logContent = `
      AQUALINK - DRIVER DELIVERY LOGS
      ===============================
      Driver: ${profile.name}
      Period: ${periodText} Report
      Generated: ${new Date().toLocaleDateString()}

      DELIVERY SUMMARY
      ================
      ${filteredLogs.map(log => 
        `Date: ${log.date}
         Total Deliveries: ${log.deliveries}
         Completed: ${log.completed}
         Cancelled: ${log.cancelled}
         Distance: ${log.totalDistance}
         Earnings: Rs. ${log.totalEarnings}
         ------------------------------`
      ).join('\n')}

      TOTAL STATISTICS
      ================
      Total Deliveries: ${filteredLogs.reduce((sum, log) => sum + log.deliveries, 0)}
      Total Completed: ${filteredLogs.reduce((sum, log) => sum + log.completed, 0)}
      Total Distance: ${filteredLogs.reduce((sum, log) => sum + parseInt(log.totalDistance), 0)} km
      Total Earnings: Rs. ${filteredLogs.reduce((sum, log) => sum + log.totalEarnings, 0)}

      Generated by: ${user?.name}
      Driver Dashboard
    `;

    const blob = new Blob([logContent], { type: 'text/plain' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `delivery-logs-${period}-${profile.name}.txt`;
    document.body.appendChild(a);
    a.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(a);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading driver dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Driver Dashboard</h1>
              <p className="text-gray-600 mt-1">Welcome back, {user?.name}</p>
            </div>
            <div className="flex items-center space-x-4">
              <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                profile.availability === 'Available' 
                  ? 'bg-green-100 text-green-800' 
                  : profile.availability === 'On Delivery'
                  ? 'bg-blue-100 text-blue-800'
                  : profile.availability === 'On Leave'
                  ? 'bg-yellow-100 text-yellow-800'
                  : 'bg-gray-100 text-gray-800'
              }`}>
                {profile.availability}
              </span>
              <button
                onClick={handleToggleAvailability}
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors duration-300"
              >
                {profile.availability === 'Available' ? 'Go Off Duty' : 'Go Available'}
              </button>
              <button
                onClick={handleLogout}
                className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-colors duration-300"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Navigation Tabs */}
        <div className="bg-white rounded-lg shadow-sm mb-8">
          <div className="border-b border-gray-200">
            <nav className="flex space-x-8 px-6">
              {[
                { id: 'dashboard', name: 'Dashboard', icon: 'ðŸ“Š' },
                { id: 'deliveries', name: 'Deliveries', icon: 'ðŸšš' },
                { id: 'emergency', name: 'Emergency', icon: 'ðŸš¨' },
                { id: 'profile', name: 'Profile', icon: 'ðŸ‘¤' },
                { id: 'logs', name: 'Delivery Logs', icon: 'ðŸ“‹' }
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm ${
                    activeTab === tab.id
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <span className="mr-2">{tab.icon}</span>
                  {tab.name}
                </button>
              ))}
            </nav>
          </div>
        </div>

        {/* Dashboard Overview */}
        {activeTab === 'dashboard' && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-blue-100 rounded-lg">
                  <span className="text-2xl">ðŸšš</span>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Today's Deliveries</p>
                  <p className="text-2xl font-bold text-gray-900">{deliveries.length}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-green-100 rounded-lg">
                  <span className="text-2xl">âœ…</span>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Completed</p>
                  <p className="text-2xl font-bold text-gray-900">{deliveries.filter(d => d.status === 'Completed').length}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-yellow-100 rounded-lg">
                  <span className="text-2xl">ðŸš¨</span>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Emergency Calls</p>
                  <p className="text-2xl font-bold text-gray-900">{emergencyNotifications.length}</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-purple-100 rounded-lg">
                  <span className="text-2xl">â­</span>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Rating</p>
                  <p className="text-2xl font-bold text-gray-900">{profile.rating}/5</p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex items-center">
                <div className="p-2 bg-green-100 rounded-lg">
                  <span className="text-2xl">ðŸ’°</span>
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-600">Monthly Salary</p>
                  <p className="text-2xl font-bold text-gray-900">{profile.salary}</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Deliveries Management */}
        {activeTab === 'deliveries' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Delivery Management</h2>

            <div className="space-y-6">
              {deliveries.map((delivery) => (
                <div key={delivery.id} className="border border-gray-200 rounded-lg p-6">
                  <div className="flex items-center justify-between mb-4">
                    <div>
                      <h3 className="text-lg font-medium text-gray-900">Order #{delivery.orderId}</h3>
                      <p className="text-sm text-gray-600">{delivery.customerName}</p>
                    </div>
                    <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                      delivery.status === 'Completed' ? 'bg-green-100 text-green-800' :
                      delivery.status === 'In Progress' ? 'bg-blue-100 text-blue-800' :
                      'bg-yellow-100 text-yellow-800'
                    }`}>
                      {delivery.status}
                    </span>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div>
                      <p className="text-sm font-medium text-gray-700">Delivery Address:</p>
                      <p className="text-sm text-gray-600">{delivery.address}</p>
                    </div>
                    <div>
                      <p className="text-sm font-medium text-gray-700">Items:</p>
                      <p className="text-sm text-gray-600">{delivery.items.join(', ')}</p>
                    </div>
                  </div>

                  <div className="mb-4">
                    <p className="text-sm font-medium text-gray-700">Route:</p>
                    <p className="text-sm text-gray-600">{delivery.route}</p>
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="text-sm text-gray-600">
                      <p>Estimated Delivery: {delivery.estimatedDelivery}</p>
                      {delivery.startTime && <p>Started: {delivery.startTime}</p>}
                      {delivery.completedTime && <p>Completed: {delivery.completedTime}</p>}
                    </div>

                    <div className="flex space-x-2">
                      {delivery.status === 'Pending' && (
                        <button
                          onClick={() => handleStartDelivery(delivery.id)}
                          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors duration-300"
                        >
                          Start Delivery
                        </button>
                      )}
                      {delivery.status === 'In Progress' && (
                        <button
                          onClick={() => handleCompleteDelivery(delivery.id)}
                          className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors duration-300"
                        >
                          Complete Delivery
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Emergency Notifications */}
        {activeTab === 'emergency' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Emergency Notifications</h2>

            <div className="space-y-4">
              {emergencyNotifications.map((notification) => (
                <div key={notification.id} className="border border-red-200 bg-red-50 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <h3 className="text-lg font-medium text-red-900">{notification.location}</h3>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      notification.priority === 'High' ? 'bg-red-100 text-red-800' : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {notification.priority} Priority
                    </span>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-3">
                    <div>
                      <p className="text-sm font-medium text-red-700">Water Level:</p>
                      <p className="text-sm text-red-600">{notification.waterLevel}</p>
                    </div>
                    <div>
                      <p className="text-sm font-medium text-red-700">Status:</p>
                      <p className="text-sm text-red-600">{notification.status}</p>
                    </div>
                    <div>
                      <p className="text-sm font-medium text-red-700">ETA:</p>
                      <p className="text-sm text-red-600">{notification.estimatedArrival}</p>
                    </div>
                  </div>

                  <div className="text-sm text-red-600">
                    <p>Assigned: {notification.assignedTime}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Driver Profile */}
        {activeTab === 'profile' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Driver Profile</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Full Name</label>
                    <input
                      type="text"
                      value={profile.name}
                      onChange={(e) => setProfile(prev => ({ ...prev, name: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                    <input
                      type="email"
                      value={profile.email}
                      onChange={(e) => setProfile(prev => ({ ...prev, email: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Phone</label>
                    <input
                      type="tel"
                      value={profile.phone}
                      onChange={(e) => setProfile(prev => ({ ...prev, phone: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Vehicle Number</label>
                    <input
                      type="text"
                      value={profile.vehicleNumber}
                      onChange={(e) => setProfile(prev => ({ ...prev, vehicleNumber: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Branch</label>
                    <input
                      type="text"
                      value={profile.branch}
                      disabled
                      className="w-full px-3 py-2 border border-gray-300 rounded-md bg-gray-50 text-gray-600"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Salary</label>
                    <input
                      type="text"
                      value={profile.salary}
                      disabled
                      className="w-full px-3 py-2 border border-gray-300 rounded-md bg-gray-50 text-gray-600"
                    />
                  </div>
                </div>

                <div className="mt-6">
                  <button className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors duration-300">
                    Update Profile
                  </button>
                </div>
              </div>

              <div>
                <div className="bg-gray-50 rounded-lg p-6">
                  <h3 className="text-lg font-medium text-gray-900 mb-4">Statistics</h3>

                  <div className="space-y-4">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Experience:</span>
                      <span className="font-medium">{profile.experience}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Rating:</span>
                      <span className="font-medium">{profile.rating}/5 â­</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Total Deliveries:</span>
                      <span className="font-medium">{profile.totalDeliveries}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Salary:</span>
                      <span className="font-medium text-green-600">{profile.salary}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Branch:</span>
                      <span className="font-medium">{profile.branch}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-600">Current Status:</span>
                      <span className={`font-medium ${
                        profile.availability === 'Available' ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {profile.availability}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Delivery Logs */}
        {activeTab === 'logs' && (
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-gray-900">Delivery Logs</h2>
              <div className="flex space-x-2">
                <button
                  onClick={() => downloadDeliveryLogs('daily')}
                  className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors duration-300"
                >
                  Download Daily
                </button>
                <button
                  onClick={() => downloadDeliveryLogs('monthly')}
                  className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors duration-300"
                >
                  Download Monthly
                </button>
                <button
                  onClick={() => downloadDeliveryLogs('yearly')}
                  className="bg-purple-500 text-white px-4 py-2 rounded-lg hover:bg-purple-600 transition-colors duration-300"
                >
                  Download Yearly
                </button>
              </div>
            </div>

            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Total Deliveries</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Completed</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Cancelled</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Distance</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Earnings</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {deliveryLogs.map((log) => (
                    <tr key={log.id}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{log.date}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{log.deliveries}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{log.completed}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{log.cancelled}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{log.totalDistance}</td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">Rs. {log.totalEarnings}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DriverDashboard;
