import React, { useState, useEffect } from 'react';
import { emergencyRequestAPI } from '../../utils/apiService';

const EmergencyDisplayTest = () => {
  const [emergencyRequests, setEmergencyRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmergencyRequests = async () => {
      try {
        setLoading(true);
        setError(null);
        
        console.log('🔍 EmergencyDisplayTest: Fetching emergency requests...');
        const response = await emergencyRequestAPI.getAllRequests();
        console.log('🔍 EmergencyDisplayTest: Response received:', response);
        
        if (response && response.data) {
          console.log('🔍 EmergencyDisplayTest: Setting requests:', response.data.length);
          setEmergencyRequests(response.data);
        } else {
          console.log('🔍 EmergencyDisplayTest: No data in response:', response);
          setEmergencyRequests([]);
        }
      } catch (err) {
        console.error('🔍 EmergencyDisplayTest: Error fetching requests:', err);
        setError(err.message);
        setEmergencyRequests([]);
      } finally {
        setLoading(false);
      }
    };

    fetchEmergencyRequests();
  }, []);

  const createTestRequest = async () => {
    try {
      console.log('🔍 EmergencyDisplayTest: Creating test request...');
      const testRequest = {
        brigadeId: 'TEST_BRIGADE_' + Date.now(),
        brigadeName: 'Test Fire Brigade',
        brigadeLocation: 'Test Location',
        requestType: 'Emergency Water Supply',
        priority: 'High',
        waterLevel: '25%',
        description: 'Test emergency request from display test component',
        coordinates: {
          lat: 6.9271,
          lng: 79.8612
        }
      };

      const response = await fetch('http://localhost:5000/emergency-requests/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(testRequest)
      });

      const result = await response.json();
      console.log('🔍 EmergencyDisplayTest: Create result:', result);

      if (result.success) {
        alert('Test request created successfully! Refreshing...');
        // Refresh the list
        const refreshResponse = await emergencyRequestAPI.getAllRequests();
        if (refreshResponse && refreshResponse.data) {
          setEmergencyRequests(refreshResponse.data);
        }
      } else {
        alert('Failed to create test request: ' + result.message);
      }
    } catch (err) {
      console.error('🔍 EmergencyDisplayTest: Error creating test request:', err);
      alert('Error creating test request: ' + err.message);
    }
  };

  const refreshRequests = async () => {
    try {
      setLoading(true);
      console.log('🔍 EmergencyDisplayTest: Refreshing requests...');
      const response = await emergencyRequestAPI.getAllRequests();
      console.log('🔍 EmergencyDisplayTest: Refresh response:', response);
      
      if (response && response.data) {
        setEmergencyRequests(response.data);
        alert(`Refreshed! Found ${response.data.length} requests.`);
      } else {
        setEmergencyRequests([]);
        alert('No requests found.');
      }
    } catch (err) {
      console.error('🔍 EmergencyDisplayTest: Error refreshing:', err);
      alert('Error refreshing: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Emergency Request Display Test</h1>
        
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Test Controls</h2>
          <div className="flex space-x-4">
            <button
              onClick={createTestRequest}
              className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
            >
              🚨 Create Test Request
            </button>
            <button
              onClick={refreshRequests}
              className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
            >
              🔄 Refresh Requests
            </button>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-sm p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Emergency Requests</h2>
          
          {loading && (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
              <p className="mt-4 text-gray-600">Loading emergency requests...</p>
            </div>
          )}

          {error && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-4">
              <p className="text-red-800">Error: {error}</p>
            </div>
          )}

          {!loading && !error && (
            <div>
              <p className="text-gray-600 mb-4">Found {emergencyRequests.length} emergency requests</p>
              
              {emergencyRequests.length === 0 ? (
                <div className="text-center py-8">
                  <p className="text-gray-500">No emergency requests found.</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {emergencyRequests.map((request) => (
                    <div key={request._id || request.id} className="border rounded-lg p-4">
                      <div className="flex justify-between items-start">
                        <div>
                          <h3 className="font-semibold text-gray-900">{request.brigadeName}</h3>
                          <p className="text-sm text-gray-600">{request.brigadeLocation}</p>
                          <p className="text-sm text-gray-500">{request.requestType}</p>
                          <p className="text-sm text-gray-500">{request.description}</p>
                        </div>
                        <div className="flex space-x-2">
                          <span className={`px-2 py-1 text-xs rounded-full ${
                            request.priority === 'High' ? 'bg-red-100 text-red-800' :
                            request.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                            'bg-green-100 text-green-800'
                          }`}>
                            {request.priority}
                          </span>
                          <span className={`px-2 py-1 text-xs rounded-full ${
                            request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' :
                            request.status === 'Approved' ? 'bg-green-100 text-green-800' :
                            'bg-red-100 text-red-800'
                          }`}>
                            {request.status}
                          </span>
                        </div>
                      </div>
                      <div className="mt-2 text-xs text-gray-500">
                        ID: {request._id || request.id} | Created: {request.requestDate}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>

        <div className="bg-white rounded-lg shadow-sm p-6 mt-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Debug Information</h2>
          <div className="bg-gray-50 rounded-lg p-4">
            <p className="text-sm text-gray-600">
              <strong>Current State:</strong> {loading ? 'Loading' : 'Loaded'}
            </p>
            <p className="text-sm text-gray-600">
              <strong>Request Count:</strong> {emergencyRequests.length}
            </p>
            <p className="text-sm text-gray-600">
              <strong>Error:</strong> {error || 'None'}
            </p>
            <p className="text-sm text-gray-600">
              <strong>Raw Data:</strong> {JSON.stringify(emergencyRequests, null, 2)}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmergencyDisplayTest;
