import React from 'react';
import './App.css';
import { Route, Routes, Navigate } from 'react-router-dom';
import { AuthProvider } from './Context/AuthContext';
import ProtectedRoute from './Components/Auth/ProtectedRoute';
import Login from './Components/Auth/Login';
import Register from './Components/Auth/Register';

// Import components
import Home from './Components/Home/Home';
import Inventory from './Components/Inventory/Inventory';
import Orders from './Components/Orders/Orders';
import Reports from './Components/Reports/Reports';
import Settings from './Components/Settings/Settings';
import Recycling from './Components/Recycling/Recycling';
import BranchManager from './Components/BranchManager/BranchManager';

function App() {
  return (
    <AuthProvider>
      <div>
        <React.Fragment>
          <Routes>
            {/* Public routes */}
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />

            {/* Protected Factory Manager routes */}
            <Route path="/" element={
              <ProtectedRoute requiredRole="Factory Manager">
                <Home />
              </ProtectedRoute>
            } />
            <Route path="/mainhome" element={
              <ProtectedRoute requiredRole="Factory Manager">
                <Home />
              </ProtectedRoute>
            } />
            <Route path="/inventory" element={
              <ProtectedRoute requiredRole="Factory Manager">
                <Inventory />
              </ProtectedRoute>
            } />
            <Route path="/orders" element={
              <ProtectedRoute requiredRole="Factory Manager">
                <Orders />
              </ProtectedRoute>
            } />
            <Route path="/recycling" element={
              <ProtectedRoute requiredRole="Factory Manager">
                <Recycling />
              </ProtectedRoute>
            } />
            <Route path="/reports" element={
              <ProtectedRoute requiredRole="Factory Manager">
                <Reports />
              </ProtectedRoute>
            } />
            <Route path="/settings" element={
              <ProtectedRoute requiredRole="Factory Manager">
                <Settings />
              </ProtectedRoute>
            } />

            {/* Protected Branch Manager routes */}
            <Route path="/branch-manager/*" element={
              <ProtectedRoute requiredRole="Branch Manager">
                <BranchManager />
              </ProtectedRoute>
            } />

            {/* Default redirect */}
            <Route path="*" element={<Navigate to="/login" replace />} />
          </Routes>
        </React.Fragment>
      </div>
    </AuthProvider>
  );
}

export default App;
