import React from 'react';
import Nav from '../Nav/Nav';
import Footer from '../Footer/Footer';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function AddUser() {
    const history = useNavigate();
    const [inputs, setInputs] = React.useState({
        name: '',
        age: '',
        address: ''
    });
    const [loading, setLoading] = React.useState(false);

    const handleChange = (e) => {
        setInputs((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            await sendRequest();
            history('/userDetails');
        } catch (error) {
            console.error('Error adding user:', error);
            alert('Error adding user. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const sendRequest = async () => {
        const response = await axios.post('http://localhost:5000/Users', {
            name: String(inputs.name),
            age: Number(inputs.age),
            address: String(inputs.address)
        });
        return response.data;
    };

    return (
        <div className="min-h-screen bg-gray-50">
            <Nav />
            
            <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                <div className="mb-8">
                    <h1 className="text-3xl font-bold text-gray-900">Add New User</h1>
                    <p className="text-gray-600 mt-2">Create a new user account</p>
                </div>

                <div className="bg-white rounded-lg shadow-md p-6">
                    <form onSubmit={handleSubmit} className="space-y-6">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Full Name
                            </label>
                            <input 
                                type="text" 
                                name='name' 
                                value={inputs.name} 
                                onChange={handleChange}
                                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                                placeholder="Enter full name"
                                required
                            />
                        </div>
                        
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Age
                            </label>
                            <input 
                                type="number" 
                                name='age' 
                                value={inputs.age} 
                                onChange={handleChange}
                                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                                placeholder="Enter age"
                                min="1"
                                max="120"
                                required
                            />
                        </div>
                        
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Address
                            </label>
                            <textarea 
                                name='address' 
                                value={inputs.address} 
                                onChange={handleChange}
                                rows="3"
                                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                                placeholder="Enter address"
                                required
                            />
                        </div>
                        
                        <div className="flex space-x-4">
                            <button 
                                type="button"
                                onClick={() => history('/userDetails')}
                                className="flex-1 px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50 transition-colors"
                            >
                                Cancel
                            </button>
                            <button 
                                type="submit"
                                disabled={loading}
                                className="flex-1 bg-primary-600 text-white px-4 py-2 rounded-md hover:bg-primary-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                {loading ? 'Adding...' : 'Add User'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>

            <Footer />
        </div>
    );
}

export default AddUser;
