import React, { useEffect, useState } from 'react';
import InteractiveMap from './InteractiveMap';

const EmergencyRouteMap = ({ emergencyRequest, branchLocation, onClose }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [distance, setDistance] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(null);
  const [showRoute, setShowRoute] = useState(false);
  const [generatedEmergencyLocation, setGeneratedEmergencyLocation] = useState(null);

  // Default coordinates for Colombo 7, Sri Lanka
  const defaultCenter = [6.8700, 79.8700]; // Colombo 7 (Dehiwala) coordinates
  
  // Parse branch coordinates
  const branchCoords = branchLocation ? 
    [branchLocation.lat || 6.8700, branchLocation.lng || 79.8700] : 
    defaultCenter;

  // Geocode address to get actual coordinates (prioritizing local accurate coordinates)
  const geocodeAddress = React.useCallback(async (address) => {
    if (!address) return defaultCenter;
    
    // Check if we already have coordinates for this address
    const addressKey = address.toLowerCase().trim();
    if (geocodeCache[addressKey]) {
      console.log('ðŸŽ¯ Using cached geocoding result for:', address);
      return geocodeCache[addressKey];
    }
    
    setIsGeocoding(true);
    try {
      console.log('ðŸŒ Processing address:', address);
      
      // FIRST: Try to get accurate local coordinates
      const localCoords = getFallbackCoordinates(address);
      if (localCoords) {
        console.log('âœ… Using accurate local coordinates for:', address);
        console.log('ðŸ“ Local coordinates:', localCoords);
        geocodeCache[addressKey] = localCoords;
        return localCoords;
      }
      
      // SECOND: If no local match, try online geocoding as backup
      console.log('ðŸŒ No local match found, trying online geocoding...');
      
      const strategies = [
        `${address}, Sri Lanka`,
        `${address}, Colombo, Sri Lanka`,
        address,
        address.split(',')[0] + ', Colombo, Sri Lanka'
      ];
      
      for (const searchQuery of strategies) {
        try {
          console.log(`ðŸ” Trying online geocoding: "${searchQuery}"`);
          
          const response = await fetch(
            `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchQuery)}&limit=5&countrycodes=lk&addressdetails=1`
          );
          
          const data = await response.json();
          
          if (data && data.length > 0) {
            let bestResult = data[0];
            
            for (const result of data) {
              if (result.address && 
                  parseFloat(result.lat) > 5.5 && parseFloat(result.lat) < 10.0 &&
                  parseFloat(result.lon) > 79.0 && parseFloat(result.lon) < 82.0) {
                bestResult = result;
                console.log('ðŸŽ¯ Found online Sri Lankan location:', result.display_name);
                break;
              }
            }
            
            const coords = [parseFloat(bestResult.lat), parseFloat(bestResult.lon)];
            
            if (coords[0] > 5.5 && coords[0] < 10.0 && coords[1] > 79.0 && coords[1] < 82.0) {
              console.log('âœ… Online geocoded coordinates:', coords);
              geocodeCache[addressKey] = coords;
              return coords;
            }
          }
        } catch (strategyError) {
          console.log(`âš ï¸ Online strategy failed: ${strategyError.message}`);
          continue;
        }
      }
      
      // FINAL FALLBACK: Use default coordinates
      console.log('âš ï¸ All geocoding failed, using default coordinates');
      geocodeCache[addressKey] = defaultCenter;
      return defaultCenter;
      
    } catch (error) {
      console.error('âŒ Geocoding failed:', error);
      geocodeCache[addressKey] = defaultCenter;
      return defaultCenter;
    } finally {
      setIsGeocoding(false);
    }
  }, []);

  // Fallback coordinates for common Colombo areas with more specific locations
  const getFallbackCoordinates = (address) => {
    const addressLower = address.toLowerCase();
    console.log('ðŸ” Checking address for local coordinates:', address);
    console.log('ðŸ” Address in lowercase:', addressLower);
    
    // Check for specific known address patterns first
    if (addressLower.includes('maharagama') && addressLower.includes('high level road')) {
      console.log('ðŸŽ¯ Detected Maharagama High Level Road address');
      console.log('ðŸ“ Returning Maharagama coordinates: [6.8481, 79.9285]');
      return [6.8481, 79.9285]; // Accurate Maharagama High Level Road coordinates
    }
    
    if (addressLower.includes('borella') && addressLower.includes('baseline road')) {
      console.log('ðŸŽ¯ Detected Borella Baseline Road address');
      console.log('ðŸ“ Returning Borella coordinates: [6.9047, 79.8844]');
      return [6.9047, 79.8844]; // Accurate Borella Baseline Road coordinates
    }
    
    // More specific Colombo area coordinates
    const areaCoordinates = {
      // Borella area - more specific
      'borella': [6.9047, 79.8844],
      'baseline road': [6.9047, 79.8844],
      'baseline': [6.9047, 79.8844],
      'borella baseline': [6.9047, 79.8844],
      
      // Colombo postal codes with specific areas
      'colombo 01': [6.9300, 79.8400], // Fort
      'colombo 02': [6.9200, 79.8500], // Slave Island
      'colombo 03': [6.9100, 79.8500], // Kollupitiya
      'colombo 04': [6.9000, 79.8600], // Bambalapitiya
      'colombo 05': [6.8900, 79.8600], // Wellawatta
      'colombo 06': [6.8800, 79.8700], // Mount Lavinia
      'colombo 07': [6.8700, 79.8700], // Dehiwala
      'colombo 08': [6.9047, 79.8844], // Borella
      'colombo 09': [6.8900, 79.8800], // Dematagoda
      'colombo 10': [6.9400, 79.8500], // Maradana
      'colombo 11': [6.9400, 79.8500], // Pettah
      'colombo 12': [6.9500, 79.8400], // Kotahena
      'colombo 13': [6.9600, 79.8300], // Modara
      'colombo 14': [6.9700, 79.8200], // Grandpass
      'colombo 15': [6.9800, 79.8100], // Mattakkuliya
      
      // Specific areas with more accurate coordinates
      'kollupitiya': [6.9100, 79.8500],
      'bambalapitiya': [6.8900, 79.8600],
      'pettah': [6.9400, 79.8500],
      'fort': [6.9300, 79.8400],
      'slave island': [6.9200, 79.8500],
      'union place': [6.9200, 79.8500],
      'liberty plaza': [6.9100, 79.8500],
      'galle road': [6.9100, 79.8500],
      'maradana': [6.9400, 79.8500],
      'dematagoda': [6.8900, 79.8800],
      'wellawatta': [6.8900, 79.8600],
      'mount lavinia': [6.8800, 79.8700],
      'dehiwala': [6.8700, 79.8700],
      
      // Southern Colombo areas with accurate coordinates
      'maharagama': [6.8481, 79.9285], // More accurate Maharagama coordinates
      'high level road': [6.8481, 79.9285], // High Level Road in Maharagama
      'maharagama high level': [6.8481, 79.9285],
      'nugegoda': [6.8631, 79.8996],
      'kottawa': [6.8400, 79.9500],
      'homagama': [6.8400, 80.0000],
      'piliyandala': [6.8500, 79.9000],
      'kaduwela': [6.9300, 79.9800],
      'avissawella': [6.9500, 80.2000],
      'rathmalana': [6.8200, 79.8800],
      'moratuwa': [6.8000, 79.9000],
      'panadura': [6.7200, 79.9000],
      'kalutara': [6.5800, 79.9600]
    };
    
    // Find matching area - check for multiple keywords with priority
    const priorityAreas = ['maharagama', 'high level road', 'nugegoda', 'kottawa', 'homagama'];
    
    // First, check priority areas (more specific locations)
    for (const priorityArea of priorityAreas) {
      if (addressLower.includes(priorityArea) && areaCoordinates[priorityArea]) {
        console.log(`ðŸŽ¯ Found priority area: "${priorityArea}" for address: "${address}"`);
        console.log(`ðŸ“ Using coordinates: ${areaCoordinates[priorityArea][0]}, ${areaCoordinates[priorityArea][1]}`);
        console.log(`ðŸ“ Returning priority coordinates: [${areaCoordinates[priorityArea][0]}, ${areaCoordinates[priorityArea][1]}]`);
        return areaCoordinates[priorityArea];
      }
    }
    
    // Then check all other areas
    for (const [area, coords] of Object.entries(areaCoordinates)) {
      if (addressLower.includes(area)) {
        console.log(`ðŸŽ¯ Found matching area: "${area}" for address: "${address}"`);
        console.log(`ðŸ“ Using coordinates: ${coords[0]}, ${coords[1]}`);
        return coords;
      }
    }
    
    // If no specific match, try to extract Colombo postal code
    const colomboMatch = addressLower.match(/colombo\s*(\d+)/);
    if (colomboMatch) {
      const postalCode = `colombo ${colomboMatch[1]}`;
      if (areaCoordinates[postalCode]) {
        console.log(`ðŸŽ¯ Found Colombo postal code: ${postalCode}`);
        return areaCoordinates[postalCode];
      }
    }
    
    // Default to Colombo center if no area match
    console.log('ðŸŽ¯ No specific area found, using default Colombo center coordinates');
    return [6.9271, 79.8612];
  };

  // Calculate distance between two points using Haversine formula
  const calculateDistance = (lat1, lng1, lat2, lng2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLng/2) * Math.sin(dLng/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  // Calculate estimated travel time (assuming average speed of 40 km/h in city traffic)
  const calculateEstimatedTime = (distanceKm) => {
    const averageSpeed = 40; // km/h
    const timeInHours = distanceKm / averageSpeed;
    const hours = Math.floor(timeInHours);
    const minutes = Math.round((timeInHours - hours) * 60);
    
    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    } else {
      return `${minutes}m`;
    }
  };

  // Calculate road distance along the route
  const calculateRoadDistance = (route) => {
    let totalDistance = 0;
    for (let i = 0; i < route.length - 1; i++) {
      const segmentDistance = calculateDistance(
        route[i][0], route[i][1],
        route[i + 1][0], route[i + 1][1]
      );
      totalDistance += segmentDistance;
    }
    return totalDistance;
  };

  // Calculate route - simplified to prevent unresponsiveness
  const calculateRoute = React.useCallback(async () => {
    // Prevent multiple simultaneous calculations
    if (loading) {
      console.log('âš ï¸ Route calculation already in progress, skipping...');
      return;
    }
    
    setLoading(true);
    setError(null);
    
    try {
      console.log('ðŸš€ Starting route calculation...');
      console.log('ðŸ“ Branch coords:', branchCoords);
      console.log('ðŸ“ Emergency coords:', emergencyCoords);
      
      // Calculate distance and time
      const routeDistance = calculateDistance(
        branchCoords[0], branchCoords[1],
        emergencyCoords[0], emergencyCoords[1]
      );
      const calculatedTime = calculateEstimatedTime(routeDistance);
      
      setDistance(routeDistance);
      setEstimatedTime(calculatedTime);
      
      console.log(`ðŸ“ Route calculated successfully:`);
      console.log(`ðŸ“ Distance: ${routeDistance.toFixed(2)} km`);
      console.log(`â±ï¸ Estimated time: ${calculatedTime}`);
      
    } catch (err) {
      console.error('Error calculating route:', err);
      setError('Failed to calculate route.');
      const fallbackDistance = calculateDistance(
        branchCoords[0], branchCoords[1],
        emergencyCoords[0], emergencyCoords[1]
      );
      setDistance(fallbackDistance);
      setEstimatedTime(calculateEstimatedTime(fallbackDistance));
    } finally {
      setLoading(false);
    }
  }, [branchCoords, emergencyCoords, loading]);

  // Initialize coordinates when component mounts or emergency request changes
  useEffect(() => {
    const initializeCoordinates = async () => {
      if (emergencyRequest?.coordinates) {
        // If coordinates are provided, use them
        const coords = [emergencyRequest.coordinates.lat, emergencyRequest.coordinates.lng];
        console.log('ðŸ“ Using provided coordinates:', coords);
        setEmergencyCoords(coords);
      } else if (emergencyRequest?.brigadeLocation) {
        // If only address is provided, geocode it
        console.log('ðŸŒ Processing address for geocoding:', emergencyRequest.brigadeLocation);
        console.log('ðŸ” Address type:', typeof emergencyRequest.brigadeLocation);
        console.log('ðŸ“ Full emergency request:', emergencyRequest);
        
        const coords = await geocodeAddress(emergencyRequest.brigadeLocation);
        console.log('ðŸ“ Final geocoded coordinates:', coords);
        setEmergencyCoords(coords);
      } else {
        console.log('ðŸ“ No address provided, using default coordinates');
        setEmergencyCoords(defaultCenter);
      }
    };

    initializeCoordinates();
  }, [emergencyRequest?.coordinates, emergencyRequest?.brigadeLocation]);

  // Automatically calculate route when coordinates are updated
  useEffect(() => {
    if (emergencyCoords && branchCoords && emergencyRequest && !loading) {
      console.log('ðŸ”„ Auto-calculating route after coordinate update');
      calculateRoute();
    }
  }, [emergencyCoords, branchCoords, calculateRoute]);











  // Manual route calculation trigger


  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-11/12 max-w-4xl shadow-lg rounded-md bg-white">
        <div className="mt-3">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-medium text-gray-900">
              Emergency Route Information
            </h3>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="mb-4 p-4 bg-blue-50 border border-blue-200 rounded-md">
            <h4 className="font-medium text-blue-900 mb-2">Emergency Details:</h4>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p><strong>Location:</strong> {emergencyRequest?.brigadeLocation}</p>
                <p><strong>Brigade:</strong> {emergencyRequest?.brigadeName}</p>
                <p><strong>Water Level:</strong> {emergencyRequest?.waterLevel}</p>
                <p><strong>Priority:</strong> {emergencyRequest?.priority}</p>
              </div>
              <div>
                <p><strong>Branch:</strong> {branchLocation?.name || 'Colombo Central Branch'}</p>
                <p><strong>Branch Address:</strong> {branchLocation?.address || '123 Galle Road, Colombo 07'}</p>
                {distance && <p><strong>Distance:</strong> {distance.toFixed(2)} km</p>}
                {estimatedTime && <p><strong>Est. Time:</strong> {estimatedTime}</p>}
              </div>
            </div>
          </div>

          {loading && (
            <div className="text-center py-4">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
              <p className="mt-2 text-gray-600">Calculating route...</p>
            </div>
          )}

          {error && (
            <div className="mb-4 p-4 bg-yellow-50 border border-yellow-200 rounded-md">
              <p className="text-yellow-800">{error}</p>
            </div>
          )}

          {/* Route Information */}
          <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-md">
            <h4 className="font-medium text-green-700 mb-3">ðŸ›£ï¸ Emergency Water Delivery Route</h4>
            
            {/* Destination Address - Most Prominent */}
            <div className="mb-3 p-3 bg-white border border-green-300 rounded-md">
              <p className="text-sm font-medium text-green-800 mb-1">ðŸ“ DESTINATION ADDRESS:</p>
              <p className="text-base font-bold text-gray-900">{emergencyRequest?.brigadeLocation || 'Address not provided'}</p>
              {isGeocoding && (
                <div className="mt-2 flex items-center text-blue-600">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2"></div>
                  <span className="text-sm">Automatically finding exact location...</span>
                </div>
              )}
              {!isGeocoding && emergencyCoords && (
                <div className="mt-2 text-xs text-gray-600">
                  <p>ðŸ“ Coordinates: {emergencyCoords[0].toFixed(6)}, {emergencyCoords[1].toFixed(6)}</p>
                  <p>ðŸŽ¯ Location verified and ready for navigation</p>
                </div>
              )}
            </div>
            
            <div className="text-sm space-y-1">
              <p><strong>ðŸ¢ Brigade:</strong> {emergencyRequest?.brigadeName}</p>
              <p><strong>ðŸšš Starting Point:</strong> Colombo Central Branch</p>
              <p><strong>ðŸŒ Map Location:</strong> {emergencyCoords[0].toFixed(6)}, {emergencyCoords[1].toFixed(6)}</p>
              {isGeocoding && (
                <div className="mt-2 p-2 bg-blue-50 border border-blue-200 rounded-md">
                  <p className="text-sm text-blue-700">ðŸ” Auto-geocoding in progress...</p>
                  <p className="text-xs text-blue-600">Finding exact location for: {emergencyRequest?.brigadeLocation}</p>
                </div>
              )}
              {distance && (
                <div className="mt-2 pt-2 border-t border-green-200">
                  <p><strong>ðŸ“ Road Distance:</strong> {distance.toFixed(2)} km</p>
                  <p><strong>â±ï¸ Estimated Time:</strong> {estimatedTime || 'Auto-calculating...'}</p>
                </div>
              )}
            </div>
          </div>

          <div className="mt-4 flex justify-between items-center">
            <div className="text-sm text-gray-600">
              {distance && <p>ðŸ“ Road distance: {distance.toFixed(2)} km</p>}
              {estimatedTime && <p>â±ï¸ Estimated time: {estimatedTime}</p>}
            </div>
            <div className="flex space-x-3">
              <button
                onClick={calculateRoute}
                disabled={loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Calculating...' : 'ðŸ”„ Recalculate Route'}
              </button>
              <button
                onClick={onClose}
                className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmergencyRouteMap;
