import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BranchRecycleBin({ branchId, branchName }) {
  const [bins, setBins] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [selectedBin, setSelectedBin] = useState(null);
  const [formErrors, setFormErrors] = useState({});
  const [formData, setFormData] = useState({
    branchId: branchId,
    branchName: branchName,
    location: '',
    capacity: '',
    currentLevel: '',
    notes: ''
  });

  useEffect(() => {
    fetchBins();
  }, []);

  const fetchBins = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5000/RecyclingBins/branch/${branchId}`);
      setBins(response.data.bins || []);
    } catch (error) {
      console.error('Error fetching recycling bins:', error);
      setBins([]);
    } finally {
      setLoading(false);
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.location.trim()) {
      errors.location = 'Location is required';
    }

    if (!formData.capacity || formData.capacity <= 0) {
      errors.capacity = 'Capacity must be a positive number';
    }

    if (!formData.currentLevel || formData.currentLevel < 0) {
      errors.currentLevel = 'Current level must be a non-negative number';
    }

    if (parseInt(formData.currentLevel) > parseInt(formData.capacity)) {
      errors.currentLevel = 'Current level cannot exceed capacity';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    try {
      const binData = {
        ...formData,
        capacity: parseInt(formData.capacity),
        currentLevel: parseInt(formData.currentLevel)
      };

      await axios.post('http://localhost:5000/RecyclingBins', binData);
      setShowAddModal(false);
      resetForm();
      fetchBins();
    } catch (error) {
      console.error('Error creating recycling bin:', error);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    try {
      const binData = {
        currentLevel: parseInt(formData.currentLevel),
        notes: formData.notes
      };

      await axios.put(`http://localhost:5000/RecyclingBins/${selectedBin._id}/fill-level`, binData);
      setShowUpdateModal(false);
      setSelectedBin(null);
      resetForm();
      fetchBins();
    } catch (error) {
      console.error('Error updating recycling bin:', error);
    }
  };

  const resetForm = () => {
    setFormData({
      branchId: branchId,
      branchName: branchName,
      location: '',
      capacity: '',
      currentLevel: '',
      notes: ''
    });
    setFormErrors({});
  };

  const openUpdateModal = (bin) => {
    setSelectedBin(bin);
    setFormData({
      branchId: bin.branchId,
      branchName: bin.branchName,
      location: bin.location,
      capacity: bin.capacity.toString(),
      currentLevel: bin.currentLevel.toString(),
      notes: bin.notes || ''
    });
    setShowUpdateModal(true);
  };

  const emptyBin = async (binId) => {
    try {
      await axios.put(`http://localhost:5000/RecyclingBins/${binId}/empty`);
      fetchBins();
    } catch (error) {
      console.error('Error emptying bin:', error);
    }
  };

  const notifyFactory = async (binId) => {
    try {
      await axios.put(`http://localhost:5000/RecyclingBins/${binId}/notify`);
      fetchBins();
    } catch (error) {
      console.error('Error notifying factory:', error);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Empty': return 'bg-green-100 text-green-800';
      case 'Low': return 'bg-blue-100 text-blue-800';
      case 'Medium': return 'bg-yellow-100 text-yellow-800';
      case 'High': return 'bg-orange-100 text-orange-800';
      case 'Critical': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getFillLevelColor = (percentage) => {
    if (percentage >= 80) return 'text-red-600';
    if (percentage >= 60) return 'text-orange-600';
    if (percentage >= 40) return 'text-yellow-600';
    if (percentage >= 20) return 'text-blue-600';
    return 'text-green-600';
  };

  const getProgressBarColor = (percentage) => {
    if (percentage >= 80) return 'bg-red-500';
    if (percentage >= 60) return 'bg-orange-500';
    if (percentage >= 40) return 'bg-yellow-500';
    if (percentage >= 20) return 'bg-blue-500';
    return 'bg-green-500';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Recycling Bins</h2>
        <button
          onClick={() => setShowAddModal(true)}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
        >
          Add New Bin
        </button>
      </div>

      {/* Bins Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {bins.map((bin) => (
          <div key={bin._id} className="bg-white rounded-lg shadow p-6">
            <div className="flex justify-between items-start mb-4">
              <div>
                <h3 className="text-lg font-semibold text-gray-900">{bin.binId}</h3>
                <p className="text-sm text-gray-600">{bin.location}</p>
              </div>
              <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(bin.status)}`}>
                {bin.status}
              </span>
            </div>

            <div className="mb-4">
              <div className="flex justify-between text-sm mb-2">
                <span className="text-gray-600">Fill Level</span>
                <span className={`font-semibold ${getFillLevelColor(bin.fillPercentage)}`}>
                  {bin.fillPercentage.toFixed(1)}%
                </span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className={`h-2 rounded-full ${getProgressBarColor(bin.fillPercentage)}`}
                  style={{ width: `${bin.fillPercentage}%` }}
                ></div>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4 text-sm mb-4">
              <div>
                <p className="text-gray-600">Capacity</p>
                <p className="font-semibold">{bin.capacity}L</p>
              </div>
              <div>
                <p className="text-gray-600">Current Level</p>
                <p className="font-semibold">{bin.currentLevel}L</p>
              </div>
            </div>

            {bin.notes && (
              <div className="mb-4">
                <p className="text-sm text-gray-600">Notes:</p>
                <p className="text-sm text-gray-900">{bin.notes}</p>
              </div>
            )}

            <div className="flex space-x-2">
              <button
                onClick={() => openUpdateModal(bin)}
                className="flex-1 bg-blue-600 text-white px-3 py-2 rounded text-sm hover:bg-blue-700"
              >
                Update Level
              </button>
              <button
                onClick={() => emptyBin(bin._id)}
                className="flex-1 bg-green-600 text-white px-3 py-2 rounded text-sm hover:bg-green-700"
              >
                Empty
              </button>
              {bin.fillPercentage >= 80 && !bin.isNotified && (
                <button
                  onClick={() => notifyFactory(bin._id)}
                  className="flex-1 bg-red-600 text-white px-3 py-2 rounded text-sm hover:bg-red-700"
                >
                  Notify Factory
                </button>
              )}
            </div>

            {bin.isNotified && (
              <div className="mt-2 text-xs text-orange-600">
                Factory has been notified
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Add Bin Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Add New Recycling Bin</h3>
              <form onSubmit={handleSubmit}>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Location</label>
                    <input
                      type="text"
                      value={formData.location}
                      onChange={(e) => setFormData({...formData, location: e.target.value})}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                      placeholder="e.g., Front Entrance, Back Yard"
                    />
                    {formErrors.location && (
                      <p className="text-red-500 text-xs mt-1">{formErrors.location}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Capacity (Liters)</label>
                    <input
                      type="number"
                      value={formData.capacity}
                      onChange={(e) => setFormData({...formData, capacity: e.target.value})}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                      placeholder="100"
                    />
                    {formErrors.capacity && (
                      <p className="text-red-500 text-xs mt-1">{formErrors.capacity}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Current Level (Liters)</label>
                    <input
                      type="number"
                      value={formData.currentLevel}
                      onChange={(e) => setFormData({...formData, currentLevel: e.target.value})}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                      placeholder="0"
                    />
                    {formErrors.currentLevel && (
                      <p className="text-red-500 text-xs mt-1">{formErrors.currentLevel}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Notes</label>
                    <textarea
                      value={formData.notes}
                      onChange={(e) => setFormData({...formData, notes: e.target.value})}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                      rows="3"
                      placeholder="Optional notes about this bin"
                    />
                  </div>
                </div>

                <div className="flex justify-end space-x-3 mt-6">
                  <button
                    type="button"
                    onClick={() => {
                      setShowAddModal(false);
                      resetForm();
                    }}
                    className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                  >
                    Add Bin
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Update Bin Modal */}
      {showUpdateModal && selectedBin && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Update Bin Level</h3>
              <form onSubmit={handleUpdate}>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Bin ID</label>
                    <input
                      type="text"
                      value={selectedBin.binId}
                      disabled
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 bg-gray-100"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Location</label>
                    <input
                      type="text"
                      value={formData.location}
                      disabled
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 bg-gray-100"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Capacity</label>
                    <input
                      type="text"
                      value={`${formData.capacity}L`}
                      disabled
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 bg-gray-100"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Current Level (Liters)</label>
                    <input
                      type="number"
                      value={formData.currentLevel}
                      onChange={(e) => setFormData({...formData, currentLevel: e.target.value})}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                    />
                    {formErrors.currentLevel && (
                      <p className="text-red-500 text-xs mt-1">{formErrors.currentLevel}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Notes</label>
                    <textarea
                      value={formData.notes}
                      onChange={(e) => setFormData({...formData, notes: e.target.value})}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                      rows="3"
                      placeholder="Optional notes about this bin"
                    />
                  </div>
                </div>

                <div className="flex justify-end space-x-3 mt-6">
                  <button
                    type="button"
                    onClick={() => {
                      setShowUpdateModal(false);
                      setSelectedBin(null);
                      resetForm();
                    }}
                    className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                  >
                    Update Level
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default BranchRecycleBin;
