import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  PieChart, Pie, Cell, LineChart, Line
} from 'recharts';
import axios from 'axios';

const FactoryDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  
  // Check if user has factory manager role
  useEffect(() => {
    if (user && user.role !== 'Factory Manager') {
      console.error('User does not have factory manager privileges:', user.role);
      alert('Access denied. Factory Manager privileges required.');
      window.location.href = '/login';
    }
  }, [user]);
  
  const [inventoryStats, setInventoryStats] = useState(null);
  const [inventoryData, setInventoryData] = useState([]);
  const [pendingOrders, setPendingOrders] = useState([]);
  const [orderStats, setOrderStats] = useState(null);
  const [recentActivities, setRecentActivities] = useState([]);
  const [deliveryStats, setDeliveryStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [recyclingRequests, setRecyclingRequests] = useState([]);
  const [recyclingBins, setRecyclingBins] = useState([]);
  const [recyclingStats, setRecyclingStats] = useState(null);

  // Fetch data from API
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        console.log('🔄 Fetching factory dashboard data...');
        
        // Fetch all data in parallel
        const [statsResponse, inventoryResponse, ordersResponse, orderStatsResponse, activitiesResponse, deliveryResponse, recyclingResponse, binsResponse, recyclingStatsResponse] = await Promise.allSettled([
          axios.get('http://localhost:5000/Inventory/stats/overview'),
          axios.get('http://localhost:5000/Inventory'),
          axios.get('http://localhost:5000/Orders/pending'),
          axios.get('http://localhost:5000/Orders/stats/overview'),
          axios.get('http://localhost:5000/Orders/activities/recent'),
          axios.get('http://localhost:5000/Delivery/stats'),
          axios.get('http://localhost:5000/RecyclingRequests/pending'),
          axios.get('http://localhost:5000/RecyclingBins/factory-notification'),
          axios.get('http://localhost:5000/RecyclingRequests/statistics')
        ]);

        // Set inventory stats
        if (statsResponse.status === 'fulfilled') {
          setInventoryStats(statsResponse.value.data.stats);
        } else {
          console.error('Error fetching inventory stats:', statsResponse.reason);
          setInventoryStats({
            totalItems: 8,
            lowStockItems: 2,
            outOfStockItems: 1,
            inStockItems: 5,
            totalQuantity: 450
          });
        }

        // Set inventory data
        if (inventoryResponse.status === 'fulfilled') {
          setInventoryData(inventoryResponse.value.data.inventory || []);
        } else {
          console.error('Error fetching inventory data:', inventoryResponse.reason);
          setInventoryData([]);
        }

        // Set pending orders
        if (ordersResponse.status === 'fulfilled') {
          setPendingOrders(ordersResponse.value.data.orders || []);
        } else {
          console.error('Error fetching pending orders:', ordersResponse.reason);
          setPendingOrders([]);
        }

        // Set order stats
        if (orderStatsResponse.status === 'fulfilled') {
          setOrderStats(orderStatsResponse.value.data.stats);
        } else {
          console.error('Error fetching order stats:', orderStatsResponse.reason);
          setOrderStats({
            totalOrders: 0,
            pendingOrders: 0,
            processingOrders: 0,
            urgentOrders: 0,
            highPriorityOrders: 0
          });
        }

        // Set recent activities
        if (activitiesResponse.status === 'fulfilled') {
          setRecentActivities(activitiesResponse.value.data.activities || []);
        } else {
          console.error('Error fetching recent activities:', activitiesResponse.reason);
          setRecentActivities([]);
        }

        // Set delivery stats
        if (deliveryResponse.status === 'fulfilled') {
          setDeliveryStats(deliveryResponse.value.data.stats);
        } else {
          console.error('Error fetching delivery stats:', deliveryResponse.reason);
          setDeliveryStats({
            factoryDeliveriesToday: 0,
            branchDeliveriesToday: 0,
            pendingShipments: 0,
            pendingDeliveries: 0
          });
        }

        // Set recycling requests
        if (recyclingResponse.status === 'fulfilled') {
          setRecyclingRequests(recyclingResponse.value.data.requests || []);
        } else {
          console.error('Error fetching recycling requests:', recyclingResponse.reason);
          setRecyclingRequests([]);
        }

        // Set recycling bins
        if (binsResponse.status === 'fulfilled') {
          setRecyclingBins(binsResponse.value.data.binsForNotification || []);
        } else {
          console.error('Error fetching recycling bins:', binsResponse.reason);
          setRecyclingBins([]);
        }

        // Set recycling stats
        if (recyclingStatsResponse.status === 'fulfilled') {
          setRecyclingStats(recyclingStatsResponse.value.data.statistics);
        } else {
          console.error('Error fetching recycling stats:', recyclingStatsResponse.reason);
          setRecyclingStats({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            completedRequests: 0,
            rejectedRequests: 0,
            totalWeight: 0,
            totalPoints: 0
          });
        }

      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'In Stock': return 'text-green-600 bg-green-100';
      case 'Low Stock': return 'text-yellow-600 bg-yellow-100';
      case 'Out of Stock': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'Urgent': return 'text-red-600 bg-red-100';
      case 'High': return 'text-orange-600 bg-orange-100';
      case 'Medium': return 'text-yellow-600 bg-yellow-100';
      case 'Low': return 'text-green-600 bg-green-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getActivityColor = (type, status) => {
    if (type === 'inventory') {
      if (status === 'critical') return 'text-red-500';
      if (status === 'warning') return 'text-yellow-500';
    }
    return 'text-blue-500';
  };

  const COLORS = ['#0077B6', '#00B4D8', '#009688', '#FF6B6B', '#4ECDC4', '#FFD93D', '#6BCF7F', '#4D96FF'];

  const tabs = [
    { id: 'overview', name: 'Overview', icon: '📊', description: 'Dashboard overview and key metrics' },
    { id: 'orders', name: 'Orders Management', icon: '📋', description: 'Manage factory orders and production' },
    { id: 'inventory', name: 'Inventory Control', icon: '📦', description: 'Monitor and control inventory levels' },
    { id: 'delivery', name: 'Delivery Tracking', icon: '🚚', description: 'Track deliveries and logistics' },
    { id: 'recycling', name: 'Recycling Center', icon: '🌱', description: 'Manage recycling operations' },
    { id: 'reports', name: 'Analytics & Reports', icon: '📈', description: 'View detailed reports and analytics' },
    { id: 'settings', name: 'Factory Settings', icon: '⚙️', description: 'Configure factory settings and preferences' }
  ];

  const renderOverviewTab = () => (
    <div className="space-y-6">
      {/* Page Title */}
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Overview</h2>
        <p className="text-gray-600">Monitor factory operations and key metrics</p>
      </div>
      
      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-blue-500">
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-blue-100">
              <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 7l-8-4-8 4m16 0l-8 4m8-4v10l-8 4m0-10L4 7m8 4v10M4 7v10l8 4" />
              </svg>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Inventory Items</p>
              <p className="text-2xl font-semibold text-gray-900">
                {loading ? '...' : inventoryStats?.totalItems || 0} Items
              </p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-orange-500">
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-orange-100">
              <svg className="w-6 h-6 text-orange-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Pending Orders</p>
              <p className="text-2xl font-semibold text-gray-900">
                {loading ? '...' : orderStats?.pendingOrders || 0} Orders
              </p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-red-500">
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-red-100">
              <svg className="w-6 h-6 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
              </svg>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Urgent Orders</p>
              <p className="text-2xl font-semibold text-gray-900">
                {loading ? '...' : orderStats?.urgentOrders || 0} Orders
              </p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-green-500">
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-green-100">
              <svg className="w-6 h-6 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Today's Deliveries</p>
              <p className="text-2xl font-semibold text-gray-900">
                {loading ? '...' : deliveryStats?.factoryDeliveriesToday || 0} Shipped
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Inventory Distribution Chart */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-800 mb-4">Inventory Distribution</h3>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie
              data={inventoryData}
              cx="50%"
              cy="50%"
              labelLine={false}
              label={({ name, quantity }) => `${name}: ${quantity}`}
              outerRadius={80}
              fill="#8884d8"
              dataKey="quantity"
            >
              {inventoryData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
          </PieChart>
        </ResponsiveContainer>
      </div>

      {/* Recent Activities */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-800 mb-4">Recent Activities</h3>
        <div className="space-y-4">
          {recentActivities.length > 0 ? (
            recentActivities.map((activity, index) => (
              <div key={index} className="flex items-center space-x-3">
                <div className={`w-2 h-2 rounded-full ${getActivityColor(activity.type, activity.status) === 'text-red-500' ? 'bg-red-500' : 
                  getActivityColor(activity.type, activity.status) === 'text-yellow-500' ? 'bg-yellow-500' : 'bg-blue-500'}`}></div>
                <span className="text-gray-700">{activity.message}</span>
                <span className="text-sm text-gray-500 ml-auto">
                  {new Date(activity.timestamp).toLocaleDateString()}
                </span>
              </div>
            ))
          ) : (
            <div className="text-center py-4">
              <p className="text-gray-500">No recent activities</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );

  const renderOrdersTab = () => (
    <div className="space-y-6">
      {/* Page Title */}
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Orders Management</h2>
        <p className="text-gray-600">Manage and track orders from branches</p>
      </div>
      
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold text-gray-800">Pending Orders from Branches</h3>
        <Link to="/orders" className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors">
          View All Orders
        </Link>
      </div>
      
      {pendingOrders.length > 0 ? (
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Order Number
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Branch
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Items
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Priority
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Expected Delivery
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {pendingOrders.map((order) => (
                  <tr key={order._id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {order.orderNumber}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">{order.branchName}</div>
                      <div className="text-sm text-gray-500">{order.branchLocation}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm text-gray-900">
                        {order.items.map(item => `${item.itemName} (${item.quantity})`).join(', ')}
                      </div>
                      <div className="text-sm text-gray-500">Total: {order.totalQuantity} units</div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getPriorityColor(order.priority)}`}>
                        {order.priority}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {new Date(order.expectedDeliveryDate).toLocaleDateString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                        order.status === 'Pending' ? 'text-yellow-600 bg-yellow-100' : 'text-blue-600 bg-blue-100'
                      }`}>
                        {order.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-md p-8 text-center">
          <p className="text-gray-500">No pending orders at the moment</p>
        </div>
      )}
    </div>
  );

  const renderInventoryTab = () => (
    <div className="space-y-6">
      {/* Page Title */}
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900">Inventory Management</h2>
        <p className="text-gray-600">Monitor and manage factory inventory levels</p>
      </div>
      
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold text-gray-800">Factory Inventory</h3>
        <Link to="/inventory" className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition-colors">
          Manage Inventory
        </Link>
      </div>
      
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
