      {/* Header with integrated navigation */}
      <div className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-40">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Top header */}
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center py-4 gap-4">
            <div>
              <div className="flex items-center space-x-2 mb-1">
                <span className="text-2xl">🏭</span>
                <h1 className="text-2xl font-bold text-blue-600">
                  Factory Manager Dashboard
                </h1>
              </div>
              <p className="text-sm text-gray-600">
                Welcome back, {user?.name || 'Factory Manager'} • {new Date().toLocaleDateString()}
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2 text-sm text-gray-500">
                <span className="w-2 h-2 bg-green-400 rounded-full"></span>
                <span>System Online</span>
              </div>
              <button 
                onClick={() => navigate('/logout')}
                className="text-sm text-gray-600 hover:text-red-600 hover:bg-red-50 px-3 py-1 rounded-md transition-colors duration-200"
              >
                Logout
              </button>
            </div>
          </div>
          
          {/* Main navigation tabs */}
          <nav className="flex flex-wrap gap-2" aria-label="Tabs">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center py-3 px-4 rounded-lg font-medium text-sm transition-all duration-200 ${
                  activeTab === tab.id
                    ? 'bg-blue-50 text-blue-700 border-2 border-blue-200 shadow-sm'
                    : 'text-gray-600 hover:text-gray-800 hover:bg-gray-50 border-2 border-transparent'
                }`}
              >
                <span className="mr-2 text-lg">{tab.icon}</span>
                <span className="hidden sm:inline">{tab.name}</span>
                <span className="sm:hidden">{tab.name.split(' ')[0]}</span>
              </button>
            ))}
          </nav>
          
          {/* Tab Descriptions and Quick Actions */}
          <div className="py-3 border-t border-gray-200">
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-3">
              <p className="text-sm text-gray-600">
                {tabs.find(tab => tab.id === activeTab)?.description || 'Select a tab to manage factory operations'}
              </p>
              <div className="flex flex-wrap gap-2">
                <button className="text-xs bg-blue-50 text-blue-700 px-3 py-1 rounded-full hover:bg-blue-100 transition-colors duration-200">
                  🔄 Refresh Data
                </button>
                <button className="text-xs bg-green-50 text-green-700 px-3 py-1 rounded-full hover:bg-green-100 transition-colors duration-200">
                  📊 Export Report
                </button>
                <button className="text-xs bg-purple-50 text-purple-700 px-3 py-1 rounded-full hover:bg-purple-100 transition-colors duration-200">
                  ⚡ Quick Actions
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Main content area */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {renderTabContent()}
      </div>
    </div>
  );
};

export default FactoryDashboard;
