import React, { useEffect, useState, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default markers in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Custom icons for different markers
const createCustomIcon = (color, iconType) => {
  return L.divIcon({
    className: 'custom-div-icon',
    html: `
      <div style="
        background-color: ${color};
        width: 30px;
        height: 30px;
        border-radius: 50% 50% 50% 0;
        border: 3px solid white;
        box-shadow: 0 2px 6px rgba(0,0,0,0.3);
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 14px;
        color: white;
        transform: rotate(-45deg);
      ">
        <span style="transform: rotate(45deg);">${iconType}</span>
      </div>
    `,
    iconSize: [30, 30],
    iconAnchor: [15, 30],
  });
};

const branchIcon = createCustomIcon('#3B82F6', '🏢');
const emergencyIcon = createCustomIcon('#EF4444', '🚨');
const exactEmergencyIcon = createCustomIcon('#DC2626', '🎯'); // Darker red for exact locations
const driverIcon = createCustomIcon('#10B981', '🚚');
const userLocationIcon = createCustomIcon('#10B981', '📍'); // Green for user location

// Component to handle map updates
const MapUpdater = ({ center, zoom }) => {
  const map = useMap();
  
  useEffect(() => {
    if (center && zoom) {
      map.setView(center, zoom);
    }
  }, [center, zoom, map]);
  
  return null;
};

// Comprehensive database of Sri Lankan locations with accurate coordinates
const sriLankanLocations = {
  // Colombo areas - Updated with more accurate coordinates
  'maradana': [6.9271, 79.8665], // More accurate Maradana coordinates
  'colombo 01': [6.9344, 79.8428], // Fort - more accurate
  'colombo 02': [6.9200, 79.8500], // Slave Island
  'colombo 03': [6.9100, 79.8500], // Kollupitiya
  'colombo 04': [6.9000, 79.8600], // Bambalapitiya
  'colombo 05': [6.8900, 79.8600], // Wellawatta
  'colombo 06': [6.8800, 79.8700], // Mount Lavinia
  'colombo 07': [6.8700, 79.8700], // Dehiwala
  'colombo 08': [6.9047, 79.8844], // Borella
  'colombo 09': [6.8900, 79.8800], // Dematagoda
  'colombo 10': [6.9271, 79.8665], // Maradana - same as main Maradana
  'colombo 11': [6.9400, 79.8500], // Pettah
  'colombo 12': [6.9500, 79.8400], // Kotahena
  'colombo 13': [6.9600, 79.8300], // Modara
  'colombo 14': [6.9700, 79.8200], // Grandpass
  'colombo 15': [6.9800, 79.8100], // Mattakkuliya
  
  // Specific areas
  'fort': [6.9300, 79.8400],
  'pettah': [6.9400, 79.8500],
  'slave island': [6.9200, 79.8500],
  'kollupitiya': [6.9100, 79.8500],
  'bambalapitiya': [6.8900, 79.8600],
  'wellawatta': [6.8900, 79.8600],
  'mount lavinia': [6.8800, 79.8700],
  'dehiwala': [6.8700, 79.8700],
  'borella': [6.9047, 79.8844],
  'dematagoda': [6.8900, 79.8800],
  'kotahena': [6.9500, 79.8400],
  'modara': [6.9600, 79.8300],
  'grandpass': [6.9700, 79.8200],
  'mattakkuliya': [6.9800, 79.8100],
  
  // Major roads
  'galle road': [6.9100, 79.8500],
  'baseline road': [6.9047, 79.8844],
  'high level road': [6.8481, 79.9285],
  'reid avenue': [6.9200, 79.8500],
  'bauddhaloka mawatha': [6.9100, 79.8500],
  
  // Southern areas
  'maharagama': [6.8481, 79.9285],
  'nugegoda': [6.8631, 79.8996],
  'kottawa': [6.8400, 79.9500],
  'homagama': [6.8400, 80.0000],
  'piliyandala': [6.8500, 79.9000],
  'kaduwela': [6.9300, 79.9800],
  'avissawella': [6.9500, 80.2000],
  'rathmalana': [6.8200, 79.8800],
  'moratuwa': [6.8000, 79.9000],
  'panadura': [6.7200, 79.9000],
  'kalutara': [6.5800, 79.9600],
  
  // Other major cities
  'kandy': [7.2906, 80.6337],
  'galle': [6.0329, 80.2169],
  'jaffna': [9.6615, 80.0255],
  'anuradhapura': [8.3114, 80.4037],
  'trincomalee': [8.5874, 81.2152],
  'batticaloa': [7.7102, 81.6924],
  'kurunegala': [7.4863, 80.3633],
  'negombo': [7.2086, 79.8358],
  'ratnapura': [6.6828, 80.4012],
  'badulla': [6.9934, 81.0550]
};

// Function to get exact coordinates for specific known addresses
const getExactCoordinatesForAddress = (address) => {
  if (!address) return null;
  
  const addressLower = address.toLowerCase();
  console.log('🔍 Checking address for exact coordinates:', address);
  console.log('🔍 Address in lowercase:', addressLower);
  
  // Check for exact matches in our database
  for (const [location, coords] of Object.entries(sriLankanLocations)) {
    if (addressLower.includes(location)) {
      console.log(`🎯 Found exact location: ${location}`);
      console.log(`📍 Returning coordinates: [${coords[0]}, ${coords[1]}]`);
      return coords;
    }
  }
  
  // Check for specific combinations
  if (addressLower.includes('maradana') && addressLower.includes('baseline road') && addressLower.includes('colombo 10')) {
    console.log('🎯 Found exact address: Maradana, Baseline Road, Colombo 10');
    return [6.9271, 79.8665]; // Updated with more accurate coordinates
  }
  
  if (addressLower.includes('maharagama') && addressLower.includes('high level road')) {
    console.log('🎯 Found exact address: Maharagama High Level Road');
    return [6.8481, 79.9285];
  }
  
  if (addressLower.includes('borella') && addressLower.includes('baseline road')) {
    console.log('🎯 Found exact address: Borella Baseline Road');
    return [6.9047, 79.8844];
  }
  
  console.log('❌ No exact match found for address:', address);
  return null; // No exact match found
};

// Function to geocode any address to get exact coordinates
const geocodeAddress = async (address) => {
  if (!address) return null;
  
  try {
    console.log('🌍 Geocoding address:', address);
    
    // Extract key components from the address
    const addressParts = address.split(',').map(part => part.trim());
    const mainLocation = addressParts[0];
    const area = addressParts[1] || '';
    const city = addressParts[2] || 'Colombo';
    
    // Try multiple search strategies with different combinations
    const searchQueries = [
      // Full address variations
      `${address}, Sri Lanka`,
      `${address}, Colombo, Sri Lanka`,
      address,
      
      // Main location with city
      `${mainLocation}, ${city}, Sri Lanka`,
      `${mainLocation}, Colombo, Sri Lanka`,
      `${mainLocation}, Sri Lanka`,
      
      // Main location only
      mainLocation,
      
      // Area-based searches
      `${mainLocation} ${area}, Sri Lanka`,
      `${mainLocation} ${area}, Colombo, Sri Lanka`,
      
      // Common Sri Lankan location patterns
      `${mainLocation}, Western Province, Sri Lanka`,
      `${mainLocation}, Colombo District, Sri Lanka`,
      
      // Postal code patterns (if any)
      address.replace(/colombo\s*(\d+)/i, 'Colombo $1, Sri Lanka'),
      
      // Road-specific searches
      address.replace(/road|street|mawatha|place/i, (match) => `${match}, Sri Lanka`)
    ];
    
    // Remove duplicates and empty strings
    const uniqueQueries = [...new Set(searchQueries.filter(q => q && q.trim()))];
    
    console.log(`🔍 Trying ${uniqueQueries.length} geocoding strategies...`);
    
    for (const searchQuery of uniqueQueries) {
      try {
        console.log(`🔍 Trying geocoding: "${searchQuery}"`);
        
        const response = await fetch(
          `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchQuery)}&limit=10&countrycodes=lk&addressdetails=1&extratags=1`
        );
        
        if (!response.ok) {
          console.log(`⚠️ HTTP error: ${response.status}`);
          continue;
        }
        
        const data = await response.json();
        
        if (data && data.length > 0) {
          console.log(`📊 Found ${data.length} results for: "${searchQuery}"`);
          
          // Score and rank results
          const scoredResults = data.map(result => {
            const score = calculateLocationScore(result, address, mainLocation);
            return { ...result, score };
          }).sort((a, b) => b.score - a.score);
          
          // Find the best result within Sri Lanka bounds
          for (const result of scoredResults) {
            const lat = parseFloat(result.lat);
            const lon = parseFloat(result.lon);
            
            // More precise Sri Lanka bounds
            if (lat > 5.5 && lat < 10.0 && lon > 79.0 && lon < 82.0) {
              const coords = [lat, lon];
              console.log('🎯 Found best Sri Lankan location:', result.display_name);
              console.log('📍 Coordinates:', coords);
              console.log('⭐ Score:', result.score);
              console.log('🏷️ Type:', result.type);
              console.log('🏷️ Class:', result.class);
              
              // Additional validation for better accuracy
              if (result.type === 'administrative' || result.type === 'residential' || result.type === 'highway') {
                console.log('✅ High confidence result selected');
                return coords;
              } else if (result.score > 50) {
                console.log('✅ Good score result selected');
                return coords;
              }
            }
          }
          
          // If no high-confidence result, return the first valid one
          for (const result of scoredResults) {
            const lat = parseFloat(result.lat);
            const lon = parseFloat(result.lon);
            
            if (lat > 5.5 && lat < 10.0 && lon > 79.0 && lon < 82.0) {
              const coords = [lat, lon];
              console.log('🎯 Using fallback Sri Lankan location:', result.display_name);
              console.log('📍 Coordinates:', coords);
              return coords;
            }
          }
        }
      } catch (strategyError) {
        console.log(`⚠️ Geocoding strategy failed: ${strategyError.message}`);
        continue;
      }
    }
    
    console.log('⚠️ All geocoding attempts failed');
    return null;
    
  } catch (error) {
    console.error('❌ Geocoding failed:', error);
    return null;
  }
};

// Function to score location results based on relevance
const calculateLocationScore = (result, originalAddress, mainLocation) => {
  let score = 0;
  const displayName = (result.display_name || '').toLowerCase();
  const address = (result.address || {}).address29 || '';
  const originalLower = originalAddress.toLowerCase();
  const mainLower = mainLocation.toLowerCase();
  
  // Exact match bonus
  if (displayName.includes(mainLower)) score += 100;
  if (address.includes(mainLower)) score += 80;
  
  // Partial match bonus
  if (displayName.includes(originalLower)) score += 60;
  
  // Sri Lanka context bonus
  if (displayName.includes('sri lanka') || displayName.includes('colombo')) score += 40;
  
  // Road/street match bonus
  if (originalLower.includes('road') && displayName.includes('road')) score += 30;
  if (originalLower.includes('street') && displayName.includes('street')) score += 30;
  if (originalLower.includes('mawatha') && displayName.includes('mawatha')) score += 30;
  
  // Importance bonus (from OSM importance field)
  if (result.importance) score += result.importance * 20;
  
  // Type bonus
  if (result.type === 'administrative') score += 10;
  if (result.type === 'residential') score += 5;
  
  return score;
};

// Component to generate emergency location (exact or random)
const generateEmergencyLocation = (branchCoords, maxDistanceKm = 30, address = null, userLocation = null) => {
  // First, try to get exact coordinates for the address
  if (address) {
    const exactCoords = getExactCoordinatesForAddress(address);
    if (exactCoords) {
      console.log('📍 Using exact coordinates for address:', address);
      return exactCoords;
    }
  }
  
  // If user location is available and accurate, use it as base for generation
  let baseCoords = branchCoords;
  if (userLocation && userLocation.coords && userLocation.accuracy < 100) {
    console.log('📍 Using user location as base for emergency generation');
    console.log('🎯 User location accuracy:', userLocation.accuracy, 'meters');
    baseCoords = userLocation.coords;
  } else {
    console.log('📍 Using branch location as base for emergency generation');
  }
  
  // If no exact match, generate random location within distance
  console.log('🎲 Generating random location within', maxDistanceKm, 'km from base coordinates');
  const R = 6371; // Earth's radius in km
  
  // Convert to radians
  const lat1 = baseCoords[0] * Math.PI / 180;
  const lng1 = baseCoords[1] * Math.PI / 180;
  
  // Generate random distance (0 to maxDistanceKm)
  const distance = Math.random() * maxDistanceKm;
  
  // Generate random bearing (0 to 2π)
  const bearing = Math.random() * 2 * Math.PI;
  
  // Calculate new coordinates
  const lat2 = Math.asin(
    Math.sin(lat1) * Math.cos(distance / R) +
    Math.cos(lat1) * Math.sin(distance / R) * Math.cos(bearing)
  );
  
  const lng2 = lng1 + Math.atan2(
    Math.sin(bearing) * Math.sin(distance / R) * Math.cos(lat1),
    Math.cos(distance / R) - Math.sin(lat1) * Math.sin(lat2)
  );
  
  const generatedCoords = [
    lat2 * 180 / Math.PI,
    lng2 * 180 / Math.PI
  ];
  
  console.log('🎲 Generated emergency location:', generatedCoords);
  console.log('📍 Base coordinates used:', baseCoords);
  console.log('📏 Distance from base:', calculateDistance(baseCoords[0], baseCoords[1], generatedCoords[0], generatedCoords[1]).toFixed(2), 'km');
  
  return generatedCoords;
};

// Calculate distance between two points using Haversine formula
const calculateDistance = (lat1, lng1, lat2, lng2) => {
  const R = 6371; // Earth's radius in km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLng = (lng2 - lng1) * Math.PI / 180;
  const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
    Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
    Math.sin(dLng/2) * Math.sin(dLng/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  return R * c;
};

const InteractiveMap = ({ 
  branchLocation, 
  emergencyRequest, 
  showRoute = false,
  onRouteCalculated,
  onEmergencyLocationGenerated 
}) => {
  const [emergencyCoords, setEmergencyCoords] = useState(null);
  const [route, setRoute] = useState([]);
  const [distance, setDistance] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(null);
  const [loading, setLoading] = useState(false);
  const [mapCenter, setMapCenter] = useState([6.9271, 79.8612]); // Colombo center
  const [mapZoom, setMapZoom] = useState(12);
  const [isGeocoding, setIsGeocoding] = useState(false);
  const [generatedEmergencyLocation, setGeneratedEmergencyLocation] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [locationPermission, setLocationPermission] = useState('prompt'); // 'granted', 'denied', 'prompt'
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  
  const mapRef = useRef(null);

  // Colombo 7 branch coordinates (more accurate)
  const colombo7BranchCoords = [6.8700, 79.8700]; // Dehiwala area coordinates
  
  // Geolocation functions
  const getUserLocation = () => {
    if (!navigator.geolocation) {
      console.log('❌ Geolocation is not supported by this browser');
      setLocationPermission('denied');
      return;
    }

    setIsGettingLocation(true);
    console.log('🌍 Requesting user location...');

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        const userCoords = [latitude, longitude];
        
        console.log('✅ User location obtained:', userCoords);
        console.log('📍 Accuracy:', position.coords.accuracy, 'meters');
        
        setUserLocation({
          coords: userCoords,
          accuracy: position.coords.accuracy,
          timestamp: position.timestamp
        });
        setLocationPermission('granted');
        setIsGettingLocation(false);
        
        // Update map center to user location if it's in Sri Lanka
        if (latitude > 5.5 && latitude < 10.0 && longitude > 79.0 && longitude < 82.0) {
          setMapCenter(userCoords);
          setMapZoom(14);
          console.log('🗺️ Map centered on user location');
        }
      },
      (error) => {
        console.error('❌ Error getting user location:', error);
        setIsGettingLocation(false);
        
        switch (error.code) {
          case error.PERMISSION_DENIED:
            console.log('🚫 Location access denied by user');
            setLocationPermission('denied');
            break;
          case error.POSITION_UNAVAILABLE:
            console.log('⚠️ Location information unavailable');
            setLocationPermission('denied');
            break;
          case error.TIMEOUT:
            console.log('⏰ Location request timed out');
            setLocationPermission('denied');
            break;
          default:
            console.log('❌ Unknown error occurred');
            setLocationPermission('denied');
            break;
        }
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000 // 5 minutes
      }
    );
  };

  // Request location permission on component mount
  useEffect(() => {
    getUserLocation();
  }, []);
  
  // Default branch location if not provided
  const defaultBranchLocation = {
    name: 'Colombo 7 Branch',
    address: '123 Galle Road, Colombo 07, Sri Lanka',
    coordinates: colombo7BranchCoords
  };

  const branch = branchLocation || defaultBranchLocation;
  const branchCoords = branch.coordinates || colombo7BranchCoords;

  // Generate emergency location when emergency request is provided
  useEffect(() => {
    const processEmergencyLocation = async () => {
      console.log('🚀 Processing emergency location...');
      console.log('📋 Emergency request:', emergencyRequest);
      console.log('📍 Current emergency coords:', emergencyCoords);
      
      if (emergencyRequest && !emergencyCoords) {
        let coords;
        let locationType = 'generated';
        
        console.log('🔍 Emergency request found, processing location...');
        
        if (emergencyRequest.brigadeLocation) {
          // First, try to get exact coordinates for the address (prioritize over provided coordinates)
          const exactCoords = getExactCoordinatesForAddress(emergencyRequest.brigadeLocation);
          console.log('🎯 Exact coords result:', exactCoords);
          
          if (exactCoords) {
            coords = exactCoords;
            locationType = 'exact';
            console.log('✅ Using exact coordinates for address (overriding provided):', emergencyRequest.brigadeLocation, '->', coords);
          } else if (emergencyRequest.coordinates) {
            // Use provided coordinates only if no exact match found
            coords = [emergencyRequest.coordinates.lat, emergencyRequest.coordinates.lng];
            locationType = 'provided';
            console.log('📍 Using provided coordinates:', coords);
          } else {
            // Try to geocode the address to get real coordinates
            console.log('🌍 Attempting to geocode address:', emergencyRequest.brigadeLocation);
            setIsGeocoding(true);
            const geocodedCoords = await geocodeAddress(emergencyRequest.brigadeLocation);
            setIsGeocoding(false);
            
            if (geocodedCoords) {
              coords = geocodedCoords;
              locationType = 'geocoded';
              console.log('✅ Successfully geocoded address:', emergencyRequest.brigadeLocation, 'to:', coords);
            } else {
              // Fallback: Generate random location within 30km using user location if available
              coords = generateEmergencyLocation(branchCoords, 30, emergencyRequest.brigadeLocation, userLocation);
              locationType = 'generated';
              console.log('🎲 Generated random emergency location within 30km:', coords);
            }
          }
        } else if (emergencyRequest.coordinates) {
          // Use provided coordinates only if no address provided
          coords = [emergencyRequest.coordinates.lat, emergencyRequest.coordinates.lng];
          locationType = 'provided';
          console.log('📍 Using provided coordinates (no address):', coords);
        } else {
          // No address provided, generate random location using user location if available
          coords = generateEmergencyLocation(branchCoords, 30, null, userLocation);
          locationType = 'generated';
          console.log('🎲 Generated random emergency location within 30km (no address):', coords);
        }
        
        // Set the generated location state
        const locationData = {
          lat: coords[0],
          lng: coords[1],
          address: emergencyRequest.brigadeLocation || 'Generated Emergency Location',
          type: locationType
        };
        setGeneratedEmergencyLocation(locationData);
        
        // Notify parent component about generated location
        if (onEmergencyLocationGenerated) {
          onEmergencyLocationGenerated(locationData);
        }
        
        setEmergencyCoords(coords);
        
        // Debug: Log the coordinates being used
        console.log('🗺️ Emergency location coordinates set:', coords);
        console.log('📍 Address:', emergencyRequest.brigadeLocation);
        console.log('🎯 Location type:', locationType);
        console.log('🔍 Final coordinates for map marker:', coords);
        console.log('📊 Location data object:', locationData);
        
        // Calculate distance
        const calculatedDistance = calculateDistance(
          branchCoords[0], branchCoords[1],
          coords[0], coords[1]
        );
        setDistance(calculatedDistance);
        
        // Calculate estimated time (assuming 40 km/h average speed)
        const timeInHours = calculatedDistance / 40;
        const hours = Math.floor(timeInHours);
        const minutes = Math.round((timeInHours - hours) * 60);
        const timeString = hours > 0 ? `${hours}h ${minutes}m` : `${minutes}m`;
        setEstimatedTime(timeString);
        
        // Update map center to show both locations
        const centerLat = (branchCoords[0] + coords[0]) / 2;
        const centerLng = (branchCoords[1] + coords[1]) / 2;
        setMapCenter([centerLat, centerLng]);
        setMapZoom(11);
      }
    };

    processEmergencyLocation();
  }, [emergencyRequest, branchCoords, emergencyCoords, onEmergencyLocationGenerated]);

  // Calculate route when showRoute is true
  useEffect(() => {
    if (showRoute && emergencyCoords && branchCoords) {
      calculateRoute();
    }
  }, [showRoute, emergencyCoords, branchCoords]);

  const calculateRoute = async () => {
    if (!emergencyCoords || !branchCoords) return;
    
    setLoading(true);
    
    try {
      // Create a simple route with waypoints
      const routePoints = [
        branchCoords,
        [
          (branchCoords[0] + emergencyCoords[0]) / 2 + (Math.random() - 0.5) * 0.01,
          (branchCoords[1] + emergencyCoords[1]) / 2 + (Math.random() - 0.5) * 0.01
        ],
        emergencyCoords
      ];
      
      setRoute(routePoints);
      
      // Calculate road distance (approximate)
      let roadDistance = 0;
      for (let i = 0; i < routePoints.length - 1; i++) {
        roadDistance += calculateDistance(
          routePoints[i][0], routePoints[i][1],
          routePoints[i + 1][0], routePoints[i + 1][1]
        );
      }
      
      // Add some road factor (roads are not straight lines)
      roadDistance *= 1.3; // 30% longer than straight line
      setDistance(roadDistance);
      
      // Calculate estimated time
      const timeInHours = roadDistance / 40; // 40 km/h average
      const hours = Math.floor(timeInHours);
      const minutes = Math.round((timeInHours - hours) * 60);
      const timeString = hours > 0 ? `${hours}h ${minutes}m` : `${minutes}m`;
      setEstimatedTime(timeString);
      
      // Notify parent component
      if (onRouteCalculated) {
        onRouteCalculated({
          distance: roadDistance,
          estimatedTime: timeString,
          route: routePoints
        });
      }
      
    } catch (error) {
      console.error('Error calculating route:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="w-full h-96 border border-gray-300 rounded-lg overflow-hidden">
      <MapContainer
        ref={mapRef}
        center={mapCenter}
        zoom={mapZoom}
        style={{ height: '100%', width: '100%' }}
        scrollWheelZoom={true}
      >
        <MapUpdater center={mapCenter} zoom={mapZoom} />
        
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        
        {/* Branch Marker */}
        <Marker position={branchCoords} icon={branchIcon}>
          <Popup>
            <div className="p-2">
              <h3 className="font-bold text-blue-600">🏢 {branch.name}</h3>
              <p className="text-sm text-gray-600">{branch.address}</p>
              <p className="text-xs text-gray-500 mt-1">
                📍 {branchCoords[0].toFixed(6)}, {branchCoords[1].toFixed(6)}
              </p>
            </div>
          </Popup>
        </Marker>
        
        {/* User Location Marker */}
        {userLocation && userLocation.coords && (
          <Marker position={userLocation.coords} icon={userLocationIcon}>
            <Popup>
              <div className="p-2">
                <h3 className="font-bold text-green-600">📍 Your Location</h3>
                <p className="text-sm text-gray-600">
                  Accuracy: {userLocation.accuracy.toFixed(0)} meters
                </p>
                <p className="text-xs text-gray-500 mt-1">
                  📍 {userLocation.coords[0].toFixed(6)}, {userLocation.coords[1].toFixed(6)}
                </p>
                <p className="text-xs text-green-600 mt-1">
                  ✅ GPS Location Active
                </p>
              </div>
            </Popup>
          </Marker>
        )}
        
        {/* Emergency Location Marker */}
        {emergencyCoords && (
          <Marker 
            position={emergencyCoords} 
            icon={
              generatedEmergencyLocation?.type === 'exact' || generatedEmergencyLocation?.type === 'geocoded' 
                ? exactEmergencyIcon 
                : emergencyIcon
            }
          >
            <Popup>
              <div className="p-2">
                <h3 className="font-bold text-red-600">🚨 Emergency Location</h3>
                <p className="text-sm text-gray-600">
                  {emergencyRequest?.brigadeName || 'Emergency Request'}
                </p>
                <p className="text-sm text-gray-600">
                  {emergencyRequest?.brigadeLocation || 'Generated Location'}
                </p>
                <p className="text-xs text-gray-500 mt-1">
                  📍 {emergencyCoords[0].toFixed(6)}, {emergencyCoords[1].toFixed(6)}
                </p>
                {generatedEmergencyLocation && (
                  <p className="text-xs text-blue-600 mt-1">
                    {generatedEmergencyLocation.type === 'exact' ? '✅ Exact location' :
                     generatedEmergencyLocation.type === 'geocoded' ? '🌍 Geocoded location' :
                     generatedEmergencyLocation.type === 'provided' ? '📍 Provided coordinates' :
                     '🎲 Generated location'}
                  </p>
                )}
                {distance && (
                  <p className="text-xs text-blue-600 mt-1">
                    📏 Distance: {distance.toFixed(2)} km
                  </p>
                )}
                {estimatedTime && (
                  <p className="text-xs text-green-600">
                    ⏱️ Est. Time: {estimatedTime}
                  </p>
                )}
              </div>
            </Popup>
          </Marker>
        )}
        
        {/* Route Line */}
        {showRoute && route.length > 0 && (
          <Polyline
            positions={route}
            color="#3B82F6"
            weight={4}
            opacity={0.8}
            dashArray="10, 10"
          />
        )}
        
        {/* Loading indicator */}
        {(loading || isGeocoding) && (
          <div className="absolute top-4 left-4 bg-white p-2 rounded shadow-lg z-[1000]">
            <div className="flex items-center space-x-2">
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600"></div>
              <span className="text-sm text-gray-600">
                {isGeocoding ? 'Finding exact location...' : 'Calculating route...'}
              </span>
            </div>
          </div>
        )}
      </MapContainer>
      
      {/* Map Info Panel */}
      <div className="absolute bottom-4 left-4 bg-white p-3 rounded shadow-lg z-[1000] max-w-xs">
        <h4 className="font-semibold text-gray-800 mb-2">📍 Route Information</h4>
        <div className="text-sm space-y-1">
          <p><strong>From:</strong> {branch.name}</p>
          <p className="text-xs text-gray-500">📍 Branch: {branchCoords[0].toFixed(6)}, {branchCoords[1].toFixed(6)}</p>
          <p><strong>To:</strong> {emergencyRequest?.brigadeName || 'Emergency Location'}</p>
          {emergencyRequest?.brigadeLocation && (
            <p className="text-xs text-gray-600"><strong>Address:</strong> {emergencyRequest.brigadeLocation}</p>
          )}
          {emergencyCoords && (
            <p className="text-xs text-gray-600"><strong>Emergency Coords:</strong> {emergencyCoords[0].toFixed(6)}, {emergencyCoords[1].toFixed(6)}</p>
          )}
          {generatedEmergencyLocation && (
            <div className="mt-2">
              <p className={`text-xs font-medium ${
                generatedEmergencyLocation.type === 'exact' ? 'text-green-600' :
                generatedEmergencyLocation.type === 'geocoded' ? 'text-blue-600' :
                generatedEmergencyLocation.type === 'provided' ? 'text-blue-600' :
                'text-orange-600'
              }`}>
                {generatedEmergencyLocation.type === 'exact' ? '✅ Exact location from database' :
                 generatedEmergencyLocation.type === 'geocoded' ? '🌍 Geocoded from address' :
                 generatedEmergencyLocation.type === 'provided' ? '📍 Using provided coordinates' :
                 '🎲 Generated location within 30km'}
              </p>
              {generatedEmergencyLocation.type === 'exact' && (
                <p className="text-xs text-green-500">🎯 High accuracy - known location</p>
              )}
              {generatedEmergencyLocation.type === 'geocoded' && (
                <p className="text-xs text-blue-500">🌍 Online geocoding used</p>
              )}
            </div>
          )}
          {distance && (
            <p><strong>Distance:</strong> {distance.toFixed(2)} km</p>
          )}
          {estimatedTime && (
            <p><strong>Est. Time:</strong> {estimatedTime}</p>
          )}
          {emergencyRequest?.priority && (
            <p className={`text-xs px-2 py-1 rounded ${
              emergencyRequest.priority === 'Critical' ? 'bg-red-100 text-red-800' :
              emergencyRequest.priority === 'High' ? 'bg-orange-100 text-orange-800' :
              'bg-yellow-100 text-yellow-800'
            }`}>
              Priority: {emergencyRequest.priority}
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default InteractiveMap;
