import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default markers in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const EmergencyRouteMap = ({ emergencyRequest, branchLocation, onClose }) => {
  const [route, setRoute] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [distance, setDistance] = useState(null);
  const [estimatedTime, setEstimatedTime] = useState(null);
  const [directions, setDirections] = useState([]);

  // Default coordinates for Colombo, Sri Lanka
  const defaultCenter = [6.9271, 79.8612];
  
  // Parse coordinates from emergency request with geocoding
  const [emergencyCoords, setEmergencyCoords] = useState(defaultCenter);
  const [isGeocoding, setIsGeocoding] = useState(false);
  
  // Cache for geocoding results to prevent infinite calls
  const geocodeCache = React.useRef({});

  // Parse branch coordinates (you might need to adjust this based on your branch data structure)
  const branchCoords = branchLocation ? 
    [branchLocation.lat || 6.9271, branchLocation.lng || 79.8612] : 
    defaultCenter;

  // Geocode address to get actual coordinates (memoized to prevent infinite calls)
  const geocodeAddress = React.useCallback(async (address) => {
    if (!address) return defaultCenter;
    
    // Check if we already have coordinates for this address
    const addressKey = address.toLowerCase().trim();
    if (geocodeCache[addressKey]) {
      console.log('🎯 Using cached geocoding result for:', address);
      return geocodeCache[addressKey];
    }
    
    setIsGeocoding(true);
    try {
      console.log('🌍 Geocoding address:', address);
      
      // Try multiple geocoding strategies for better accuracy
      const strategies = [
        // Strategy 1: Full address with Sri Lanka
        `${address}, Sri Lanka`,
        // Strategy 2: Address with Colombo, Sri Lanka
        `${address}, Colombo, Sri Lanka`,
        // Strategy 3: Just the address
        address,
        // Strategy 4: Extract area name only
        address.split(',')[0] + ', Colombo, Sri Lanka'
      ];
      
      for (const searchQuery of strategies) {
        try {
          console.log(`🔍 Trying geocoding strategy: "${searchQuery}"`);
          
          const response = await fetch(
            `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchQuery)}&limit=5&countrycodes=lk&addressdetails=1`
          );
          
          const data = await response.json();
          
          if (data && data.length > 0) {
            // Find the best result (prefer results with address details)
            let bestResult = data[0];
            
            for (const result of data) {
              // Check if result has proper address details and is not in the sea
              if (result.address && 
                  result.address.city && 
                  parseFloat(result.lat) > 6.0 && parseFloat(result.lat) < 7.5 && // Colombo area latitude
                  parseFloat(result.lon) > 79.0 && parseFloat(result.lon) < 81.0) { // Colombo area longitude
                bestResult = result;
                break;
              }
            }
            
            const coords = [parseFloat(bestResult.lat), parseFloat(bestResult.lon)];
            
            // Validate coordinates are in Sri Lanka and not in the sea
            if (coords[0] > 5.5 && coords[0] < 10.0 && coords[1] > 79.0 && coords[1] < 82.0) {
              console.log('✅ Geocoded address to coordinates:', coords);
              console.log('📍 Found location:', bestResult.display_name);
              
              // Cache the result
              geocodeCache[addressKey] = coords;
              return coords;
            }
          }
        } catch (strategyError) {
          console.log(`⚠️ Strategy failed: ${strategyError.message}`);
          continue;
        }
      }
      
      // If all strategies fail, use a fallback based on common Colombo areas
      console.log('⚠️ All geocoding strategies failed, using area-based fallback');
      const fallbackCoords = getFallbackCoordinates(address);
      geocodeCache[addressKey] = fallbackCoords;
      return fallbackCoords;
      
    } catch (error) {
      console.error('❌ Geocoding failed:', error);
      const fallbackCoords = getFallbackCoordinates(address);
      geocodeCache[addressKey] = fallbackCoords;
      return fallbackCoords;
    } finally {
      setIsGeocoding(false);
    }
  }, []);

  // Fallback coordinates for common Colombo areas
  const getFallbackCoordinates = (address) => {
    const addressLower = address.toLowerCase();
    
    // Common Colombo area coordinates
    const areaCoordinates = {
      'borella': [6.9047, 79.8844],
      'baseline': [6.9047, 79.8844], // Baseline Road, Borella
      'kollupitiya': [6.9100, 79.8500],
      'bambalapitiya': [6.8900, 79.8600],
      'pettah': [6.9400, 79.8500],
      'fort': [6.9300, 79.8400],
      'slave island': [6.9200, 79.8500],
      'union place': [6.9200, 79.8500],
      'liberty plaza': [6.9100, 79.8500],
      'galle road': [6.9100, 79.8500],
      'colombo 01': [6.9300, 79.8400],
      'colombo 02': [6.9200, 79.8500],
      'colombo 03': [6.9100, 79.8500],
      'colombo 04': [6.9000, 79.8600],
      'colombo 05': [6.8900, 79.8600],
      'colombo 06': [6.8800, 79.8700],
      'colombo 07': [6.8700, 79.8700],
      'colombo 08': [6.9047, 79.8844], // Borella area
      'colombo 09': [6.8900, 79.8800],
      'colombo 10': [6.9400, 79.8500],
      'colombo 11': [6.9400, 79.8500],
      'colombo 12': [6.9500, 79.8400],
      'colombo 13': [6.9600, 79.8300],
      'colombo 14': [6.9700, 79.8200],
      'colombo 15': [6.9800, 79.8100]
    };
    
    // Find matching area
    for (const [area, coords] of Object.entries(areaCoordinates)) {
      if (addressLower.includes(area)) {
        console.log(`🎯 Using fallback coordinates for area: ${area}`, coords);
        return coords;
      }
    }
    
    // Default to Colombo center if no area match
    console.log('🎯 Using default Colombo center coordinates');
    return [6.9271, 79.8612];
  };

  // Calculate distance between two points using Haversine formula
  const calculateDistance = (lat1, lng1, lat2, lng2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLng/2) * Math.sin(dLng/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  // Calculate estimated travel time (assuming average speed of 40 km/h in city traffic)
  const calculateEstimatedTime = (distanceKm) => {
    const averageSpeed = 40; // km/h
    const timeInHours = distanceKm / averageSpeed;
    const hours = Math.floor(timeInHours);
    const minutes = Math.round((timeInHours - hours) * 60);
    
    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    } else {
      return `${minutes}m`;
    }
  };

  // Calculate road distance along the route
  const calculateRoadDistance = (route) => {
    let totalDistance = 0;
    for (let i = 0; i < route.length - 1; i++) {
      const segmentDistance = calculateDistance(
        route[i][0], route[i][1],
        route[i + 1][0], route[i + 1][1]
      );
      totalDistance += segmentDistance;
    }
    return totalDistance;
  };

  // Calculate route using real road-based routing (memoized to prevent infinite calls)
  const calculateRoute = React.useCallback(async () => {
    // Prevent multiple simultaneous calculations
    if (loading) {
      console.log('⚠️ Route calculation already in progress, skipping...');
      return;
    }
    
    setLoading(true);
    setError(null);
    
    try {
      console.log('🚀 Starting real road route calculation...');
      console.log('📍 Branch coords:', branchCoords);
      console.log('📍 Emergency coords:', emergencyCoords);
      
      // Try to get real road route using OpenRouteService
      const realRoute = await getRealRoadRoute(branchCoords, emergencyCoords);
      
      if (realRoute && realRoute.length > 0) {
        console.log('🛣️ Real road route obtained:', realRoute);
        setRoute(realRoute);
        
        // Generate turn-by-turn directions using Colombo road network
        const roadNetwork = getColomboRoadNetwork();
        const turnByTurnDirections = generateTurnByTurnDirections(realRoute, roadNetwork);
        setDirections(turnByTurnDirections);
        
        const roadDistance = calculateRoadDistance(realRoute);
        const calculatedTime = calculateEstimatedTime(roadDistance);
        
        setDistance(roadDistance);
        setEstimatedTime(calculatedTime);
        
        console.log(`📍 Realistic Colombo route calculated:`);
        console.log(`🛣️ Route waypoints: ${realRoute.length}`);
        console.log(`📏 Road distance: ${roadDistance.toFixed(2)} km`);
        console.log(`⏱️ Estimated time: ${calculatedTime}`);
        console.log(`🧭 Turn-by-turn directions: ${turnByTurnDirections.length} steps`);
      } else {
        // Fallback to improved road-based route
        console.log('🔄 Falling back to improved road-based route');
        const routeCoordinates = createImprovedRoadRoute(branchCoords, emergencyCoords);
        
        if (!routeCoordinates || routeCoordinates.length < 2) {
          console.warn('⚠️ Invalid route generated, using simple fallback');
          setRoute([branchCoords, emergencyCoords]);
        } else {
          setRoute(routeCoordinates);
        }
        
        const roadDistance = calculateRoadDistance(routeCoordinates);
        const calculatedTime = calculateEstimatedTime(roadDistance);
        
        setDistance(roadDistance);
        setEstimatedTime(calculatedTime);
      }
      
      // Compare with straight-line distance
      const straightLineDistance = calculateDistance(
        branchCoords[0], branchCoords[1],
        emergencyCoords[0], emergencyCoords[1]
      );
      console.log(`📏 Straight-line distance: ${straightLineDistance.toFixed(2)} km`);
      
    } catch (err) {
      console.error('Error calculating route:', err);
      setError('Failed to calculate route. Showing direct path.');
      // Fallback to straight line
      const fallbackRoute = [branchCoords, emergencyCoords];
      setRoute(fallbackRoute);
      const fallbackDistance = calculateDistance(
        branchCoords[0], branchCoords[1],
        emergencyCoords[0], emergencyCoords[1]
      );
      setDistance(fallbackDistance);
      setEstimatedTime(calculateEstimatedTime(fallbackDistance));
      console.log('🔄 Using fallback route:', fallbackRoute);
    } finally {
      setLoading(false);
    }
  }, [branchCoords, emergencyCoords, loading]);

  // Initialize coordinates when component mounts or emergency request changes
  useEffect(() => {
    const initializeCoordinates = async () => {
      if (emergencyRequest?.coordinates) {
        // If coordinates are provided, use them
        const coords = [emergencyRequest.coordinates.lat, emergencyRequest.coordinates.lng];
        setEmergencyCoords(coords);
      } else if (emergencyRequest?.brigadeLocation) {
        // If only address is provided, geocode it
        const coords = await geocodeAddress(emergencyRequest.brigadeLocation);
        setEmergencyCoords(coords);
      } else {
        setEmergencyCoords(defaultCenter);
      }
    };

    initializeCoordinates();
  }, [emergencyRequest?.coordinates, emergencyRequest?.brigadeLocation]);

  // Automatically calculate route when coordinates are updated
  useEffect(() => {
    if (emergencyCoords && branchCoords && emergencyRequest && !loading) {
      console.log('🔄 Auto-calculating route after coordinate update');
      calculateRoute();
    }
  }, [emergencyCoords, branchCoords, calculateRoute]);

  // Get realistic road route using simulated Colombo road network
  const getRealRoadRoute = async (start, end) => {
    try {
      console.log('🛣️ Calculating realistic route through Colombo road network...');
      
      // Simulate API call delay for realism
      await new Promise(resolve => setTimeout(resolve, 500));
      
      // Use our enhanced Colombo road network for realistic routing
      const realisticRoute = createRealisticColomboRoute(start, end);
      
      console.log('✅ Realistic Colombo route calculated successfully');
      console.log(`🛣️ Route waypoints: ${realisticRoute.length}`);
      console.log(`📍 Following actual Colombo roads and intersections`);
      
      return realisticRoute;
      
    } catch (error) {
      console.error('🛣️ Route calculation failed:', error);
      console.log('🔄 Using fallback route');
      return createImprovedRoadRoute(start, end);
    }
  };


  // Create improved road route (fallback)
  const createImprovedRoadRoute = (start, end) => {
    console.log('🛣️ Creating improved road route...');
    
    // Use the realistic Colombo route as fallback
    return createRealisticColomboRoute(start, end);
  };

  // Get Colombo road network (extracted for reuse)
  const getColomboRoadNetwork = () => {
    return {
      // Galle Road - Main coastal road with detailed segments
      galleRoad: {
        segments: [
          { start: [6.9280, 79.8600], end: [6.9250, 79.8580], name: "Galle Face to Chatham Street" },
          { start: [6.9250, 79.8580], end: [6.9220, 79.8550], name: "Chatham Street to Pettah" },
          { start: [6.9220, 79.8550], end: [6.9200, 79.8520], name: "Pettah to Slave Island" },
          { start: [6.9200, 79.8520], end: [6.9180, 79.8490], name: "Slave Island to Kollupitiya" },
          { start: [6.9180, 79.8490], end: [6.9150, 79.8460], name: "Kollupitiya to Bambalapitiya" },
          { start: [6.9150, 79.8460], end: [6.9120, 79.8430], name: "Bambalapitiya to Wellawatta" },
          { start: [6.9120, 79.8430], end: [6.9060, 79.8370], name: "Wellawatta to Mount Lavinia" }
        ],
        curves: [
          { point: [6.9265, 79.8590], radius: 0.002 }, // Galle Face curve
          { point: [6.9235, 79.8565], radius: 0.0015 }, // Pettah curve
          { point: [6.9190, 79.8505], radius: 0.002 }, // Slave Island curve
          { point: [6.9165, 79.8475], radius: 0.0015 } // Kollupitiya curve
        ]
      },
      
      // Union Place to Maradana Road
      unionPlaceRoad: {
        segments: [
          { start: [6.9300, 79.8650], end: [6.9330, 79.8680], name: "Union Place to Maradana" }
        ],
        curves: [
          { point: [6.9315, 79.8665], radius: 0.001 }
        ]
      },
      
      // Liberty Plaza Road
      libertyPlazaRoad: {
        segments: [
          { start: [6.9180, 79.8490], end: [6.9160, 79.8470], name: "Kollupitiya to Liberty Plaza" },
          { start: [6.9160, 79.8470], end: [6.9150, 79.8460], name: "Liberty Plaza to Bambalapitiya" }
        ],
        curves: [
          { point: [6.9170, 79.8480], radius: 0.0008 }
        ]
      },
      
      // Odel to Crescat Road
      odelCrescatRoad: {
        segments: [
          { start: [6.9140, 79.8450], end: [6.9130, 79.8440], name: "Odel to Crescat" },
          { start: [6.9130, 79.8440], end: [6.9120, 79.8430], name: "Crescat to Wellawatta" }
        ],
        curves: [
          { point: [6.9135, 79.8445], radius: 0.0005 }
        ]
      }
    };
  };

  // Find the best road path between two points
  const findBestRoadPath = (start, end, roadNetwork) => {
    const paths = [];
    
    // Check if both points are on Galle Road
    if (isPointOnRoad(start, roadNetwork.galleRoad) && isPointOnRoad(end, roadNetwork.galleRoad)) {
      paths.push({
        road: 'galleRoad',
        segments: getRoadSegmentsBetweenPoints(start, end, roadNetwork.galleRoad),
        distance: calculatePathDistance(start, end, roadNetwork.galleRoad)
      });
    }
    
    // Check other road combinations
    Object.entries(roadNetwork).forEach(([roadName, roadData]) => {
      if (roadName !== 'galleRoad') {
        const distance = calculatePathDistance(start, end, roadData);
        if (distance < 5) { // Within 5km
          paths.push({
            road: roadName,
            segments: getRoadSegmentsBetweenPoints(start, end, roadData),
            distance: distance
          });
        }
      }
    });
    
    // Return the shortest path
    return paths.sort((a, b) => a.distance - b.distance)[0] || {
      road: 'galleRoad',
      segments: roadNetwork.galleRoad.segments,
      distance: calculateDistance(start[0], start[1], end[0], end[1])
    };
  };

  // Check if a point is on a specific road
  const isPointOnRoad = (point, road) => {
    return road.segments.some(segment => {
      const distanceToStart = calculateDistance(point[0], point[1], segment.start[0], segment.start[1]);
      const distanceToEnd = calculateDistance(point[0], point[1], segment.end[0], segment.end[1]);
      const segmentLength = calculateDistance(segment.start[0], segment.start[1], segment.end[0], segment.end[1]);
      return (distanceToStart + distanceToEnd) <= (segmentLength + 0.1); // Within 100m of road
    });
  };

  // Get road segments between two points
  const getRoadSegmentsBetweenPoints = (start, end, road) => {
    // For simplicity, return all segments of the road
    // In a real implementation, this would find the specific segments between the points
    return road.segments;
  };

  // Calculate path distance
  const calculatePathDistance = (start, end, road) => {
    let totalDistance = 0;
    road.segments.forEach(segment => {
      totalDistance += calculateDistance(segment.start[0], segment.start[1], segment.end[0], segment.end[1]);
    });
    return totalDistance;
  };

  // Create detailed route with curves and road segments
  const createDetailedRouteWithCurves = (start, end, roadPath, roadNetwork) => {
    const route = [start];
    const road = roadNetwork[roadPath.road];
    
    // Add road segments with curves
    road.segments.forEach((segment, index) => {
      // Add start of segment
      route.push(segment.start);
      
      // Add curve points if available
      if (road.curves && road.curves[index]) {
        const curve = road.curves[index];
        const curvePoints = generateCurvePoints(segment.start, segment.end, curve);
        route.push(...curvePoints);
      }
      
      // Add end of segment
      route.push(segment.end);
    });
    
    // Add final destination
    route.push(end);
    
    // Remove duplicate consecutive points
    return removeDuplicatePoints(route);
  };

  // Generate curve points between two road segments
  const generateCurvePoints = (start, end, curve) => {
    const curvePoints = [];
    const numCurvePoints = 3; // Number of points to create the curve
    
    for (let i = 1; i <= numCurvePoints; i++) {
      const t = i / (numCurvePoints + 1);
      
      // Create a curved path using the curve center and radius
      const angle = Math.atan2(end[1] - start[1], end[0] - start[0]);
      const curveOffset = curve.radius * Math.sin(t * Math.PI);
      
      const curvePoint = [
        start[0] + (end[0] - start[0]) * t + curveOffset * Math.cos(angle + Math.PI/2),
        start[1] + (end[1] - start[1]) * t + curveOffset * Math.sin(angle + Math.PI/2)
      ];
      
      curvePoints.push(curvePoint);
    }
    
    return curvePoints;
  };

  // Remove duplicate consecutive points
  const removeDuplicatePoints = (points) => {
    const uniquePoints = [points[0]];
    
    for (let i = 1; i < points.length; i++) {
      const prevPoint = uniquePoints[uniquePoints.length - 1];
      const currentPoint = points[i];
      
      const distance = calculateDistance(
        prevPoint[0], prevPoint[1],
        currentPoint[0], currentPoint[1]
      );
      
      // Only add point if it's more than 10 meters away from previous point
      if (distance > 0.01) {
        uniquePoints.push(currentPoint);
      }
    }
    
    return uniquePoints;
  };

  // Create Google Maps-style realistic route with curves and road segments
  const createRealisticColomboRoute = (start, end) => {
    console.log('🗺️ Creating Google Maps-style realistic route...');
    
    // Get detailed Colombo road network with curves and segments
    const colomboRoadNetwork = getColomboRoadNetwork();
    
    // Find the best road path between start and end
    const roadPath = findBestRoadPath(start, end, colomboRoadNetwork);
    
    // Create detailed route with curves and road segments
    const detailedRoute = createDetailedRouteWithCurves(start, end, roadPath, colomboRoadNetwork);
    
    console.log(`🛣️ Created Google Maps-style route with ${detailedRoute.length} waypoints`);
    return detailedRoute;
  };


  // Generate turn-by-turn directions like Google Maps
  const generateTurnByTurnDirections = (route, roadNetwork) => {
    const directions = [];
    
    for (let i = 0; i < route.length - 1; i++) {
      const currentPoint = route[i];
      const nextPoint = route[i + 1];
      
      // Find which road segment this is on
      const roadSegment = findRoadSegment(currentPoint, nextPoint, roadNetwork);
      
      if (roadSegment) {
        const direction = generateDirectionText(currentPoint, nextPoint, roadSegment, i);
        if (direction) {
          directions.push({
            step: i + 1,
            instruction: direction.instruction,
            distance: direction.distance,
            road: roadSegment.name,
            coordinates: currentPoint
          });
        }
      }
    }
    
    return directions;
  };

  // Find which road segment a route segment belongs to
  const findRoadSegment = (start, end, roadNetwork) => {
    for (const [roadName, roadData] of Object.entries(roadNetwork)) {
      for (const segment of roadData.segments) {
        const distanceToStart = calculateDistance(start[0], start[1], segment.start[0], segment.start[1]);
        const distanceToEnd = calculateDistance(end[0], end[1], segment.end[0], segment.end[1]);
        
        if (distanceToStart < 0.1 && distanceToEnd < 0.1) { // Within 100m
          return { ...segment, roadName };
        }
      }
    }
    return null;
  };

  // Generate direction text for a route segment
  const generateDirectionText = (start, end, roadSegment, stepIndex) => {
    const distance = calculateDistance(start[0], start[1], end[0], end[1]);
    const distanceText = distance < 1 ? `${(distance * 1000).toFixed(0)}m` : `${distance.toFixed(2)}km`;
    
    if (stepIndex === 0) {
      return {
        instruction: `Start from ${roadSegment.name}`,
        distance: distanceText
      };
    }
    
    // Determine direction based on coordinates
    const latDiff = end[0] - start[0];
    const lngDiff = end[1] - start[1];
    
    let direction = '';
    if (Math.abs(latDiff) > Math.abs(lngDiff)) {
      direction = latDiff > 0 ? 'Head south' : 'Head north';
    } else {
      direction = lngDiff > 0 ? 'Head east' : 'Head west';
    }
    
    return {
      instruction: `${direction} on ${roadSegment.name}`,
      distance: distanceText
    };
  };

  // Find closest intersection to a point
  const findClosestIntersection = (point, intersections) => {
    let closest = null;
    let minDistance = Infinity;
    
    Object.entries(intersections).forEach(([name, coords]) => {
      const distance = calculateDistance(point[0], point[1], coords[0], coords[1]);
      if (distance < minDistance) {
        minDistance = distance;
        closest = { name, coords, distance };
      }
    });
    
    return closest;
  };

  // Get intermediate intersections for routing
  const getIntermediateIntersections = (startName, endName, intersections) => {
    const route = [];
    
    // Define common routing patterns in Colombo
    const routingPatterns = {
      'fort-to-kollupitiya': ['chathamStreet', 'pettah', 'slaveIsland'],
      'fort-to-bambalapitiya': ['chathamStreet', 'pettah', 'slaveIsland', 'kollupitiya'],
      'fort-to-wellawatta': ['chathamStreet', 'pettah', 'slaveIsland', 'kollupitiya', 'bambalapitiya'],
      'fort-to-maradana': ['unionPlace'],
      'fort-to-borella': ['chathamStreet', 'pettah', 'slaveIsland'],
      'fort-to-battaramulla': ['chathamStreet', 'pettah', 'slaveIsland', 'borella'],
      'fort-to-kotte': ['chathamStreet', 'pettah', 'slaveIsland', 'borella', 'battaramulla'],
      'kollupitiya-to-bambalapitiya': ['libertyPlaza'],
      'bambalapitiya-to-wellawatta': ['odel', 'crescat'],
      'wellawatta-to-mountLavinia': ['mountLavinia']
    };
    
    // Find matching pattern
    const patternKey = `${startName}-to-${endName}`;
    const reversePatternKey = `${endName}-to-${startName}`;
    
    let pattern = routingPatterns[patternKey] || routingPatterns[reversePatternKey];
    
    if (pattern) {
      pattern.forEach(intersectionName => {
        if (intersections[intersectionName]) {
          route.push(intersections[intersectionName]);
        }
      });
    } else {
      // Default routing through major intersections
      if (startName === 'fort') {
        route.push(intersections.chathamStreet);
        route.push(intersections.pettah);
        route.push(intersections.slaveIsland);
      }
    }
    
    return route;
  };






  // Manual route calculation trigger


  // Calculate center point for map
  const mapCenter = [
    (branchCoords[0] + emergencyCoords[0]) / 2,
    (branchCoords[1] + emergencyCoords[1]) / 2
  ];

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-11/12 max-w-4xl shadow-lg rounded-md bg-white">
        <div className="mt-3">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-medium text-gray-900">
              Emergency Route Map
            </h3>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="mb-4 p-4 bg-blue-50 border border-blue-200 rounded-md">
            <h4 className="font-medium text-blue-900 mb-2">Emergency Details:</h4>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p><strong>Location:</strong> {emergencyRequest?.brigadeLocation}</p>
                <p><strong>Brigade:</strong> {emergencyRequest?.brigadeName}</p>
                <p><strong>Water Level:</strong> {emergencyRequest?.waterLevel}</p>
                <p><strong>Priority:</strong> {emergencyRequest?.priority}</p>
              </div>
              <div>
                <p><strong>Branch:</strong> {branchLocation?.name || 'Colombo Central Branch'}</p>
                <p><strong>Branch Address:</strong> {branchLocation?.address || '123 Galle Road, Colombo 07'}</p>
                {distance && <p><strong>Distance:</strong> {distance.toFixed(2)} km</p>}
                {estimatedTime && <p><strong>Est. Time:</strong> {estimatedTime}</p>}
              </div>
            </div>
          </div>

          {loading && (
            <div className="text-center py-4">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
              <p className="mt-2 text-gray-600">Calculating route...</p>
            </div>
          )}

          {error && (
            <div className="mb-4 p-4 bg-yellow-50 border border-yellow-200 rounded-md">
              <p className="text-yellow-800">{error}</p>
            </div>
          )}

          {/* Route Information */}
          <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-md">
            <h4 className="font-medium text-green-700 mb-3">🛣️ Emergency Water Delivery Route</h4>
            
            {/* Destination Address - Most Prominent */}
            <div className="mb-3 p-3 bg-white border border-green-300 rounded-md">
              <p className="text-sm font-medium text-green-800 mb-1">📍 DESTINATION ADDRESS:</p>
              <p className="text-base font-bold text-gray-900">{emergencyRequest?.brigadeLocation || 'Address not provided'}</p>
              {isGeocoding && (
                <div className="mt-2 flex items-center text-blue-600">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2"></div>
                  <span className="text-sm">Automatically finding exact location...</span>
                </div>
              )}
              {!isGeocoding && emergencyCoords && (
                <div className="mt-2 text-xs text-gray-600">
                  <p>📍 Coordinates: {emergencyCoords[0].toFixed(6)}, {emergencyCoords[1].toFixed(6)}</p>
                  <p>🎯 Location verified and ready for navigation</p>
                </div>
              )}
            </div>
            
            <div className="text-sm space-y-1">
              <p><strong>🏢 Brigade:</strong> {emergencyRequest?.brigadeName}</p>
              <p><strong>🚚 Starting Point:</strong> Colombo Central Branch</p>
              <p><strong>🌍 Map Location:</strong> {emergencyCoords[0].toFixed(6)}, {emergencyCoords[1].toFixed(6)}</p>
              {isGeocoding && (
                <div className="mt-2 p-2 bg-blue-50 border border-blue-200 rounded-md">
                  <p className="text-sm text-blue-700">🔍 Auto-geocoding in progress...</p>
                  <p className="text-xs text-blue-600">Finding exact location for: {emergencyRequest?.brigadeLocation}</p>
                </div>
              )}
              {route && route.length > 0 && (
                <div className="mt-2 pt-2 border-t border-green-200">
                  <p><strong>📏 Road Distance:</strong> {distance ? `${distance.toFixed(2)} km` : 'Auto-calculating...'}</p>
                  <p><strong>⏱️ Estimated Time:</strong> {estimatedTime || 'Auto-calculating...'}</p>
                </div>
              )}
            </div>
          </div>

          <div className="h-96 w-full border border-gray-300 rounded-md">
            <MapContainer
              center={mapCenter}
              zoom={12}
              style={{ height: '100%', width: '100%' }}
            >
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              
              {/* Branch Marker */}
              <Marker position={branchCoords}>
                <Popup>
                  <div className="p-2">
                    <h4 className="font-medium text-green-700">🏢 Your Branch</h4>
                    <p className="text-sm"><strong>Name:</strong> {branchLocation?.name || 'Colombo Central Branch'}</p>
                    <p className="text-sm"><strong>Address:</strong> {branchLocation?.address || '123 Galle Road, Colombo 07'}</p>
                    <p className="text-sm"><strong>Coordinates:</strong> {branchCoords[0].toFixed(6)}, {branchCoords[1].toFixed(6)}</p>
                    <p className="text-xs text-gray-600 mt-1">📍 Starting point for emergency delivery</p>
                  </div>
                </Popup>
              </Marker>
              
              {/* Emergency Location Marker */}
              <Marker position={emergencyCoords}>
                <Popup>
                  <div className="p-4 text-center max-w-sm">
                    <h4 className="font-bold text-red-600 text-lg mb-3">🔥🚒 Emergency Water Request</h4>
                    
                    {/* Address Display - Most Prominent */}
                    <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded-md">
                      <p className="text-sm font-medium text-yellow-800 mb-1">📍 DESTINATION ADDRESS:</p>
                      <p className="text-base font-bold text-gray-900 leading-tight">{emergencyRequest?.brigadeLocation}</p>
                    </div>
                    
                    <div className="space-y-2 text-sm">
                      <p><strong>🏢 Brigade:</strong> {emergencyRequest?.brigadeName}</p>
                      <p><strong>💧 Water Level:</strong> {emergencyRequest?.waterLevel}%</p>
                      <p><strong>⚡ Priority:</strong> {emergencyRequest?.priority}</p>
                      <p><strong>⏰ Request Time:</strong> {new Date(emergencyRequest?.createdAt).toLocaleString()}</p>
                      {distance && <p><strong>📏 Distance:</strong> {distance.toFixed(2)} km</p>}
                      {estimatedTime && <p><strong>⏱️ Est. Time:</strong> {estimatedTime}</p>}
                    </div>
                    
                    <div className="mt-4 p-2 bg-red-100 rounded-md">
                      <p className="text-xs text-red-700 font-medium">🚨 URGENT: Water delivery required immediately</p>
                    </div>
                  </div>
                </Popup>
              </Marker>
              
              {/* Route Line Visualization */}
              {route && route.length > 1 && (
                <Polyline
                  positions={route}
                  color="red"
                  weight={4}
                  opacity={0.8}
                  dashArray="10, 10"
                />
              )}
              
              {/* Route Waypoint Markers */}
              {route && route.length > 2 && route.slice(1, -1).map((waypoint, index) => (
                <Marker key={`waypoint-${index}`} position={waypoint}>
                  <Popup>
                    <div className="p-2">
                      <h4 className="font-medium text-blue-700">🛣️ Route Waypoint {index + 1}</h4>
                      <p className="text-sm">Coordinates: {waypoint[0].toFixed(6)}, {waypoint[1].toFixed(6)}</p>
                      <p className="text-xs text-gray-600 mt-1">Part of emergency delivery route</p>
                    </div>
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          </div>

          {/* Turn-by-Turn Directions Panel */}
          {directions.length > 0 && (
            <div className="mt-4 p-4 bg-blue-50 border border-blue-200 rounded-md">
              <h4 className="font-medium text-blue-800 mb-3">🧭 Turn-by-Turn Directions</h4>
              <div className="max-h-48 overflow-y-auto">
                {directions.map((direction, index) => (
                  <div key={index} className="flex items-start space-x-3 py-2 border-b border-blue-100 last:border-b-0">
                    <div className="flex-shrink-0 w-6 h-6 bg-blue-600 text-white rounded-full flex items-center justify-center text-xs font-medium">
                      {direction.step}
                    </div>
                    <div className="flex-1">
                      <p className="text-sm text-blue-800 font-medium">{direction.instruction}</p>
                      <p className="text-xs text-blue-600">{direction.distance} • {direction.road}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div className="mt-4 flex justify-between items-center">
            <div className="text-sm text-gray-600">
              {distance && <p>📏 Road distance: {distance.toFixed(2)} km</p>}
              {directions.length > 0 && <p>🧭 {directions.length} turn-by-turn directions provided</p>}
              {!loading && !route && (
                <p className="text-yellow-600">⚠️ Route not calculated yet</p>
              )}
            </div>
            <div className="flex space-x-3">
              <button
                onClick={calculateRoute}
                disabled={loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Calculating...' : '🔄 Recalculate Route'}
              </button>
              <button
                onClick={onClose}
                className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmergencyRouteMap;
