import React, { useState, useEffect } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import { useCart } from '../../Context/CartContext';
import UniversalNavbar from '../Nav/UniversalNavbar';
import axios from 'axios';

const Products = () => {
  const { category } = useParams();
  const { isAuthenticated, user } = useAuth();
  const { addToCart, totalItems } = useCart();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(category || 'all');
  const [priceRange, setPriceRange] = useState([0, 10000]);
  const [sortBy, setSortBy] = useState('name');
  const [showCartNotification, setShowCartNotification] = useState(false);

  // Product categories mapping
  const getProductCategory = (productName) => {
    if (productName.includes('Water Bottles')) return 'water-bottles';
    if (productName.includes('Membrane') || productName.includes('Cartridge') || productName.includes('UV')) return 'filters';
    if (productName.includes('Pump') || productName.includes('Filter')) return 'spare-parts';
    return 'filters';
  };

  // Product descriptions mapping
  const getProductDescription = (productName) => {
    const descriptions = {
      'RO Membranes': 'High-quality reverse osmosis membrane filter for pure water purification',
      'Mud-filters': 'Sediment filter cartridge for pre-filtration and water clarity',
      'Mineral Cartridge': 'Mineral cartridge to add essential minerals back to purified water',
      'UV Cartridge': 'Ultraviolet sterilization cartridge for water purification and disinfection',
      'Water Pumps': 'High-performance water pump for efficient water circulation',
      '5L Water Bottles': 'Premium 5-liter drinking water bottle for daily hydration',
      '10L Water Bottles': 'Premium 10-liter drinking water bottle for family use',
      '20L Water Bottles': 'Premium 20-liter drinking water bottle for office and home'
    };
    return descriptions[productName] || 'High-quality water treatment product';
  };

  // Product pricing mapping (in LKR)
  const getProductPrice = (productName) => {
    const prices = {
      'RO Membranes': 2500,
      'Mud-filters': 1200,
      'Mineral Cartridge': 1800,
      'UV Cartridge': 2200,
      'Water Pumps': 8500,
      '5L Water Bottles': 150,
      '10L Water Bottles': 250,
      '20L Water Bottles': 4000
    };
    return prices[productName] || 1000;
  };

  // Fetch products from backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const response = await axios.get('http://localhost:5000/CustomerPurchases/branch/inventory');
        
        if (response.data.success) {
          const inventoryItems = response.data.inventory;
          
          if (inventoryItems.length === 0) {
            setError('No products available in the system. Please contact the administrator to add products to the branch inventory.');
            return;
          }
          
          // Transform inventory items to product format
          const transformedProducts = inventoryItems.map((item, index) => ({
            id: index + 1,
            name: item.name,
            category: getProductCategory(item.name),
            price: getProductPrice(item.name),
            originalPrice: getProductPrice(item.name) * 1.2, // 20% markup for original price
            image: `/product${index + 1}.jpg`,
            description: getProductDescription(item.name),
            stock: item.quantity,
            warranty: item.name.includes('Bottles') ? 'N/A' : '1 Year',
            rating: 4.0 + Math.random() * 1, // Random rating between 4.0-5.0
            reviews: Math.floor(Math.random() * 200) + 50, // Random reviews between 50-250
            status: item.status,
            unit: item.unit
          }));
          
          setProducts(transformedProducts);
        } else {
          setError('Failed to fetch products from server');
        }
      } catch (error) {
        console.error('Error fetching products:', error);
        if (error.code === 'ECONNREFUSED' || error.message.includes('Network Error')) {
          setError('Backend server is not running. Please start the backend server and ensure MongoDB is running.');
        } else {
          setError('Unable to connect to server. Please check if the backend is running.');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  useEffect(() => {
    filterProducts();
  }, [products, searchQuery, selectedCategory, priceRange, sortBy]);

  const filterProducts = () => {
    let filtered = products;

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.description.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by price range
    filtered = filtered.filter(product =>
      product.price >= priceRange[0] && product.price <= priceRange[1]
    );

    // Sort products
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'rating':
          return b.rating - a.rating;
        case 'name':
        default:
          return a.name.localeCompare(b.name);
      }
    });

    setFilteredProducts(filtered);
  };

  const handleAddToCart = (product) => {
    addToCart(product, 1);
    setShowCartNotification(true);
    setTimeout(() => setShowCartNotification(false), 2000);
  };

  const categories = [
    { id: 'all', name: 'All Products', icon: 'üõçÔ∏è' },
    { id: 'filters', name: 'Filters', icon: 'üíß' },
    { id: 'spare-parts', name: 'Spare Parts', icon: 'üîß' },
    { id: 'water-bottles', name: 'Water Bottles', icon: 'ü•§' }
  ];

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={i <= rating ? 'text-yellow-400' : 'text-gray-300'}>
          ‚òÖ
        </span>
      );
    }
    return stars;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 via-teal-50 to-slate-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-teal-500 mx-auto"></div>
          <p className="mt-4 text-slate-600">Loading products...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-teal-50 to-slate-100">
      {/* Universal Navbar */}
      <UniversalNavbar />

      {/* Main Content with top padding for fixed navbar */}
      <div className="pt-16">
        {/* Header */}
        <div className="bg-gradient-to-r from-teal-600 to-coral-600 shadow-lg border-b border-teal-700/20">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold text-white">Products</h1>
                <p className="text-teal-100 mt-1">Find the perfect water solution for your needs</p>
              </div>
              <div className="flex items-center space-x-4">
                <span className="text-sm text-teal-200">
                  {filteredProducts.length} products found
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Cart Notification */}
        {showCartNotification && (
          <div className="fixed top-20 right-4 bg-green-500 text-white px-4 py-2 rounded-lg shadow-lg z-50 animate-bounce">
            <div className="flex items-center space-x-2">
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
              </svg>
              <span>Item added to cart!</span>
            </div>
          </div>
        )}

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col lg:flex-row gap-8">
            {/* Filters Sidebar */}
            <div className="lg:w-64 flex-shrink-0">
              <div className="bg-white rounded-lg shadow-sm p-6 sticky top-24">
                <h3 className="text-lg font-semibold text-slate-800 mb-4">Filters</h3>
                
                {/* Search */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-700 mb-2">Search</label>
                  <input
                    type="text"
                    placeholder="Search products..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full px-3 py-2 border border-teal-300 rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  />
                </div>

                {/* Categories */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-700 mb-2">Categories</label>
                  <div className="space-y-2">
                    {categories.map((cat) => (
                      <label key={cat.id} className="flex items-center">
                        <input
                          type="radio"
                          name="category"
                          value={cat.id}
                          checked={selectedCategory === cat.id}
                          onChange={(e) => setSelectedCategory(e.target.value)}
                          className="mr-2 text-teal-500 focus:ring-teal-500"
                        />
                        <span className="text-sm text-slate-700">{cat.icon} {cat.name}</span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Price Range */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-700 mb-2">Price Range (LKR)</label>
                  <div className="space-y-2">
                    <input
                      type="range"
                      min="0"
                      max="10000"
                      value={priceRange[1]}
                      onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
                      className="w-full accent-teal-500"
                    />
                    <div className="flex justify-between text-xs text-slate-500">
                      <span>LKR {priceRange[0]}</span>
                      <span>LKR {priceRange[1]}</span>
                    </div>
                  </div>
                </div>

                {/* Sort By */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-700 mb-2">Sort By</label>
                  <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="w-full px-3 py-2 border border-teal-300 rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  >
                    <option value="name">Name</option>
                    <option value="price-low">Price: Low to High</option>
                    <option value="price-high">Price: High to Low</option>
                    <option value="rating">Rating</option>
                  </select>
                </div>

                {/* Clear Filters */}
                <button
                  onClick={() => {
                    setSearchQuery('');
                    setSelectedCategory('all');
                    setPriceRange([0, 10000]);
                    setSortBy('name');
                  }}
                  className="w-full bg-gray-100 text-gray-700 py-2 px-4 rounded-md hover:bg-gray-200 transition-colors duration-300"
                >
                  Clear Filters
                </button>
              </div>
            </div>

            {/* Products Grid */}
            <div className="flex-1">
              {error ? (
                <div className="text-center py-12">
                  <div className="text-6xl mb-4">‚ö†Ô∏è</div>
                  <h3 className="text-xl font-semibold text-red-600 mb-2">Error Loading Products</h3>
                  <p className="text-gray-600 mb-4">{error}</p>
                  <button
                    onClick={() => window.location.reload()}
                    className="bg-teal-500 text-white px-4 py-2 rounded-md hover:bg-teal-600 transition-colors duration-300"
                  >
                    Retry
                  </button>
                </div>
              ) : filteredProducts.length === 0 ? (
                <div className="text-center py-12">
                  <div className="text-6xl mb-4">üîç</div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">No products found</h3>
                  <p className="text-gray-600">Try adjusting your filters or search terms</p>
                </div>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {filteredProducts.map((product) => (
                    <div key={product.id} className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-300 overflow-hidden border border-teal-100">
                      <div className="relative">
                        <div className="h-48 bg-gradient-to-br from-teal-100 to-coral-100 flex items-center justify-center">
                          <span className="text-6xl">{product.category === 'water-bottles' ? 'ü•§' : product.category === 'filters' ? 'üíß' : 'üîß'}</span>
                        </div>
                        {product.status === 'Low Stock' && (
                          <div className="absolute top-2 right-2 bg-coral-500 text-white text-xs px-2 py-1 rounded-full">
                            Low Stock
                          </div>
                        )}
                        {product.status === 'Out of Stock' && (
                          <div className="absolute top-2 right-2 bg-gray-500 text-white text-xs px-2 py-1 rounded-full">
                            Out of Stock
                          </div>
                        )}
                      </div>
                      
                      <div className="p-4">
                        <h3 className="text-lg font-semibold text-gray-900 mb-2">{product.name}</h3>
                        <p className="text-gray-600 text-sm mb-3 line-clamp-2">{product.description}</p>
                        
                        <div className="flex items-center mb-3">
                          <div className="flex items-center mr-2">
                            {renderStars(product.rating)}
                          </div>
                          <span className="text-sm text-gray-500">({product.reviews})</span>
                        </div>
                        
                        <div className="flex items-center justify-between mb-4">
                          <div>
                            <span className="text-xl font-bold text-gray-900">LKR {product.price.toLocaleString()}</span>
                            {product.originalPrice > product.price && (
                              <span className="text-sm text-gray-500 line-through ml-2">LKR {product.originalPrice.toLocaleString()}</span>
                            )}
                          </div>
                          <span className="text-sm text-gray-500">Stock: {product.stock}</span>
                        </div>
                        
                        {product.warranty !== 'N/A' && (
                          <div className="mb-4">
                            <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded-full">
                              {product.warranty} Warranty
                            </span>
                          </div>
                        )}
                        
                        <div className="flex space-x-2">
                          <Link
                            to={`/product/${product.id}`}
                            className="flex-1 bg-coral-500 text-white py-2 px-4 rounded-md hover:bg-coral-600 transition-colors duration-300 text-center text-sm font-medium"
                          >
                            View Details
                          </Link>
                          {product.status !== 'Out of Stock' && (
                            <button 
                              onClick={() => handleAddToCart(product)}
                              className="bg-teal-500 text-white py-2 px-4 rounded-md hover:bg-teal-600 transition-colors duration-300 text-sm font-medium"
                            >
                              Add to Cart
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Products;
