import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function User(props) {
  const history = useNavigate();

  if (!props.user) return null;

  const { _id, name, age, address } = props.user;

  const deleteHandler = async () => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await axios.delete(`http://localhost:5000/Users/${_id}`);
        history("/userDetails");
      } catch (err) {
        console.error("Error deleting user:", err);
        alert('Error deleting user. Please try again.');
      }
    }
  };

  return (
    <div className="bg-white border border-gray-200 rounded-lg p-6 shadow-sm hover:shadow-md transition-shadow">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center">
            <svg className="w-6 h-6 text-primary-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
            </svg>
          </div>
          <div>
            <h3 className="text-lg font-semibold text-gray-900">{name}</h3>
            <p className="text-sm text-gray-500">ID: {_id}</p>
          </div>
        </div>
        
        <div className="flex items-center space-x-3">
          <div className="text-right">
            <p className="text-sm text-gray-600">Age: <span className="font-medium text-gray-900">{age}</span></p>
            <p className="text-sm text-gray-600">Address: <span className="font-medium text-gray-900">{address}</span></p>
          </div>
        </div>
      </div>
      
      <div className="mt-4 flex space-x-3">
        <Link to={`/UserDetails/${_id}`}>
          <button className="bg-primary-600 text-white px-4 py-2 rounded-md hover:bg-primary-700 transition-colors text-sm font-medium">
            Update User
          </button>
        </Link>
        <button 
          onClick={deleteHandler}
          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors text-sm font-medium"
        >
          Delete
        </button>
      </div>
    </div>
  );
}

export default User;
