import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import { useCart } from '../../Context/CartContext';
import UniversalNavbar from '../Nav/UniversalNavbar';
import axios from 'axios';

const HomePage = () => {
  const { user, isAuthenticated } = useAuth();
  const { totalItems } = useCart();
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  
  // Customer dashboard state
  const [activeTab, setActiveTab] = useState('dashboard');
  const [branches, setBranches] = useState([]);
  const [recyclingRequests, setRecyclingRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    branchId: '',
    wasteWeight: '',
    notes: ''
  });
  const [branchesLoading, setBranchesLoading] = useState(false);

  const handleSystemSwitch = () => {
    navigate('/emergency');
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Implement search functionality
    console.log('Searching for:', searchQuery);
  };

  const handleLogin = () => {
    navigate('/login');
  };

  const handleSignup = () => {
    navigate('/register/customer');
  };

  // Customer dashboard functions
  useEffect(() => {
    if (isAuthenticated() && user?.role === 'Customer') {
      fetchBranches();
      if (user && user._id) {
        fetchRecyclingRequests();
      }
    }
  }, [user, isAuthenticated]);

  // Handle navigation to profile tab from navbar
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const tab = urlParams.get('tab');
    if (tab === 'profile' && isAuthenticated() && user?.role === 'Customer') {
      setActiveTab('profile');
    }
  }, [isAuthenticated, user]);

  const fetchBranches = async () => {
    try {
      setBranchesLoading(true);
      const response = await axios.get('http://localhost:5000/public/branches');
      if (response.data.success) {
        setBranches(response.data.branches || []);
      } else {
        setBranches([]);
      }
    } catch (error) {
      console.error('Error fetching branches:', error);
      setBranches([]);
    } finally {
      setBranchesLoading(false);
    }
  };

  const fetchRecyclingRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get(`http://localhost:5000/RecyclingRequests/customer/${user._id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      setRecyclingRequests(response.data.requests || []);
    } catch (error) {
      console.error('Error fetching recycling requests:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRecyclingSubmit = async (e) => {
    e.preventDefault();
    
    if (!user) {
      alert('You must be logged in to submit recycling requests. Please log in again.');
      return;
    }
    
    if (!formData.branchId || !formData.wasteWeight) {
      alert('Please fill in all required fields');
      return;
    }

    const selectedBranch = branches.find(b => b._id === formData.branchId);
    if (!selectedBranch) {
      alert('Please select a valid branch');
      return;
    }

    setLoading(true);
    try {
      const requestData = {
        customerId: user._id,
        customerName: user.name,
        customerEmail: user.email,
        branchId: formData.branchId,
        branchName: selectedBranch.name,
        branchLocation: selectedBranch.location || selectedBranch.address || 'N/A',
        wasteWeight: parseFloat(formData.wasteWeight),
        notes: formData.notes
      };

      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found. Please log in again.');
      }

      const response = await axios.post('http://localhost:5000/RecyclingRequests', requestData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      setFormData({
        branchId: '',
        wasteWeight: '',
        notes: ''
      });

      fetchRecyclingRequests();
      alert('Recycling request submitted successfully! You will earn points once approved.');
    } catch (error) {
      console.error('Error submitting recycling request:', error);
      let errorMessage = 'Error submitting recycling request. Please try again.';
      
