import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  PieChart, Pie, Cell, LineChart, Line
} from 'recharts';
import axios from 'axios';

const FactoryDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  
  // Check if user has factory manager role
  useEffect(() => {
    if (user && user.role !== 'Factory Manager') {
      console.error('User does not have factory manager privileges:', user.role);
      alert('Access denied. Factory Manager privileges required.');
      window.location.href = '/login';
    }
  }, [user]);
  
  const [inventoryStats, setInventoryStats] = useState(null);
  const [inventoryData, setInventoryData] = useState([]);
  const [pendingOrders, setPendingOrders] = useState([]);
  const [orderStats, setOrderStats] = useState(null);
  const [recentActivities, setRecentActivities] = useState([]);
  const [deliveryStats, setDeliveryStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [recyclingRequests, setRecyclingRequests] = useState([]);
  const [recyclingBins, setRecyclingBins] = useState([]);
  const [recyclingStats, setRecyclingStats] = useState(null);
  const [monthlyData, setMonthlyData] = useState([]);

  // Fetch data from API
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        console.log('ðŸ”„ Fetching factory dashboard data...');
        
        // Fetch all data in parallel
        const [statsResponse, inventoryResponse, ordersResponse, orderStatsResponse, activitiesResponse, deliveryResponse, recyclingResponse, binsResponse, recyclingStatsResponse] = await Promise.allSettled([
          axios.get('http://localhost:5000/Inventory/stats/overview'),
          axios.get('http://localhost:5000/Inventory'),
          axios.get('http://localhost:5000/Orders/pending'),
          axios.get('http://localhost:5000/Orders/stats/overview'),
          axios.get('http://localhost:5000/Orders/activities/recent'),
          axios.get('http://localhost:5000/Delivery/stats'),
          axios.get('http://localhost:5000/RecyclingRequests/pending'),
          axios.get('http://localhost:5000/RecyclingBins/factory-notification'),
          axios.get('http://localhost:5000/RecyclingRequests/statistics')
        ]);

        // Set inventory stats
        if (statsResponse.status === 'fulfilled') {
          setInventoryStats(statsResponse.value.data.stats);
        } else {
          console.error('Error fetching inventory stats:', statsResponse.reason);
          setInventoryStats({
            totalItems: 8,
            lowStockItems: 2,
            outOfStockItems: 1,
            inStockItems: 5,
            totalQuantity: 450
          });
        }

        // Set inventory data
        if (inventoryResponse.status === 'fulfilled') {
          setInventoryData(inventoryResponse.value.data.inventory || []);
        } else {
          console.error('Error fetching inventory data:', inventoryResponse.reason);
          setInventoryData([]);
        }

        // Set pending orders
        if (ordersResponse.status === 'fulfilled') {
          setPendingOrders(ordersResponse.value.data.orders || []);
        } else {
          console.error('Error fetching pending orders:', ordersResponse.reason);
          setPendingOrders([]);
        }

        // Set order stats
        if (orderStatsResponse.status === 'fulfilled') {
          setOrderStats(orderStatsResponse.value.data.stats);
        } else {
          console.error('Error fetching order stats:', orderStatsResponse.reason);
          setOrderStats({
            totalOrders: 0,
            pendingOrders: 0,
            processingOrders: 0,
            urgentOrders: 0,
            highPriorityOrders: 0
          });
        }

        // Set recent activities
        if (activitiesResponse.status === 'fulfilled') {
          setRecentActivities(activitiesResponse.value.data.activities || []);
        } else {
          console.error('Error fetching recent activities:', activitiesResponse.reason);
          setRecentActivities([]);
        }

        // Set delivery stats
        if (deliveryResponse.status === 'fulfilled') {
          setDeliveryStats(deliveryResponse.value.data.stats);
        } else {
          console.error('Error fetching delivery stats:', deliveryResponse.reason);
          setDeliveryStats({
            factoryDeliveriesToday: 0,
            branchDeliveriesToday: 0,
            pendingShipments: 0,
            pendingDeliveries: 0
          });
        }

        // Set recycling requests
        if (recyclingResponse.status === 'fulfilled') {
          setRecyclingRequests(recyclingResponse.value.data.requests || []);
        } else {
          console.error('Error fetching recycling requests:', recyclingResponse.reason);
          setRecyclingRequests([]);
        }

        // Set recycling bins
        if (binsResponse.status === 'fulfilled') {
          setRecyclingBins(binsResponse.value.data.binsForNotification || []);
        } else {
          console.error('Error fetching recycling bins:', binsResponse.reason);
          setRecyclingBins([]);
        }

        // Set recycling stats
        if (recyclingStatsResponse.status === 'fulfilled') {
          setRecyclingStats(recyclingStatsResponse.value.data.statistics);
        } else {
          console.error('Error fetching recycling stats:', recyclingStatsResponse.reason);
          setRecyclingStats({
            totalRequests: 0,
            pendingRequests: 0,
            approvedRequests: 0,
            completedRequests: 0,
            rejectedRequests: 0,
            totalWeight: 0,
            totalPoints: 0
          });
        }

      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

