            onClick={() => handleGenerateRecyclingReport()}
            className="bg-purple-600 text-white px-4 py-2 rounded-md hover:bg-purple-700 transition-colors"
          >
            Generate Report
          </button>
        </div>
      </div>
    </div>
  );

  const renderReportsTab = () => (
