import React, { useState, useEffect } from 'react';
import { useAuth } from '../../Context/AuthContext';
import UniversalNavbar from '../Nav/UniversalNavbar';
import axios from 'axios';

const MyAccount = () => {
  const { user, isAuthenticated } = useAuth();
  
  // Customer dashboard state
  const [activeTab, setActiveTab] = useState('dashboard');
  const [branches, setBranches] = useState([]);
  const [recyclingRequests, setRecyclingRequests] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    branchId: '',
    wasteWeight: '',
    notes: ''
  });
  const [branchesLoading, setBranchesLoading] = useState(false);

  // Customer dashboard functions
  useEffect(() => {
    if (isAuthenticated() && user?.role === 'Customer') {
      fetchBranches();
      if (user && user._id) {
        fetchRecyclingRequests();
      }
    }
  }, [user, isAuthenticated]);

  // Handle navigation to profile tab from navbar
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const tab = urlParams.get('tab');
    if (tab === 'profile' && isAuthenticated() && user?.role === 'Customer') {
      setActiveTab('profile');
    }
  }, [isAuthenticated, user]);

  const fetchBranches = async () => {
    try {
      setBranchesLoading(true);
      const response = await axios.get('http://localhost:5000/public/branches');
      if (response.data.success) {
        setBranches(response.data.branches);
      }
    } catch (error) {
      console.error('Error fetching branches:', error);
    } finally {
      setBranchesLoading(false);
    }
  };

  const fetchRecyclingRequests = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/RecyclingRequests/customer/${user._id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      if (response.data.success) {
        setRecyclingRequests(response.data.requests);
      }
    } catch (error) {
      console.error('Error fetching recycling requests:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRecyclingSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      
      // Debug: Check if user is logged in
      if (!token) {
        alert('Please log in to submit a recycling request');
        return;
      }
      
      if (!user) {
        alert('User information not available. Please refresh the page and try again.');
        return;
      }
      
      // Get branch details for the request
      const selectedBranch = branches.find(branch => branch._id === formData.branchId);
      if (!selectedBranch) {
        alert('Please select a valid branch');
        return;
      }
      
      const requestData = {
        customerId: user._id,
        customerName: user.name,
        customerEmail: user.email,
        branchId: formData.branchId,
        branchName: selectedBranch.name,
        branchLocation: selectedBranch.address,
        wasteWeight: parseFloat(formData.wasteWeight),
        notes: formData.notes
      };
      
      const response = await axios.post('http://localhost:5000/RecyclingRequests', requestData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (response.data.success) {
        alert('Recycling request submitted successfully! The branch manager will review your request.');
        setFormData({ branchId: '', wasteWeight: '', notes: '' });
        fetchRecyclingRequests(); // Refresh the list
      }
    } catch (error) {
      console.error('Error submitting recycling request:', error);
      if (error.response?.data?.message) {
        alert(`Error: ${error.response.data.message}`);
      } else {
        alert('Error submitting recycling request. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending': return 'bg-yellow-100 text-yellow-800';
      case 'Approved': return 'bg-green-100 text-green-800';
      case 'Completed': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  // Redirect if not authenticated or not a customer
  if (!isAuthenticated() || user?.role !== 'Customer') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-800 via-teal-900 to-slate-900 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-white mb-4">Access Denied</h1>
          <p className="text-teal-200 mb-8">You need to be logged in as a customer to access this page.</p>
          <a href="/login" className="bg-gradient-to-r from-teal-500 to-coral-500 text-white px-6 py-3 rounded-lg hover:from-teal-600 hover:to-coral-600 transition-colors">
            Go to Login
          </a>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-800 via-teal-900 to-slate-900">
      {/* Universal Navbar */}
      <UniversalNavbar />

      {/* Main Content with top padding for fixed navbar */}
      <div className="pt-16">
        {/* Page Header */}
        <section className="py-12 px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto text-center">
            <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">
              My <span className="bg-gradient-to-r from-teal-400 to-coral-400 bg-clip-text text-transparent">Account</span>
            </h1>
            <p className="text-xl text-teal-200 max-w-3xl mx-auto">
              Manage your account, track orders, and access all your customer features
            </p>
          </div>
        </section>

        {/* Customer Dashboard Section */}
        <section className="py-8 px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto">
            {/* Customer Navigation */}
            <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 mb-8">
              <div className="flex flex-wrap justify-center gap-4">
                <button
                  onClick={() => setActiveTab('dashboard')}
                  className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                    activeTab === 'dashboard'
                      ? 'bg-gradient-to-r from-teal-500 to-coral-500 text-white shadow-lg'
                      : 'text-white hover:bg-white/20'
                  }`}
                >
                  Dashboard
                </button>
                <button
                  onClick={() => setActiveTab('recycling')}
                  className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                    activeTab === 'recycling'
                      ? 'bg-gradient-to-r from-teal-500 to-coral-500 text-white shadow-lg'
                      : 'text-white hover:bg-white/20'
                  }`}
                >
                  🌱 Recycling
                </button>
                <button
                  onClick={() => setActiveTab('orders')}
                  className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 ${
                    activeTab === 'orders'
                      ? 'bg-gradient-to-r from-teal-500 to-coral-500 text-white shadow-lg'
                      : 'text-white hover:bg-white/20'
                  }`}
                >
                  My Orders
                </button>
                <button
                  onClick={() => setActiveTab('profile')}
                  className={`px-6 py-3 rounded-lg font-medium transition-all duration-200 flex items-center ${
                    activeTab === 'profile'
                      ? 'bg-gradient-to-r from-teal-500 to-coral-500 text-white shadow-lg'
                      : 'text-white hover:bg-white/20'
                  }`}
                >
                  <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                  </svg>
                  My Profile
                </button>
              </div>
            </div>

            {/* Dashboard Content */}
            {activeTab === 'dashboard' && (
              <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 text-center">
                  <div className="text-4xl mb-4">🛍️</div>
                  <h3 className="text-xl font-semibold text-white mb-2">Total Orders</h3>
                  <p className="text-3xl font-bold text-blue-200">12</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 text-center">
                  <div className="text-4xl mb-4">✅</div>
                  <h3 className="text-xl font-semibold text-white mb-2">Completed</h3>
                  <p className="text-3xl font-bold text-green-200">8</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 text-center">
                  <div className="text-4xl mb-4">⏳</div>
                  <h3 className="text-xl font-semibold text-white mb-2">Pending</h3>
                  <p className="text-3xl font-bold text-yellow-200">4</p>
                </div>
                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6 text-center">
                  <div className="text-4xl mb-4">🌱</div>
                  <h3 className="text-xl font-semibold text-white mb-2">Points</h3>
                  <p className="text-3xl font-bold text-green-200">{user?.recyclingPoints || 0}</p>
                </div>
              </div>
            )}

            {/* Recycling Content */}
            {activeTab === 'recycling' && (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                  <h3 className="text-xl font-semibold text-white mb-4">Submit Recycling Request</h3>
                  <form onSubmit={handleRecyclingSubmit} className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-white mb-2">Select Branch *</label>
                      <select
                        name="branchId"
                        value={formData.branchId}
                        onChange={handleInputChange}
                        required
                        disabled={branchesLoading}
                        className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/50"
                      >
                        <option value="">Choose a branch...</option>
                        {branches.map(branch => (
                          <option key={branch._id} value={branch._id} className="bg-gray-800">
                            {branch.name} - {branch.address}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white mb-2">Waste Weight (kg) *</label>
                      <input
                        type="number"
                        name="wasteWeight"
                        value={formData.wasteWeight}
                        onChange={handleInputChange}
                        required
                        min="1"
                        className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/50"
                        placeholder="Enter weight in kg"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-white mb-2">Notes</label>
                      <textarea
                        name="notes"
                        value={formData.notes}
                        onChange={handleInputChange}
                        rows="3"
                        className="w-full px-3 py-2 bg-white/20 border border-white/30 rounded-lg text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-white/50"
                        placeholder="Any additional notes..."
                      />
                    </div>
                    <button
                      type="submit"
                      disabled={loading}
                      className="w-full bg-gradient-to-r from-teal-500 to-coral-500 text-white py-2 px-4 rounded-lg hover:from-teal-600 hover:to-coral-600 disabled:opacity-50 transition-colors"
                    >
                      {loading ? 'Submitting...' : 'Submit Request'}
                    </button>
                  </form>
                </div>

                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                  <h3 className="text-xl font-semibold text-white mb-4">Recycling History</h3>
                  {loading ? (
                    <p className="text-white/70">Loading...</p>
                  ) : recyclingRequests.length === 0 ? (
                    <p className="text-white/70">No recycling requests yet.</p>
                  ) : (
                    <div className="space-y-3">
                      {recyclingRequests.map(request => (
                        <div key={request._id} className="bg-white/5 rounded-lg p-4">
                          <div className="flex justify-between items-start mb-2">
                            <h4 className="text-white font-medium">Request #{request._id.slice(-6)}</h4>
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(request.status)}`}>
                              {request.status}
                            </span>
                          </div>
                          <p className="text-white/70 text-sm">Weight: {request.wasteWeight} kg</p>
                          <p className="text-white/70 text-sm">Date: {new Date(request.createdAt).toLocaleDateString()}</p>
                          {request.notes && (
                            <p className="text-white/70 text-sm mt-1">Notes: {request.notes}</p>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Orders Content */}
            {activeTab === 'orders' && (
              <div className="space-y-6">
                <h3 className="text-2xl font-semibold text-white mb-6">Order History</h3>
                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-lg font-medium text-white">Order #ORD-001</h3>
                      <p className="text-white/70">5L Water Bottles x 10</p>
                      <p className="text-sm text-white/50">Ordered on: 2024-01-15</p>
                    </div>
                    <span className="bg-teal-100 text-teal-800 px-2 py-1 rounded-full text-sm font-medium">
                      Delivered
                    </span>
                  </div>
                </div>
                <div className="bg-white/10 backdrop-blur-md rounded-2xl p-6">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-lg font-medium text-white">Order #ORD-002</h3>
                      <p className="text-white/70">10L Water Bottles x 5</p>
                      <p className="text-sm text-white/50">Ordered on: 2024-01-20</p>
                    </div>
                    <span className="bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full text-sm font-medium">
                      In Transit
                    </span>
                  </div>
                </div>
              </div>
            )}

            {/* Profile Content */}
            {activeTab === 'profile' && (
              <div className="space-y-8">
                {/* Profile Header */}
                <div className="relative bg-gradient-to-br from-blue-500/20 via-cyan-500/20 to-blue-600/20 backdrop-blur-xl rounded-3xl p-8 border border-blue-400/30 overflow-hidden">
                  {/* Background Pattern */}
                  <div className="absolute inset-0 opacity-10">
                    <div className="absolute inset-0" style={{
                      backgroundImage: `url("data:image/svg+xml,%3Csvg width='40' height='40' viewBox='0 0 40 40' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%23ffffff' fill-opacity='0.1'%3E%3Cpath d='M20 20c0-5.5-4.5-10-10-10s-10 4.5-10 10 4.5 10 10 10 10-4.5 10-10zm10 0c0-5.5-4.5-10-10-10s-10 4.5-10 10 4.5 10 10 10 10-4.5 10-10z'/%3E%3C/g%3E%3C/svg%3E")`
                    }}></div>
                  </div>
                  
                  <div className="relative flex flex-col md:flex-row items-center md:items-start gap-8">
                    {/* Avatar Section */}
                    <div className="relative">
                      <div className="w-32 h-32 bg-gradient-to-br from-blue-500 to-cyan-500 rounded-full flex items-center justify-center text-white text-4xl font-bold shadow-2xl border-4 border-white/20">
                        {user?.name ? user.name.charAt(0).toUpperCase() : 'U'}
                      </div>
                      <div className="absolute -bottom-2 -right-2 w-8 h-8 bg-green-500 rounded-full border-4 border-white/20 flex items-center justify-center">
                        <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                        </svg>
                      </div>
                    </div>

                    {/* User Info */}
                    <div className="flex-1 text-center md:text-left">
                      <h2 className="text-3xl font-bold text-white mb-2">{user?.name || 'User Name'}</h2>
                      <p className="text-blue-200 text-lg mb-4">{user?.email || 'user@example.com'}</p>
                      
                      {/* Status Badge */}
                      <div className="inline-flex items-center px-4 py-2 bg-green-500/20 border border-green-400/30 rounded-full text-green-300 mb-6">
                        <span className="w-2 h-2 bg-green-400 rounded-full mr-2 animate-pulse"></span>
                        Active Member
                      </div>

                      {/* Quick Stats */}
                      <div className="grid grid-cols-3 gap-4">
                        <div className="text-center">
                          <div className="text-2xl font-bold text-cyan-300">{user?.recyclingPoints || 0}</div>
                          <div className="text-sm text-blue-200">Eco Points</div>
                        </div>
                        <div className="text-center">
                          <div className="text-2xl font-bold text-blue-300">12</div>
                          <div className="text-sm text-blue-200">Total Orders</div>
                        </div>
                        <div className="text-center">
                          <div className="text-2xl font-bold text-green-300">8</div>
                          <div className="text-sm text-blue-200">Completed</div>
                        </div>
                      </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="flex flex-col gap-3">
                      <button className="px-6 py-3 bg-gradient-to-r from-blue-500 to-cyan-500 text-white rounded-xl font-semibold hover:shadow-lg hover:shadow-blue-500/25 transition-all duration-300 flex items-center">
                        <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                        </svg>
                        Edit Profile
                      </button>
                      <button className="px-6 py-3 bg-white/10 border border-white/20 text-white rounded-xl font-semibold hover:bg-white/20 transition-all duration-300 flex items-center">
                        <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                        </svg>
                        Security
                      </button>
                    </div>
                  </div>
                </div>

                {/* Profile Information Cards */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                  {/* Personal Information */}
                  <div className="bg-white/10 backdrop-blur-xl rounded-3xl p-8 border border-blue-400/20">
                    <div className="flex items-center mb-6">
                      <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-cyan-500 rounded-xl flex items-center justify-center mr-4">
                        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                        </svg>
                      </div>
                      <h3 className="text-2xl font-bold text-white">Personal Information</h3>
                    </div>
                    
                    <div className="space-y-6">
                      <div className="flex items-center justify-between p-4 bg-white/5 rounded-xl border border-white/10">
                        <div className="flex items-center">
                          <svg className="w-5 h-5 text-cyan-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                          </svg>
                          <span className="text-blue-200">Full Name</span>
                        </div>
                        <span className="text-white font-medium">{user?.name || 'Not provided'}</span>
                      </div>

                      <div className="flex items-center justify-between p-4 bg-white/5 rounded-xl border border-white/10">
                        <div className="flex items-center">
                          <svg className="w-5 h-5 text-cyan-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                          </svg>
                          <span className="text-blue-200">Email Address</span>
                        </div>
                        <span className="text-white font-medium">{user?.email || 'Not provided'}</span>
                      </div>

                      <div className="flex items-center justify-between p-4 bg-white/5 rounded-xl border border-white/10">
                        <div className="flex items-center">
                          <svg className="w-5 h-5 text-cyan-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                          </svg>
                          <span className="text-blue-200">Phone Number</span>
                        </div>
                        <span className="text-white font-medium">{user?.phone || 'Not provided'}</span>
                      </div>

                      <div className="flex items-center justify-between p-4 bg-white/5 rounded-xl border border-white/10">
                        <div className="flex items-center">
                          <svg className="w-5 h-5 text-cyan-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                          </svg>
                          <span className="text-blue-200">Address</span>
                        </div>
                        <span className="text-white font-medium">{user?.address || 'Not provided'}</span>
                      </div>
                    </div>
                  </div>

                  {/* Account Statistics */}
                  <div className="bg-white/10 backdrop-blur-xl rounded-3xl p-8 border border-blue-400/20">
                    <div className="flex items-center mb-6">
                      <div className="w-12 h-12 bg-gradient-to-br from-green-500 to-emerald-500 rounded-xl flex items-center justify-center mr-4">
                        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                        </svg>
                      </div>
                      <h3 className="text-2xl font-bold text-white">Account Statistics</h3>
                    </div>
                    
                    <div className="space-y-6">
                      <div className="p-6 bg-gradient-to-r from-blue-500/20 to-cyan-500/20 rounded-xl border border-blue-400/30">
                        <div className="flex items-center justify-between mb-2">
                          <span className="text-blue-200">Eco Points</span>
                          <span className="text-2xl font-bold text-cyan-300">{user?.recyclingPoints || 0}</span>
                        </div>
                        <div className="w-full bg-white/20 rounded-full h-2">
                          <div className="bg-gradient-to-r from-cyan-400 to-blue-400 h-2 rounded-full" style={{width: `${Math.min((user?.recyclingPoints || 0) / 100 * 100, 100)}%`}}></div>
                        </div>
                        <p className="text-sm text-blue-300 mt-2">Keep recycling to earn more points!</p>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div className="p-4 bg-white/5 rounded-xl border border-white/10 text-center">
                          <div className="text-2xl font-bold text-blue-300 mb-1">12</div>
                          <div className="text-sm text-blue-200">Total Orders</div>
                        </div>
                        <div className="p-4 bg-white/5 rounded-xl border border-white/10 text-center">
                          <div className="text-2xl font-bold text-green-300 mb-1">8</div>
                          <div className="text-sm text-blue-200">Completed</div>
                        </div>
                        <div className="p-4 bg-white/5 rounded-xl border border-white/10 text-center">
                          <div className="text-2xl font-bold text-yellow-300 mb-1">3</div>
                          <div className="text-sm text-blue-200">Pending</div>
                        </div>
                        <div className="p-4 bg-white/5 rounded-xl border border-white/10 text-center">
                          <div className="text-2xl font-bold text-red-300 mb-1">1</div>
                          <div className="text-sm text-blue-200">Cancelled</div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Recent Activity & Achievements */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                  {/* Recent Activity */}
                  <div className="bg-white/10 backdrop-blur-xl rounded-3xl p-8 border border-blue-400/20">
                    <div className="flex items-center mb-6">
                      <div className="w-12 h-12 bg-gradient-to-br from-purple-500 to-pink-500 rounded-xl flex items-center justify-center mr-4">
                        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                      </div>
                      <h3 className="text-2xl font-bold text-white">Recent Activity</h3>
                    </div>
                    
                    <div className="space-y-4">
                      <div className="flex items-center p-4 bg-white/5 rounded-xl border border-white/10">
                        <div className="w-10 h-10 bg-green-500/20 rounded-full flex items-center justify-center mr-4">
                          <svg className="w-5 h-5 text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                          </svg>
                        </div>
                        <div className="flex-1">
                          <p className="text-white font-medium">Order #ORD-001 Delivered</p>
                          <p className="text-sm text-blue-300">2 hours ago</p>
                        </div>
                      </div>

                      <div className="flex items-center p-4 bg-white/5 rounded-xl border border-white/10">
                        <div className="w-10 h-10 bg-cyan-500/20 rounded-full flex items-center justify-center mr-4">
                          <svg className="w-5 h-5 text-cyan-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                          </svg>
                        </div>
                        <div className="flex-1">
                          <p className="text-white font-medium">Recycling Request Submitted</p>
                          <p className="text-sm text-blue-300">1 day ago</p>
                        </div>
                      </div>

                      <div className="flex items-center p-4 bg-white/5 rounded-xl border border-white/10">
                        <div className="w-10 h-10 bg-blue-500/20 rounded-full flex items-center justify-center mr-4">
                          <svg className="w-5 h-5 text-blue-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
                          </svg>
                        </div>
                        <div className="flex-1">
                          <p className="text-white font-medium">New Order Placed</p>
                          <p className="text-sm text-blue-300">3 days ago</p>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Achievements */}
                  <div className="bg-white/10 backdrop-blur-xl rounded-3xl p-8 border border-blue-400/20">
                    <div className="flex items-center mb-6">
                      <div className="w-12 h-12 bg-gradient-to-br from-yellow-500 to-orange-500 rounded-xl flex items-center justify-center mr-4">
                        <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
                        </svg>
                      </div>
                      <h3 className="text-2xl font-bold text-white">Achievements</h3>
                    </div>
                    
                    <div className="space-y-4">
                      <div className="flex items-center p-4 bg-gradient-to-r from-yellow-500/20 to-orange-500/20 rounded-xl border border-yellow-400/30">
                        <div className="w-12 h-12 bg-yellow-500 rounded-full flex items-center justify-center mr-4">
                          <span className="text-white text-xl">🏆</span>
                        </div>
                        <div className="flex-1">
                          <p className="text-white font-medium">First Order</p>
                          <p className="text-sm text-yellow-300">Completed your first order</p>
                        </div>
                        <div className="w-6 h-6 bg-yellow-400 rounded-full flex items-center justify-center">
                          <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                          </svg>
                        </div>
                      </div>

                      <div className="flex items-center p-4 bg-gradient-to-r from-green-500/20 to-emerald-500/20 rounded-xl border border-green-400/30">
                        <div className="w-12 h-12 bg-green-500 rounded-full flex items-center justify-center mr-4">
                          <span className="text-white text-xl">🌱</span>
                        </div>
                        <div className="flex-1">
                          <p className="text-white font-medium">Eco Warrior</p>
                          <p className="text-sm text-green-300">Recycled 10+ bottles</p>
                        </div>
                        <div className="w-6 h-6 bg-green-400 rounded-full flex items-center justify-center">
                          <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                          </svg>
                        </div>
                      </div>

                      <div className="flex items-center p-4 bg-white/5 rounded-xl border border-white/10">
                        <div className="w-12 h-12 bg-gray-500 rounded-full flex items-center justify-center mr-4">
                          <span className="text-white text-xl">💎</span>
                        </div>
                        <div className="flex-1">
                          <p className="text-white font-medium">VIP Member</p>
                          <p className="text-sm text-gray-400">Complete 50+ orders</p>
                        </div>
                        <div className="w-6 h-6 bg-gray-400 rounded-full flex items-center justify-center">
                          <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                          </svg>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </section>
      </div>
    </div>
  );
};

export default MyAccount;
