import React, { useState, useEffect } from 'react';
import Nav from '../Nav/Nav';
import Footer from '../Footer/Footer';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  PieChart, Pie, Cell
} from 'recharts';
import axios from 'axios';

function Home() {
  const [inventoryStats, setInventoryStats] = useState(null);
  const [inventoryData, setInventoryData] = useState([]);
  const [pendingOrders, setPendingOrders] = useState([]);
  const [orderStats, setOrderStats] = useState(null);
  const [recentActivities, setRecentActivities] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [statsResponse, inventoryResponse, ordersResponse, orderStatsResponse, activitiesResponse] = await Promise.all([
        axios.get('http://localhost:5000/Inventory/stats/overview'),
        axios.get('http://localhost:5000/Inventory'),
        axios.get('http://localhost:5000/Orders/pending'),
        axios.get('http://localhost:5000/Orders/stats/overview'),
        axios.get('http://localhost:5000/Orders/activities/recent')
      ]);

      setInventoryStats(statsResponse.data.stats);
      setInventoryData(inventoryResponse.data.inventory);
      setPendingOrders(ordersResponse.data.orders);
      setOrderStats(orderStatsResponse.data.stats);
      setRecentActivities(activitiesResponse.data.activities);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      // Set default data if API fails
      setInventoryStats({
        totalItems: 5,
        lowStockItems: 2,
        outOfStockItems: 1,
        inStockItems: 2,
        totalQuantity: 150
      });
      setOrderStats({
        totalOrders: 0,
        pendingOrders: 0,
        processingOrders: 0,
        urgentOrders: 0,
        highPriorityOrders: 0
      });
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'In Stock': return 'text-success-600 bg-success-100';
      case 'Low Stock': return 'text-yellow-600 bg-yellow-100';
      case 'Out of Stock': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'Urgent': return 'text-red-600 bg-red-100';
      case 'High': return 'text-orange-600 bg-orange-100';
      case 'Medium': return 'text-yellow-600 bg-yellow-100';
      case 'Low': return 'text-green-600 bg-green-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getActivityColor = (type, status) => {
    if (type === 'inventory') {
      if (status === 'critical') return 'text-red-500';
      if (status === 'warning') return 'text-yellow-500';
    }
    return 'text-primary-500';
  };

  const COLORS = ['#0077B6', '#00B4D8', '#009688', '#FF6B6B', '#4ECDC4'];

  return (
    <div className="min-h-screen bg-gray-50">
      <Nav />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Welcome Section */}
        <header className="text-center mb-8">
          <h1 className="text-4xl font-bold text-primary-600 mb-4">
            Welcome, Factory Manager
          </h1>
          <p className="text-lg text-gray-600 max-w-3xl mx-auto">
            Monitor factory operations, manage orders from branches, and ensure smooth inventory management.
            Track pending orders and stock levels in real-time.
          </p>
        </header>

        {/* Quick Stats */}
        <section className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-primary-500">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-primary-100">
                <svg className="w-6 h-6 text-primary-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 7l-8-4-8 4m16 0l-8 4m8-4v10l-8 4m0-10L4 7m8 4v10M4 7v10l8 4" />
                </svg>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Raw Material Inventory</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {loading ? '...' : inventoryStats?.totalItems || 0} Items
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-orange-500">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-orange-100">
                <svg className="w-6 h-6 text-orange-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending Orders</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {loading ? '...' : orderStats?.pendingOrders || 0} Orders
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-red-500">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-red-100">
                <svg className="w-6 h-6 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Urgent Orders</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {loading ? '...' : orderStats?.urgentOrders || 0} Orders
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6 border-l-4 border-success-500">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-success-100">
                <svg className="w-6 h-6 text-success-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Quantity</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {loading ? '...' : inventoryStats?.totalQuantity || 0} Units
                </p>
              </div>
            </div>
          </div>
        </section>

        {/* Pending Orders Section */}
        <section className="bg-white rounded-lg shadow-md p-6 mb-8">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-semibold text-gray-800">Pending Orders from Branches</h2>
            <span className="text-sm text-gray-500">Total: {pendingOrders.length} orders</span>
          </div>
          
          {pendingOrders.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Order Number
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Branch
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Items
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Priority
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Expected Delivery
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {pendingOrders.map((order) => (
                    <tr key={order._id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        {order.orderNumber}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{order.branchName}</div>
                        <div className="text-sm text-gray-500">{order.branchLocation}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">
                          {order.items.map(item => `${item.itemName} (${item.quantity})`).join(', ')}
                        </div>
                        <div className="text-sm text-gray-500">Total: {order.totalQuantity} units</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getPriorityColor(order.priority)}`}>
                          {order.priority}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {new Date(order.expectedDeliveryDate).toLocaleDateString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                          order.status === 'Pending' ? 'text-yellow-600 bg-yellow-100' : 'text-blue-600 bg-blue-100'
                        }`}>
                          {order.status}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-8">
              <p className="text-gray-500">No pending orders at the moment</p>
            </div>
          )}
        </section>

        {/* Inventory Distribution Chart */}
        <section className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Inventory Distribution</h2>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={inventoryData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, quantity }) => `${name}: ${quantity}`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="quantity"
              >
                {inventoryData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </section>

        {/* Recent Activities */}
        <section className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Recent Activities</h2>
          <div className="space-y-4">
            {recentActivities.length > 0 ? (
              recentActivities.map((activity, index) => (
                <div key={index} className="flex items-center space-x-3">
                  <div className={`w-2 h-2 rounded-full ${getActivityColor(activity.type, activity.status) === 'text-red-500' ? 'bg-red-500' : 
                    getActivityColor(activity.type, activity.status) === 'text-yellow-500' ? 'bg-yellow-500' : 'bg-primary-500'}`}></div>
                  <span className="text-gray-700">{activity.message}</span>
                  <span className="text-sm text-gray-500 ml-auto">
                    {new Date(activity.timestamp).toLocaleDateString()}
                  </span>
                </div>
              ))
            ) : (
              <div className="text-center py-4">
                <p className="text-gray-500">No recent activities</p>
              </div>
            )}
          </div>
        </section>

        {/* Quick Actions */}
        <section className="flex flex-wrap gap-4 justify-center">
          <button className="bg-primary-600 text-white px-6 py-3 rounded-lg hover:bg-primary-700 transition-colors font-medium">
            View All Orders
          </button>
          <button className="bg-secondary-600 text-white px-6 py-3 rounded-lg hover:bg-secondary-700 transition-colors font-medium">
            Manage Inventory
          </button>
          <button className="bg-success-600 text-white px-6 py-3 rounded-lg hover:bg-success-700 transition-colors font-medium">
            Generate Reports
          </button>
        </section>
      </div>

      <Footer />
    </div>
  );
}

export default Home;
