import React, { useState, useEffect } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../Context/AuthContext';
import { useCart } from '../../Context/CartContext';
import UniversalNavbar from '../Nav/UniversalNavbar';
import axios from 'axios';

const Products = () => {
  const { category } = useParams();
  const { isAuthenticated } = useAuth();
  const { addToCart, totalItems } = useCart();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(category || 'all');
  const [priceRange, setPriceRange] = useState([0, 10000]);
  const [sortBy, setSortBy] = useState('name');
  const [showCartNotification, setShowCartNotification] = useState(false);

  // Sample product data
  const sampleProducts = [
    {
      id: 1,
      name: 'RO Membrane Filter',
      category: 'filters',
      price: 299.99,
      originalPrice: 399.99,
      image: '/product1.jpg',
      description: 'High-quality reverse osmosis membrane filter for pure water',
      stock: 15,
      warranty: '1 Year',
      rating: 4.5,
      reviews: 128
    },
    {
      id: 2,
      name: '5L Water Bottle',
      category: 'water-bottles',
      price: 2.99,
      originalPrice: 3.99,
      image: '/product2.jpg',
      description: 'Premium 5-liter drinking water bottle',
      stock: 50,
      warranty: 'N/A',
      rating: 4.2,
      reviews: 89
    },
    {
      id: 3,
      name: '10L Water Bottle',
      category: 'water-bottles',
      price: 4.99,
      originalPrice: 5.99,
      image: '/product3.jpg',
      description: 'Premium 10-liter drinking water bottle',
      stock: 30,
      warranty: 'N/A',
      rating: 4.3,
      reviews: 67
    },
    {
      id: 4,
      name: '20L Water Bottle',
      category: 'water-bottles',
      price: 8.99,
      originalPrice: 10.99,
      image: '/product4.jpg',
      description: 'Premium 20-liter drinking water bottle',
      stock: 25,
      warranty: 'N/A',
      rating: 4.4,
      reviews: 45
    },
    {
      id: 5,
      name: 'Sediment Filter Cartridge',
      category: 'spare-parts',
      price: 19.99,
      originalPrice: 24.99,
      image: '/product5.jpg',
      description: 'Sediment filter cartridge for pre-filtration',
      stock: 40,
      warranty: '1 Year',
      rating: 4.1,
      reviews: 156
    },
    {
      id: 6,
      name: 'Carbon Filter Cartridge',
      category: 'spare-parts',
      price: 24.99,
      originalPrice: 29.99,
      image: '/product6.jpg',
      description: 'Activated carbon filter cartridge for taste and odor removal',
      stock: 35,
      warranty: '1 Year',
      rating: 4.6,
      reviews: 203
    },
    {
      id: 7,
      name: 'UV Cartridge',
      category: 'filters',
      price: 89.99,
      originalPrice: 109.99,
      image: '/product7.jpg',
      description: 'Ultraviolet sterilization cartridge for water purification',
      stock: 12,
      warranty: '1 Year',
      rating: 4.7,
      reviews: 78
    },
    {
      id: 8,
      name: 'Mineral Cartridge',
      category: 'filters',
      price: 34.99,
      originalPrice: 39.99,
      image: '/product8.jpg',
      description: 'Mineral cartridge to add essential minerals back to water',
      stock: 28,
      warranty: '1 Year',
      rating: 4.3,
      reviews: 92
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setProducts(sampleProducts);
      setLoading(false);
    }, 1000);
  }, []);

  useEffect(() => {
    filterProducts();
  }, [products, searchQuery, selectedCategory, priceRange, sortBy]);

  const filterProducts = () => {
    let filtered = products;

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.description.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by price range
    filtered = filtered.filter(product =>
      product.price >= priceRange[0] && product.price <= priceRange[1]
    );

    // Sort products
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'rating':
          return b.rating - a.rating;
        case 'name':
        default:
          return a.name.localeCompare(b.name);
      }
    });

    setFilteredProducts(filtered);
  };

  const handleAddToCart = (product) => {
    addToCart(product, 1);
    setShowCartNotification(true);
    setTimeout(() => setShowCartNotification(false), 2000);
  };

  const categories = [
    { id: 'all', name: 'All Products', icon: '🛍️' },
    { id: 'filters', name: 'Filters', icon: '💧' },
    { id: 'spare-parts', name: 'Spare Parts', icon: '🔧' },
    { id: 'water-bottles', name: 'Water Bottles', icon: '🥤' }
  ];

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={i <= rating ? 'text-yellow-400' : 'text-gray-300'}>
          ★
        </span>
      );
    }
    return stars;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 via-teal-50 to-slate-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-teal-500 mx-auto"></div>
          <p className="mt-4 text-slate-600">Loading products...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-teal-50 to-slate-100">
      {/* Universal Navbar */}
      <UniversalNavbar />

      {/* Main Content with top padding for fixed navbar */}
      <div className="pt-16">
        {/* Header */}
        <div className="bg-gradient-to-r from-teal-600 to-coral-600 shadow-lg border-b border-teal-700/20">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold text-white">Products</h1>
                <p className="text-teal-100 mt-1">Find the perfect water solution for your needs</p>
              </div>
              <div className="flex items-center space-x-4">
                <span className="text-sm text-teal-200">
                  {filteredProducts.length} products found
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Cart Notification */}
        {showCartNotification && (
          <div className="fixed top-20 right-4 bg-green-500 text-white px-4 py-2 rounded-lg shadow-lg z-50 animate-bounce">
            <div className="flex items-center space-x-2">
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
              </svg>
              <span>Item added to cart!</span>
            </div>
          </div>
        )}

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex flex-col lg:flex-row gap-8">
            {/* Filters Sidebar */}
            <div className="lg:w-64 flex-shrink-0">
              <div className="bg-white rounded-lg shadow-sm p-6 sticky top-24">
                <h3 className="text-lg font-semibold text-slate-800 mb-4">Filters</h3>
                
                {/* Search */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-700 mb-2">Search</label>
                  <input
                    type="text"
                    placeholder="Search products..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full px-3 py-2 border border-teal-300 rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  />
                </div>

                {/* Categories */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-700 mb-2">Categories</label>
                  <div className="space-y-2">
                    {categories.map((cat) => (
                      <label key={cat.id} className="flex items-center">
                        <input
                          type="radio"
                          name="category"
                          value={cat.id}
                          checked={selectedCategory === cat.id}
                          onChange={(e) => setSelectedCategory(e.target.value)}
                          className="mr-2 text-teal-500 focus:ring-teal-500"
                        />
                        <span className="text-sm text-slate-700">{cat.icon} {cat.name}</span>
                      </label>
                    ))}
                  </div>
                </div>

                {/* Price Range */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-700 mb-2">Price Range</label>
                  <div className="space-y-2">
                    <input
                      type="range"
                      min="0"
                      max="1000"
                      value={priceRange[1]}
                      onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
                      className="w-full accent-teal-500"
                    />
                    <div className="flex justify-between text-xs text-slate-500">
                      <span>${priceRange[0]}</span>
                      <span>${priceRange[1]}</span>
                    </div>
                  </div>
                </div>

                {/* Sort By */}
                <div className="mb-6">
                  <label className="block text-sm font-medium text-slate-700 mb-2">Sort By</label>
                  <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="w-full px-3 py-2 border border-teal-300 rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  >
                    <option value="name">Name</option>
                    <option value="price-low">Price: Low to High</option>
                    <option value="price-high">Price: High to Low</option>
                    <option value="rating">Rating</option>
                  </select>
                </div>

                {/* Clear Filters */}
                <button
                  onClick={() => {
                    setSearchQuery('');
                    setSelectedCategory('all');
                    setPriceRange([0, 1000]);
                    setSortBy('name');
                  }}
                  className="w-full bg-gray-100 text-gray-700 py-2 px-4 rounded-md hover:bg-gray-200 transition-colors duration-300"
                >
                  Clear Filters
                </button>
              </div>
            </div>

            {/* Products Grid */}
            <div className="flex-1">
              {filteredProducts.length === 0 ? (
                <div className="text-center py-12">
                  <div className="text-6xl mb-4">🔍</div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">No products found</h3>
                  <p className="text-gray-600">Try adjusting your filters or search terms</p>
                </div>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {filteredProducts.map((product) => (
                    <div key={product.id} className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-300 overflow-hidden border border-teal-100">
                      <div className="relative">
                        <div className="h-48 bg-gradient-to-br from-teal-100 to-coral-100 flex items-center justify-center">
                          <span className="text-6xl">{product.category === 'water-bottles' ? '🥤' : product.category === 'filters' ? '💧' : '🔧'}</span>
                        </div>
                        {product.stock < 10 && (
                          <div className="absolute top-2 right-2 bg-coral-500 text-white text-xs px-2 py-1 rounded-full">
                            Low Stock
                          </div>
                        )}
                        {product.stock === 0 && (
                          <div className="absolute top-2 right-2 bg-gray-500 text-white text-xs px-2 py-1 rounded-full">
                            Out of Stock
                          </div>
                        )}
                      </div>
                      
                      <div className="p-4">
                        <h3 className="text-lg font-semibold text-gray-900 mb-2">{product.name}</h3>
                        <p className="text-gray-600 text-sm mb-3 line-clamp-2">{product.description}</p>
                        
                        <div className="flex items-center mb-3">
                          <div className="flex items-center mr-2">
                            {renderStars(product.rating)}
                          </div>
                          <span className="text-sm text-gray-500">({product.reviews})</span>
                        </div>
                        
                        <div className="flex items-center justify-between mb-4">
                          <div>
                            <span className="text-xl font-bold text-gray-900">${product.price}</span>
                            {product.originalPrice > product.price && (
                              <span className="text-sm text-gray-500 line-through ml-2">${product.originalPrice}</span>
                            )}
                          </div>
                          <span className="text-sm text-gray-500">Stock: {product.stock}</span>
                        </div>
                        
                        {product.warranty !== 'N/A' && (
                          <div className="mb-4">
                            <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded-full">
                              {product.warranty} Warranty
                            </span>
                          </div>
                        )}
                        
                        <div className="flex space-x-2">
                          <Link
                            to={`/product/${product.id}`}
                            className="flex-1 bg-coral-500 text-white py-2 px-4 rounded-md hover:bg-coral-600 transition-colors duration-300 text-center text-sm font-medium"
                          >
                            View Details
                          </Link>
                          {product.stock > 0 && (
                            <button 
                              onClick={() => handleAddToCart(product)}
                              className="bg-teal-500 text-white py-2 px-4 rounded-md hover:bg-teal-600 transition-colors duration-300 text-sm font-medium"
                            >
                              Add to Cart
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Products;
