import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router';
import Nav from '../Nav/Nav'; // Add this import

function UpdateUser() {
    const history = useNavigate();
    const { id } = useParams();
    const [inputs, setInputs] = useState({ name: '', age: '', address: '' });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchHandler = async () => {
            const response = await axios.get(`http://localhost:5000/users/${_id}`)
                .then((res) => res.data)
                .then((data) => {
                    setInputs(data.user);
                    setLoading(false);
                });
        };
        fetchHandler();
    }, [id]);

    const sendRequest = async () => {
        await axios.put(`http://localhost:5000/users/${id}`, {
            name: String(inputs.name),
            age: Number(inputs.age),
            address: String(inputs.address)
        }).then(res => res.data);
    };

    const handleChange = (e) => {
        setInputs((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        sendRequest().then(() => history(`/UserDetails/${id}`));
    };

    if (loading) return <div>Loading...</div>;

    return (
        <div>
            <Nav />
            <h1>Update User</h1>
            <form onSubmit={handleSubmit}>
                <label>Name</label>
                <input type="text" name='name' value={inputs.name} onChange={handleChange} />
                <br />
                <label>Age</label>
                <br />
                <input type="text" name='age' value={inputs.age} onChange={handleChange} />
                <br />
                <label>Address</label>
                <br />
                <input type="text" name='address' value={inputs.address} onChange={handleChange} />
                <br />
                <button type="submit">Update User</button>
            </form>
        </div>
    );
}

export default UpdateUser;
